}      
.community .forum-container .discussions .toolbar ul li{
  border-left:1px solid #56646C !important;
}
.community .forum-container .discussions .topic {
  border-bottom:1px solid #56646C !important;
}
.community .forum-container .discussions .topic .topic-info .sub-text {
  border-top:1px solid #56646C !important;
}
.community .forum-container header {
  border-bottom:1px solid #56646C !important;
}
}
