css:
@-moz-document domain("followmy.tv") {
ul.activity-feed li span {
color: #ccc;
font-style: normal!important;
padding-left: 5px;
}
body {
background:#050505 url(http://i.imgur.com/iQiT2Yo.png)!important;
font-family: "Helvetica", arial, verdana, sans-serif;
font-size: 85%;
color: #e5e5e5;
}
#container div.main-block.has-columns div.col.main div.col-inner {
background:#202020 url(http://i.imgur.com/iQiT2Yo.png)!important;
}
#container div.main-block div.col.right {
background:#202020 url(http://i.imgur.com/iQiT2Yo.png)!important;
}
#container div.main-block.has-columns .inner {
background-color: #212121!important;
}
#container div.main-block.has-columns div.col.main {
background:#202020 url(http://i.imgur.com/iQiT2Yo.png)!important;
}
.db-header {
background:#202020 url(http://i.imgur.com/iQiT2Yo.png)!important;
}
#head {
background:#202020 url(http://i.imgur.com/iQiT2Yo.png)!important;
border-bottom: 1px solid #232323;
}
div.recent-user-episodes ul li {
border-bottom: 1px solid #343434;
}
.sub-nav-wrapper div.donate {
visibility: hidden;
display: none;
}
ul.subnav li.active a {
background:#202020 url(http://i.imgur.com/iQiT2Yo.png)!important;
color: #e5e5e5;
}
ul.subnav li a {
border: 1px solid #303030;
background:#202020 url(http://i.imgur.com/iQiT2Yo.png)!important;
color: #e5e5e5;
}
ul.subnav li {
background: #none!important;
color: #e5e5e5!important;
}
li.not-active {
background: none!important;
color: #e5e5e5!important;
}
#container div.right div.block div.pad {
background:#202020 url(http://i.imgur.com/iQiT2Yo.png)!important;
color: #e5e5e5;
border: 1px solid #353535;
}
a:link, a:visited, a:hover {
color: #B5D8F5;
text-decoration: none;
}
div.action-header div.info h3 {
color: #e5e5e5;
}
ul.friendlist-block li a {
color: #B5D8F5;
font-weight: bold;
}
div.couch-potato-row {
position: relative;
height: 38px;
margin: 4px 0;
overflow: hidden;
background: #252525;
background: -moz-linear-gradient(top, #191919 100%,#242424 0%,#303030 11%);
background: -webkit-gradient(linear, left top, left bottom, color-stop(100%,#191919), color-stop(0%,#242424), color-stop(11%,#303030));
background: -webkit-linear-gradient(top, #191919 100%,#242424 0%,#303030 1%);
background: -o-linear-gradient(top, #191919 100%,#242424 0%,#303030 11%);
background: -ms-linear-gradient(top, #191919 100%,#242424 0%,#303030 11%);
background: linear-gradient(top, #191919 100%,#242424 0%,#303030 11%);
border: 1px solid #BFC0CC;
border-radius: 3px;
}
div.couch-potato-row.own-record .progress {
background: #fefcea;
background: -moz-linear-gradient(top, #A9AAAB 0%, #8C8C8C 100%);
background: -webkit-gradient(linear, left top, left bottom, color-stop(0%,#fefcea), color-stop(100%,#8C8C8C));
background: -webkit-linear-gradient(top, #A9AAAB 0%,#8C8C8C 100%);
background: -o-linear-gradient(top, #A9AAAB 0%,#8C8C8C 100%);
background: -ms-linear-gradient(top, #A9AAAB 0%,#8C8C8C 100%);
background: linear-gradient(top, #A9AAAB 0%,#8C8C8C 100%);
}
div.couch-potato-row div.time {
color: #e5e5e5;
text-shadow: 0.1em 0.1em 0.3em black;
}
div.couch-potato-row .progress {
background: #797E82;
}
div.couch-potato-row .name a {
color: #000;
text-shadow: 0.1em 0.1em 0.2em white;
font-weight: bold;
}
#container div.main-block {
margin: 0 auto;
border: 1px solid #303030;
background:#202020 url(http://i.imgur.com/iQiT2Yo.png)!important;
}
ul.activity-feed li {
border-bottom: 1px solid #282828;
color: #e5e5e5;
}
h3, li.month, table.calendar tbody td li span.airtime {
font-weight: bold;
color: #e5e5e5;
}
table.calendar tbody td.day.nonmonth div.day-title {
color: #e5e5e5!important;
background: #262626!important;
}
table.calendar tbody td.day div.day-title {
color: #e5e5e5!important;
background: #282828!important;
}
table.calendar thead tr.weekdays th {
font-weight: normal!important;
font-size: 11px!important;
padding: 3px 0!important;
background:#202020 url(http://i.imgur.com/iQiT2Yo.png)!important;
border-right: 1px solid #303030!important;
color: #e5e5e5!important;
}
table.calendar tbody td.day {
border: 1px solid 303030!important;
}
td.today {
background-color: #303030!important;
}
#footer {
height: 120px;
background:#202020 url(http://i.imgur.com/iQiT2Yo.png)!important;
border-top: 1px solid #fff;
}
div.markify div.row-inner.past {
background-color: #212121;
}
div.markify div.row-inner.even {
background:#202020 url(http://i.imgur.com/iQiT2Yo.png)!important;
}
div.markify div.row-inner.even.today {
background: #303030;
}
div.markify div.row-inner.past.odd {
background-color: #252525;
}
div.markify div.row-inner.today.odd {
background-color: #303030;
}
div.markify div.row-inner.odd.future {
background-color: #252525;
}
div.markify div.row-inner.yesterday.odd {
background-color: #252525;
}
div.markify div.row-inner.odd.tomorrow {
background-color: #252525;
}
div.markify .show-name a {
color: #e5e5e5;
}
div.markify .show-name .airdate {
color: #ddd;
}
div.markify .actions li a.unpin, div.markify .actions li a.ignore {
color: #e5e5e5;
}
div.markify .episode-nr-wrapper {
background: #242424;
color: #e5e5e5;
border: 1px solid #303030;
}
div.markify .episode-nr-wrapper.multi span.unwatched-count {
color: #e5e5e5;
font-weight: bold;
}
div.markify .actions li a {
-moz-box-shadow: none;
-webkit-box-shadow: none;
background: #242424;
background: -moz-linear-gradient(100% 100% 90deg, #323232, #262626);
background: -webkit-gradient(linear, 0% 0%, 0% 100%, from(#323232), to(#262626));
background: -ms-linear-gradient(top, #323232 0%,#262626 100%);
border: 1px solid #303030;
color: #e5e5e5;
font-weight: bold !important;
text-shadow: none;
}
ul.inner-tabs li a.active {
background: #252525;
border: 1px solid #303030;
color: #e5e5e5;
font-weight: bold;
}
ul.inner-tabs {
border-bottom: 1px solid #303030;
}
.db-header .checkinbar input.checkin {
outline: none;
width: 97%;
font-size: 1.2em;
padding: 10px;
color: #e5e5e5;
border: 1px solid #303030;
-moz-box-shadow: none;
-webkit-box-shadow: none;
border-radius: 4px;
background: -webkit-gradient(linear, left top, left bottom, color-stop(0, #252525), color-stop(0.16, #212121));
background: -moz-linear-gradient(center top, #252525 0%, #212121 16%);
}
.db-header .checkinbar input.checkin.inactive {
color: #e5e5e5;
display: block;
text-shadow: none!important;
font-weight: bold;
}
header .db-header-action.loading {
background: #212121 url('http://followmy.tv/img/icons/loading.gif') no-repeat 97% 62% !important;
}
div.recent-user-episodes ul li div.info .show a {
color: #e5e5e5;
}
div.commentform div.form-wrapper {
background:#161616 url(http://i.imgur.com/iQiT2Yo.png)!important;
border: 1px solid #303030;
}
#container div.commentform div.input.textarea textarea {
background:#202020 url(http://i.imgur.com/iQiT2Yo.png)!important;
color: #e5e5e5;
border: 1px solid #303030;
}
#container div.commentform div.comment-right .character-counter {
background: #252525;
color: #e5e5e5;
text-shadow: none;
}
#usercounts .count, #userhits .count {
background: #252525;
color: #e5e5e5;
border: 1px solid #303030;
}
#usercounts .count .label, #userhits .count .label {
color: #e5e5e5;
}
#container div.right div.block h3 {
color: #e5e5e5;
text-shadow: none;
}
div.page-sum {
background:#202020 url(http://i.imgur.com/iQiT2Yo.png)!important;
color: #e5e5e5;
text-shadow: none;
}
ul.buttonbar input.submit, ul.buttonbar li a {
background: -moz-linear-gradient(100% 100% 90deg, #323232, #262626);
background: -webkit-gradient(linear, 0% 0%, 0% 100%, from(#323232), to(#262626));
-moz-box-shadow: none;
-webkit-box-shadow: none;
background-color: #212121;
border: 1px solid #303030;
color: #e5e5e5;
text-shadow: none;
}
div.navigation ul li.active a {
background: transparent;
}
ul.service li.menu ul.sub li a {
border-top: 1px solid #303030;
background: #252525;
}
ul.service li.menu ul.sub li.signout a {
background: #252525;
}
ul.service li.menu .user .extra {
background: transparent url('http://followmy.tv/img/icons/arrow_bottom.png') no-repeat 100% 50%;
}
ul.service li.menu .user {
cursor: pointer;
padding: 4px 10px 4px 6px;
border-radius: 4px;
background: -moz-linear-gradient(100% 100% 90deg, #323232, #262626);
background: -webkit-gradient(linear, 0% 0%, 0% 100%, from(#323232), to(#262626));
border: 1px solid #303030;
}
div.search input {
background: #212121 url('http://followmy.tv/img/icons/search.png') no-repeat 10px 50%;
color: #e5e5e5!important;
border: 1px solid #303030;
}
div.markify div.row {
border-bottom: 1px solid #303030;
}
.categorize {
border: 1px solid #303030;
padding: 7px;
border-radius: 5px;
background:#161616 url(http://i.imgur.com/iQiT2Yo.png)!important;
}
#container div.main-block.has-columns .inner {
background:#202020 url(http://i.imgur.com/iQiT2Yo.png)!important;
}
div.tagsearch {
border: 1px solid #303030;
}
div.tagsearch #listTags {
background:#202020 url(http://i.imgur.com/iQiT2Yo.png)!important;
color: #e5e5e5;
}
div.comments div.highlight {
background: #242424!important;
}
div.comments span.r-actions a {
border: 1px solid #303030;
padding: 2px;
border-radius: 6px;
background-color: #212121;
}
#footer div.desc li a {
color: #B5D8F5;
}
a.big-button.userdelete span {
background: #242424;
border: 1px solid #303030;
color: #e5e5e5;
-moz-box-shadow: none;
-webkit-box-shadow: none;
}
a.big-button {
text-shadow: none;
color: #e5e5e5;
font-weight: normal;
font-size: 0.9em;
line-height: 27px;
background: #242424;
border: 1px solid #303030;
-moz-box-shadow: none;
-webkit-box-shadow: none;
}
a.button {
text-shadow: none;
font-weight: bold;
border: 1px solid #303030;
color: #e5e5e5;
-moz-box-shadow: none;
-webkit-box-shadow: none;
background: -moz-linear-gradient(100% 100% 90deg, #323232, #262626);
background: -webkit-gradient(linear, 0% 0%, 0% 100%, from(#323232), to(#262626));
}
table.generic-table tbody tr.odd td {
background: #262626;
}
table.generic-table thead th, table.generic-table tbody td {
border-bottom: 1px solid #303030;
}
ul.buttonbar li.label {
background:#202020 url(http://i.imgur.com/iQiT2Yo.png)!important;
border: 1px solid #303030;
color: #e5e5e5;
}
table.generic-table thead th {
border-bottom: 1px solid #303030!important;
color: #e5e5e5!important;
text-shadow: none!important;
background: #262626!important;
}
#notificationsBlock h3 {
color: #e5e5e5;
border-bottom: 1px solid #303030;
padding: 5px 10px;
font-size: 1em;
background-position: 97% 50% !important;
background:#202020 url(http://i.imgur.com/iQiT2Yo.png)!important;
}
li {
border-bottom: none;
}
.notification-list li, #notificationsBlock li, #notificationsBlock li:hover, .notification-list li:hover  {
border-bottom: 1px solid #303030;
background:#202020 url(http://i.imgur.com/iQiT2Yo.png)!important;
color: #e5e5e5;
}
.notification-list li .notification-message {
color: #e5e5e5;
}
#notificationsBlock li.all a {
background:#202020 url(http://i.imgur.com/iQiT2Yo.png)!important;
color: #e5e5e5;
}
#notificationsBlock li .notification-date {
background:#202020 url(http://i.imgur.com/iQiT2Yo.png)!important;
color: #e5e5e5;
}
.notification-list li .notification-message, #notificationsBlock li .notification-date, #notificationsBlock li .notification-message {
background:#202020 url(http://i.imgur.com/iQiT2Yo.png)!important;
color: #e5e5e5;
}
#notificationsBlock li .notification-message a {
color: #B5D8F5;
}
.db-header .db-header-action li.active a {
font-weight: bold;
color: #e5e5e5;
background:#202020 url(http://i.imgur.com/iQiT2Yo.png)!important;
border: 1px solid #303030;
}
.db-header .db-header-action li i {
background: none;
}
div.recent-user-episodes div.rating span.rating {
border: none;
background:#202020 url(http://i.imgur.com/iQiT2Yo.png)!important;
color: #e5e5e5;
}
#container div.right div.block.about p {
color: #e5e5e5;
}
.formatted p {
color: #e5e5e5;
}
.formatted li {
color: #e5e5e5;
}
#notificationsBlock li.unread, .notification-list li.unread {
background:#202020 url(http://i.imgur.com/iQiT2Yo.png)!important;
}
div.userlist.inline {
background:#202020 url(http://i.imgur.com/iQiT2Yo.png)!important;
color: #e5e5e5;
}
div.split-content .rating-wrap {
float: right;
width: 70px;
color: #e5e5e5;
border: 1px solid #303030;
border-radius: 5px;
text-align: center;
background: #242424;
}
div.show-teaser div.actions .following {
color: #e5e5e5;
background: #242424 url('http://followmy.tv/img/glyphicons/16_grey/glyphicons_051_eye_open.png') no-repeat 6px 50%;
border: 1px solid #303030;
}
ul.autocompleter-choices li.group {
background:#202020 url(http://i.imgur.com/iQiT2Yo.png)!important;
color: #e5e5e5;
}
ul.autocompleter-choices li {
border-top: 1px solid #303030;
background:#202020 url(http://i.imgur.com/iQiT2Yo.png)!important;
color: #e5e5e5;
-moz-box-shadow: none;
-webkit-box-shadow: none;
}
ul.autocompleter-choices li.autocompleter-selected {
background: #242424;
color: #e5e5e5;
}
#fmtvDialog .fmtvDialog-content {
background:#202020 url(http://i.imgur.com/iQiT2Yo.png)!important;
color: #e5e5e5;
border: 1px solid #303030;
}
.subscribe-popup ul.popup-actions li.form, .subscribe-popup ul.popup-actions li {
color: #e5e5e5;
border: 1px solid #303030;
background-color: #212121;
}
div.split-content .item.odd {
background: #262626;
}
div.split-content .item.header .form-action {
background: #262626;
border: 1px solid #303030;
}
.status {
background:#202020 url(http://i.imgur.com/iQiT2Yo.png)!important;
color: #e5e5e5;
border: 1px solid #303030;
}
.epinr {
color: #e5e5e5;
text-shadow: none;
background:#202020 url(http://i.imgur.com/iQiT2Yo.png)!important;
border: 1px solid #303030;
}
div.checkin-state .mycheckin {
background-color: #212121;
color: #e5e5e5;
}
div.split-content .item-teaser .labels li {
color: #e5e5e5;
background: #313131 url('http://followmy.tv/img/glyphicons/16_blue/glyphicons_051_eye_open.png') no-repeat 6px 50%;
}
div.message {
background-color: #323232;
color: #e5e5e5;
}
div.couch-potato-row div.count {
color: #e5e5e5;
background:#202020 url(http://i.imgur.com/iQiT2Yo.png)!important;
}
#system-notice div.system-notice-inner {
color: #e5e5e5;
background:#262626 url(http://i.imgur.com/iQiT2Yo.png)!important;
}
#system-notice {
border: 1px solid #303030;
}
}
