css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("forum.thegamecreators.com") {

body{
	background-image: url(http://zimnox.com/tgc/stylish/dark-texture.png) !important;
}

.header_notop{
    color: #e0e0e0 !important;
}



.BoardDescription, .BoardInfo2, .BoardInfo1, .BoardInfo{
	color:#e0e0e0 !important;
}


.BoardDescriptionNew, .BoardDescriptionNewSticky, .BoardDescription, 
.BoardInfo, .BoardInfo2, .BoardInfo1, .BoardIcon{
	border-collapse:separate;
	border: 1px solid #1e1e1e !important;
	border-radius: 6px;
}





.BoardDescriptionNewSticky{
	background-color: rgba(100, 250, 100, .05);
}

.info_table {
	margin: auto auto !important;
	color:#dadada !important;
	border-collapse:separate !important;
	border-spacing: 4px;
}

.info_table_header {
	background:none !important;
	border-bottom:1px solid #323232 !important;
}

.BoardIndexHead {
	border:0;
	background:#0A0A0A !important;
	color:#96ec00 !important;
}

input[type=button], input[type=submit]{
	border-radius: 5px !important;
	border: 1px inset #6e6e6e !important;
	background:#fdfdfd !important;
	box-shadow: 0px 0px 2px #000000;
}

input[type=button]:active, input[type=submit]:active{
	border:0;
	border-radius: 5px !important;
	border: 1px inset #1e1e1e !important;
}

input[name="message_subject"]{width:100%;}


img[src="g/previous_off.gif"]{
	content: url(http://zimnox.com/tgc/stylish/previous_off.png);
}
img[src="g/next_off.gif"]{
	content: url(http://zimnox.com/tgc/stylish/next_off.png);
}
img[src="g/top_off.gif"]{
	content: url(http://zimnox.com/tgc/stylish/top_off.png);
}
img[src="g/home_off.gif"]{
	content: url(http://zimnox.com/tgc/stylish/home_off.png);
}
img[src="g/post_off.gif"]{
	content: url(http://zimnox.com/tgc/stylish/post_off.png);
}
img[src="g/previous.gif"],img[src="/g/previous.gif"]{
	content: url(http://zimnox.com/tgc/stylish/previous.png);
}
img[src="g/previous.gif"]:hover,img[src="/g/previous.gif"]:hover{
	content: url(http://zimnox.com/tgc/stylish/previous_on.png);
}
img[src="g/top.gif"],img[src="/g/top.gif"]{
	content: url(http://zimnox.com/tgc/stylish/top.png);
}
img[src="g/top.gif"]:hover,img[src="/g/top.gif"]:hover{
	content: url(http://zimnox.com/tgc/stylish/top_on.png);
}
img[src="g/home.gif"],img[src="/g/home.gif"]{
	content: url(http://zimnox.com/tgc/stylish/home.png);
}
img[src="g/home.gif"]:hover,img[src="/g/home.gif"]:hover{
	content: url(http://zimnox.com/tgc/stylish/home_on.png);
}
img[src="g/post.gif"],img[src="/g/post.gif"]{
	content: url(http://zimnox.com/tgc/stylish/post.png);
}
img[src="g/post.gif"]:hover,img[src="/g/post.gif"]:hover{
	content: url(http://zimnox.com/tgc/stylish/post_on.png);
}
img[src="g/search.gif"],img[src="/g/search.gif"]{
	content: url(http://zimnox.com/tgc/stylish/search.png);
}
img[src="g/search.gif"]:hover,img[src="/g/search.gif"]:hover{
	content: url(http://zimnox.com/tgc/stylish/search_on.png);
}
img[src="g/next.gif"],img[src="/g/next.gif"]{
	content: url(http://zimnox.com/tgc/stylish/next.png);
}
img[src="g/next.gif"]:hover,img[src="/g/next.gif"]:hover{
	content: url(http://zimnox.com/tgc/stylish/next_on.png);
}


img[src="/g/themes/blue/corner-left.gif"]{
	content: url(http://zimnox.com/tgc/stylish/corner-left.png);
}
img[src="/g/themes/blue/footer-left.gif"]{
	content: url(http://zimnox.com/tgc/stylish/footer-left.png);
}

td[background="/g/themes/blue/footer-back.gif"]{
	background: url(http://zimnox.com/tgc/stylish/footer-back.png) !important;
}

img[src="/g/themes/blue/footer-right.gif"]{
	content: url(http://zimnox.com/tgc/stylish/footer-right.png);
}
img[src="/g/themes/blue/corner-right.gif"]{
	content: url(http://zimnox.com/tgc/stylish/corner-right.png);
}

td[background="/g/themes/blue/border-left.gif"]{
	background: url(http://zimnox.com/tgc/stylish/border-left.png) !important;
}
td[background="/g/themes/blue/border-right.gif"]{
	background: url(http://zimnox.com/tgc/stylish/border-right.png) !important;
}

td[bgcolor="#ffffff"]{
	background-color: transparent !important;
}

td[style="border-bottom: 1px solid #8d8d8d; border-right: 1px solid #8d8d8d;"]{
	border:none !important;
	border-collapse:separate !important;
	background: url(http://zimnox.com/tgc/stylish/dark-texture.png) !important;
}



td[background="/g/themes/blue/header-back1.gif"]{
	background: url(http://zimnox.com/tgc/stylish/header-back1.png);
}
td[background="/g/themes/blue/header-back2.gif"]{
	background: url(http://zimnox.com/tgc/stylish/header-back2.png);
}
td[background="/g/themes/blue/header-back3.gif"]{
	background: url(http://zimnox.com/tgc/stylish/header-back3.png);
}
img[src="/g/themes/blue/header-logo.gif"]{
	content: url(http://zimnox.com/tgc/stylish/header-logo.png);
}
img[src="/g/themes/blue/header-left.gif"]{
	content: url(http://zimnox.com/tgc/stylish/header-left.png);
}
img[src="/g/themes/blue/header-topright.gif"]{
	content: url(http://zimnox.com/tgc/stylish/header-topright.png);
}
img[src="/g/themes/blue/header-right1.gif"]{
	content: url(http://zimnox.com/tgc/stylish/header-right1.png);
}
img[src="/g/themes/blue/header-right2.gif"]{
	content: url(http://zimnox.com/tgc/stylish/header-right2.png);
}
#hot{height:17px !important;}



input[type=checkbox]{
	-webkit-appearance:none ;
	width:32px;
	height:32px;
	background:url(http://zimnox.com/tgc/stylish/checkbox.png) 0px  0px no-repeat !important;
	vertical-align:middle;
	cursor:pointer;
}

input[type=checkbox]:checked{
	background:url(http://zimnox.com/tgc/stylish/checkbox.png) -32px  0px no-repeat !important;
}
input[type=text]{background:#fdfdfd !important;
	border-radius: 5px !important;
	border: 1px outset #6e6e6e !important;}

textarea, select{
	background:#fdfdfd !important;
	border-radius: 5px !important;
	border: 1px outset #6e6e6e !important;
}
select{
	-webkit-appearance:none !important;
	margin-top:2px !important;
	padding-right:10px !important;
	background:url(http://zimnox.com/tgc/stylish/select_arrows.png) no-repeat right #fdfdfd !important;
	background-position: absolute; right: 4px; top: 0px !important;
}


body {
	
	margin: 0px 8px;
	font-family: Verdana, Arial, Helvetica, sans-serif;
    font-size: 11px;
	color:#e0e0e0;
}

A {
	color: #2bf !important;
	text-decoration:none !important;
}

A:Hover {
	color: #6ef !important;
	text-shadow:0 0 12px #2bf;
}

.center_pane {
    background-color: #1e1e1e !important;
    padding: 10px;
	padding-left: 18px;
}


.BoardMessage, .BoardPost,.BoardPost2, .BoardInfo3, .NewBoardMessage, .BoardIndexHead, .info_table {
			border-collapse:separate !important;
			border: 2px solid #1e1e1e !important;
			border-radius: 6px;
			border-spacing: 10px;
}

hr{border:1px solid #343434}

#google_translate_element{background:url(http://zimnox.com/tgc/stylish/dark-texture.png) !important; }

.BoardDescription {
	padding: 0px 6px 0px 6px;
	background: #e9e9e9;
}



.center_pane {
	background: transparent !important;
	padding: 10px;
	padding-left: 10px !important;
}


.BoardDescription,
.BoardIcon,
.BoardInfo,
.BoardInfo1,
.BoardInfo2,
.BoardMessage,
.BoardPost,
.info_table ,
.showcase_table
{
    background: rgba(60,60,60,0.5) !important;
}


.BoardDescriptionNew,
	.BoardDescriptionNew + td,
	.BoardDescriptionNew + td + td,
	.BoardDescriptionNew + td + td + td,
	.BoardDescriptionNew + td + td + td + td,
.BoardDescriptionNewSticky,
	.BoardDescriptionNewSticky + td,
	.BoardDescriptionNewSticky + td + td,
	.BoardDescriptionNewSticky + td + td + td,
	.BoardDescriptionNewSticky + td + td + td + td,
.NewBoardMessage
{
    background: rgba(100,100,100,0.5) !important;
}





.BoardPost {
	padding: 6px;
	color: #E0E0E0 !important;
}

.BoardPost2 {
	padding-left: 6px;
	padding-right: 6px;
	background: #0A0A0A !important;
}


.BoardMessage {
	padding: 6px;
	color:#F2F2F2 !important;
	line-height: 19px;
	font-size: 12px;
}

.NewBoardMessage {
	color:#F2F2F2 !important;
}


.BoardInfo {
	line-height: 14px;
	background: #e2e2e2;
}

.BoardInfo1 {
	background: #e1e1e1;
	line-height: 13px;
}

.BoardInfo2 {
	background: #e0e0e0;
	line-height: 13px;
}

.BoardInfo3 {
	background: #0A0A0A !important;
	line-height: 13px;
}

.BoardIcon {
	padding-left: 6px;
	padding-right: 6px;

}

.BoardPost {
	padding: 6px;
	color: #E0E0E0;
}

.BoardPost2 {
	padding-left: 6px;
	padding-right: 6px;
	background: #0A0A0A;
}

.signature {
	color: #F2F2F9 !important;
}


.BoardQuote {
	background: #202020 !important;
	color: #E0E0E0 !important;
	padding: 8px;
	margin-left: 12px;
	margin-bottom:14px;
	margin-top:8px;
	font-size: 11px;
	line-height: 17px;
	
	border-collapse:separate !important;
	border-left: 4px solid #96ec00;
	
	border-radius: 10px;
	border-spacing: 10px;
	box-shadow: 0px 0px 14px #000000;
}


}
