css:
@-moz-document domain('blognone.com'), url(http://www.blognone.com/){

#wrapper {
    background-color: #ffffff;
}

div.content-container {
    background-color: #ffffff;
}

div.node.clear-block {
    border-style: none;
    margin-bottom: 10px;
}

div.content-title-box {
    border-style: none;
    border-color: #ffffff;
    background-color: #ffffff;
    color: #7a7a7a;
}

div.content-title-box h2 a {
    color: #666666;
    font-size: 24px;
    background-color: #ffffff;
    font-weight: normal;
    font-variant: normal;
    border-style: none;
    border-bottom-width: 5px;
    border-color: #db07db;
}

#rightbottom_sidebar {
    border-style: none;
}

div.block.block-views h2 {
    background-color: #ffffff;
    color: #757575;
    font-size: 22px;
}

div.block.block-aggregator h2 {
    color: #757575;
    background-color: #ffffff;
    font-size: 22px;
}

div.node.sticky.clear-block {
    background-color: #ffffff;
}

div.content {
    border-style: none;
}

.news-info {
    border-top: none;
    border-bottom: none;
}

span.field-content a {
    color: #878787;
}

div.view-footer div a {
    color: #878787;
}

div.more-link a {
    color: #878787;
}

ul li a {
    color: #878787;
}

div.user-block {
    background-color: #ffffff;
}

div.signout {
    background-color: #ffffff;
    color: #f0f0f0;
}

div.signout a {
    color: #808080;
}

div.user-header {
    background-color: #ffffff;
}

div.user-header h2 {
    background-color: #ffffff;
    color: #878787;
}

#navi-menu li.leaf a {
    color: #ffffff;
}

span.submitted {
    background-color: #ffffff;
    border-style: none;
}

div.block.block-poll h2 {
    background-color: #ffffff;
    color: #878787;
}

div.block.block-block h2 {
    color: #878787;
    background-color: #ffffff;
}

div.content-title-box h2 {
    border-style: solid;
    border-bottom-width: 2px;
    border-color: #e0e0e0;
  	font-weight: normal;
}

#footer {
    background-color: #ffffff;
}

a.active {
    background-color: #ffffff;
}
#rightbottom_sidebar .block h2 {
 
  color: #878787;
  background: none;
}

.sticky .content-title-box, .sticky .content-title-box a {
      background-color: #fff;
}
    
.sticky .submitted {
	background: none;
}
        
ul.user-menu li a {
	color: #878787;
}
.comment-title {
  	color: #878787;
   background: #fff !important;

    border: none;
}
  
  .comment-title h2 {         
  	font-weight: normal;
	}
 
  
#comment-area{
     border: none;
}
  
  
.comment{
     border-top: 1px solid #eee; 
     border-left: 1px solid #d8d8d8; 


    
}


    
.comment-info{
        
    	background: #fff;
    	color: #878787;
    
}
  
.comment-info a{
        
    	color: #878787;
  		font-weight: bold;
    
}

.comment-zebra-gray{
    
   		background: #fff; 
  
}

#comments div.box{
  border: none;
}
#comments div.box h2{
  border-bottom: none;
  margin-top: 30px;
  text-align: center;
  font-size: 24px;
  font-weight: normal;
  background: #FFF;
  color: #878787 !important;
}

}
