css:
/*
DarkTextured stylesheet for TGC Forums
Created by Libervurto libervurto@gmx.com

### KNOWN BUGS ###
- Pages not displaying correctly: profile, my threads.
- Can't seem to change the shading of text box or code snippet.
*/

@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("forum.thegamecreators.com") {
/* GROUP TAB DIMENSIONS AND ALIGNMENT */
.active_tab,
.inactive_tab,
.inactive_tab_left,
.long_active_tab,
.long_inactive_tab,
.long_inactive_tab_left
{
    width: 78px;
    height: 26px;
    vertical-align: middle;
    text-align: center;
}


/* GROUP BACKGROUND */
table
{
    background: transparent !important;
}

.BoardIndexHead,
.BoardInfo3,
.BoardPost2,
.info_table_header
{
    background: rgba(0,0,0,.5) !important;
}

.BoardDescription,
.BoardIcon,
.BoardInfo,
.BoardInfo1,
.BoardInfo2,
.BoardMessage,
.BoardPost,
.info_table tbody,
.showcase_table
{
    background: rgba(128,128,128,.1) !important;
}

.BoardDescriptionNew,
	.BoardDescriptionNew + td,
	.BoardDescriptionNew + td + td,
	.BoardDescriptionNew + td + td + td,
	.BoardDescriptionNew + td + td + td + td,
.BoardDescriptionNewSticky,
	.BoardDescriptionNewSticky + td,
	.BoardDescriptionNewSticky + td + td,
	.BoardDescriptionNewSticky + td + td + td,
	.BoardDescriptionNewSticky + td + td + td + td,
.NewBoardMessage
{
    background: rgba(128,128,128,.3) !important;
}


/* GROUP BORDERS */
table,
.info_table
{
    border:0px !important;
}

.inactive_tab,
.inactive_tab_left,
.long_inactive_tab,
.long_inactive_tab_left,
.inactive_tab_filler
{
    border-bottom: 1px solid #585858;
}

.BoardDescription,
.BoardDescriptionNew,
.BoardDescriptionNewSticky,
.BoardDescriptionPending,
.BoardIcon,
.BoardIndexHead,
.BoardInfo,
.BoardInfo1,
.BoardInfo2,
.BoardInfo3,
.BoardMessage,
.BoardMessageSlim,
.BoardPost,
.BoardPost2,
.DeletedBoardMessage,
.NewBoardMessage,
.NewBoardMessageSlim,
.PendingBoardMessage,
.cpanel,
.info_table tbody,
.info_table_cell,
.info_table_header,
.pricebox,
.prices,
.prices_saving,
.showcase_table,
.system_notices,
.system_requirements_table,
.details_table,
.details_table_header
{
    border: 1px solid #111 !important;
    border-radius: 2px !important;
}


/* GROUP COLOUR */
.BoardDescription,
.BoardDescriptionNew,
.BoardDescriptionNewSticky,
.BoardMessage,
.BoardPost,
.BoardPost2,
.BoardIcon,
.BoardIndexHead,
.BoardInfo,
.BoardInfo1,
.BoardInfo2,
.BoardInfo3,
.LI_item,
.NewBoardMessage,
table,
.info_table_header,
.showcase_header,
.system_requirements_header,
.system_requirements_table,
.details_table_header
{
    color: #eee !important;
}

.active_tab,
.long_active_tab,
.inactive_tab_left,
.long_inactive_tab_left
{
    color: black;
}

.inactive_tab,
.long_inactive_tab
{
    color: #8d8d8d;
}

.formerror,
.required,
.prices_saving
{
    color: #ff0000;
}

.formsuccess
{
    color: #0033cc;
}


/* GROUP FONT-SIZE */
.system_requirements_header
{
    font-size: 12px;
}

/* GROUP FONT-WEIGHT */
.BoardIndexHead,
.BoardNavGroup,
.breadcrumb,
.details_table_header,
.formerror,
.formsuccess,
.header,
.header_bot,
.header_notop,
.info_table_header,
.left_nav_header,
.list,
.list_table_header,
.page_footer,
.prices,
.prices_saving,
.required,
.right_nav_header,
.searchresult,
.showcase_header,
.site_index_section,
.sort-table thead,
.system_notices,
.system_requirements_header,
A.breadcrumb:hover,
A.header_notop,
A.page_footer:Hover,
LI
{
    font-weight: bold;
}


/* GROUP MARGIN */
.formerror,
.formsuccess
{
    margin: 18px;
}


/* GROUP PADDING */
.BoardDescription,
.BoardDescriptionNew,
.BoardDescriptionNewSticky,
.BoardDescriptionPending,
.BoardIcon
{
    padding-left: 6px;
    padding-right: 6px;
}


/* **********************************************
	Generic
*/

body {
/*    background-image:none !important;*/
    background-image: url(http://i14.photobucket.com/albums/a314/Burtoni/dark-bobble_zpsab5f4d81.png) !important;
	background-color: #111 !important;
	margin-top:40px !important;
	font-family: calibri, sans-serif;
	font-size: 11px;
}

header {color:#222 !important;}

TD
{
	font-size: 11px;
	margin: 4px;
}

P {
    line-height: 17px;
    margin-top: 12px;
    margin-bottom: 12px;
}

A IMG {
	border: 0px;
}

P.BoardIndex{
	line-height: 17px;
	margin-top: 6px;
	margin-bottom: 6px;
}

LI {line-height: 17px;}

.LI_item {font-weight: normal;}

PRE {
	scroll: auto;
	font-family: Courier;
	font-size: 10px;
	color: #00ffee;
	background: #000000;
	border: 10px solid #ff0000;
	padding: 6px;
	margin-left: 12px;
	line-height: 10px;
}

A {
	color: #2bf !important;
	text-decoration:none !important;
	font-weight:bold !important;
}

A:Hover {
	color: #bff !important;
	text-shadow: 0 0 12px #2bf !important;
	font-weight:bold !important;
}

/*
	FAQ Table
*/

.data {
	margin-left: 4px;
	height: 16px;
	font-family: Tahoma;
	font-size: 8pt;
	vertical-align: top;
}

A.data {
	color: black;
	text-decoration: none;
}

A.data:Hover {
	color: #648BCB;
	text-decoration: underline;
}

.searchresult {
	background: #ffff33;
	color: black;
	padding: 2px;
}

/*
	NotePad Tabs
*/

/**/

.tab_content {
	padding-left: 12px;
	padding-right: 12px;
	background: #fcfcfc;
	border-left: 1px solid #585858;
	border-right: 1px solid #585858;
	border-bottom: 1px solid #585858;
}

/*
	Alternating Table Colours - non-site specific
*/

.list_table_header {
	color: white;
	background: black;
}

.list_table_cell_1 {
	background: #efefef;
	border-bottom: 1px solid #afafaf;
}

.list_table_cell_2 {
	background: white;
	border-bottom: 1px solid #afafaf;
}

/*
	Forms, Errors
*/


/*
	Control Panel Info Box
*/

.cpanel {
	padding: 1px;
	background: #efefef;
}

/*
	System Notices Strip
*/

.system_notices {
	margin-bottom: 12px;
	background: #ff0000;
	color: white;
}

/*
	Breadcrumb Trail
*/

.breadcrumb {
    font-size: 10px;
    color: #7f7f7f;
    margin-bottom: 12px;
}

A.breadcrumb:hover {
    font-size: 10px;
    color: #4f4f4f;
    margin-bottom: 12px;
}

/*
	Details Table (No gradient, boxed style)
*/

.details_table_header {
	padding-top: 6px;
	padding-bottom: 6px;
	text-align: center;
}

.details_table_cell {
	border-right: 1px solid #cdcdcd;
}

/*
	Information Table (gradient)
*/

.info_table {
	width: 100% !important;
	background: none !important;
}

.info_table_header {
	font-size: 12px;
}

/*
	Showcase Table - Generic Settings
*/

.showcase_table {margin-bottom: 12px;}

.showcase_header {font-size: 12px;}

/*
	Further Links Table
*/

.link_table {
    margin-top: 20px;
    margin-bottom: 20px;
}
 
.link_table_cell {
	padding-top: 3px;
	padding-bottom: 3px;
}

/*
	Text Headers
*/

.header,
.header_notop,
A.header_notop,
.header_bot
{
	font-size: 17px;
	text-align: left;
	color:#ddd !important;
}

.header {margin-top: 25px;}

.header_bot {
    margin-top: 12px;
    margin-bottom: 12px;
}


.left_nav_header {
	width: 152px;
	color:#222 !important;
	font-size: 11px;
	padding-top: 2px;
	padding-bottom: 2px;
	margin-top: 8px;
	margin-bottom: 4px;
	text-align: center;
}

.right_nav_header {
	width: 132px;
	color:#222 !important;
	font-size: 11px;
	padding-top: 2px;
	padding-bottom: 2px;
	margin-top: 8px;
	margin-bottom: 4px;
	text-align: center;
}

/*
	Structure
*/

.content {
    line-height: 17px;
}

.left_pane {
    border-right: 1px solid #d4d4d4;
    background-color: #ffffff;
    padding-bottom: 8px;
}

.left_info {
    text-align: justify;
	width: 150px;
}

.center_pane {
	background: transparent !important;
	padding: 10px;
	padding-left: 10px !important;
}

.right_pane {
    border-left: 1px solid #d4d4d4;
    background-color: #ffffff;
    padding-bottom: 5px;
}

.right_pane_no_content {
    background-color: #ffffff;
}

.site_index_section {
	padding-top: 4px;
	padding-bottom: 4px;
	padding-left: 4px;
	text-align: left;
}

/*
	Header and Footer
*/

.page_header {
    background: #000000;
}

.page_footer {
    font-family: Tahoma, Verdana, Geneva, Arial, Helvetica, sans-serif;
    font-size: 11px;
    color: #ffffff;
    text-decoration: none;
}

A.page_footer:Hover {
    font-family: Tahoma, Verdana, Geneva, Arial, Helvetica, sans-serif;
    font-size: 11px;
    color: #ff0000;
	text-decoration: underline;
}

/*
	Sort Table
*/

.sort-table {
	border:		1px Solid ThreeDShadow;
	background:	grey;
	color:		black;
}

.sort-table thead {background:ButtonFace;}

.sort-table td {
	padding-top: 2px;
	padding-bottom: 2px;
}

.sort-table thead td {
	border:			1px solid;
	border-color:	ButtonHighlight ButtonShadow
					ButtonShadow ButtonHighlight;
	cursor:			default;
}

.sort-table thead td:active {
	border-color:	ButtonShadow ButtonHighlight 
					ButtonHighlight ButtonShadow;
/*	padding:		3px 4px 1px 6px;*/
}

.sort-arrow {
	width:					11px;
	height:					11px;
	background-position:	center center;
	background-repeat:		no-repeat;
	margin:					0 2px;
}

.sort-arrow.descending {
/*	background-image:url("/g/downsimple.png");*/
}

.sort-arrow.ascending {
/*	background-image:url("/g/upsimple.png");*/
}

.even {
	background:	#efefef;
}
.odd {
	background: #ffffff;
}

/*
	Apollo
*/

/**/

.BoardIndexHead {
	padding: 0;
	margin: 0;
	font-size: 11px;
	text-align: center;
	color: #eee !important;
	height: 24px;
}

.BoardDescriptionPending {background: #ffcc66;}

.BoardSubHead {
	background: rgba(140,140,140,0.8) !important;
	border: 1px solid #8d8d8d;
	padding: 8px 0px;
}

.BoardInfo,
.BoardInfo1,
.BoardInfo2
{line-height: 13px;}

/*
	Thread Messages
*/

/* Some generic parameters */
.BoardMessage,
.BoardMessageSlim,
.BoardPost,
.DeletedBoardMessage,
.NewBoardMessage,
.NewBoardMessageSlim,
.PendingBoardMessage
{
	padding: 6px;
	line-height: 19px;
	font-size: 12px;
}

.BoardMessageSlim {background: #ff0 !important;}

.NewBoardMessageSlim {background: #D5E4F3;}

/* I'm guessing these are for moderators */
.PendingBoardMessage {background: #ffcc66;}

.DeletedBoardMessage {background: #FFCC00;}

/*
	The select list with all of the Boards in it
*/

.BoardNavGroup {background: #dedede;}

.BoardNav {background: #efefef;}

.BoardNavSelect {font-size : 12px;}

.Moderator {
	margin-top: 8px;
	margin-bottom: 8px;
	padding: 2px;
	border: 1px solid #2d2d2d;
	background: #f900f9;
}

.BoardQuote {
	background: none !important;
	color: #eee !important;
	padding: 8px !important;
	margin-left: 12px !important;
	margin-right:12px !important;
	margin-bottom:14px !important;
	margin-top:8px !important;
	font-size: 11px !important;
	line-height: 17px !important;
	border: 1px #eee !important;
	border-style: dotted !important;
}

.BoardMessageInfo {font-size: 11px;}

.signature {
	margin-top: 8px;
	margin-bottom: 0px !important;
	font-size: 10px;
	color: #fd0 !important;
	line-height: 12px;
}

A.CodeSnippet {
	text-decoration: none;
	color: #eee !important;
	padding: 4px;
	border: 1px solid #eee !important;
	background-color: #333 !important;
}

.CodeSnippetBlock {
	display: none;
	font-family: Courier, Arial;
	font-size: 10px;
	color: #eee !important;
	border: 1px solid #eee !important;
	background-color: #111 !important;
	padding: 4px;
	text-overflow: ellipsis;
	word-wrap: break-word;
}

.parent {
	background: #666;
	color: black;
}

.child {
	background: #fc00fc;
	color: black;
}

#msgBody 
{
	width: 95% !important;
	height: 200px !important;
}

/*
form[name="ApolloForm"]{
	position:fixed !important;
   	z-index: 1 !important;
	bottom:0 !important;
	left:0 !important;
	width:100% !important;
}*/

form[name="ApolloForm"] tr > td {width:50px !important;}

.content table:nth-child(2),
.content table:nth-child(3) {
	background-color:rgba(5,5,5,0.8) !important;
	position:fixed !important;
   	z-index:1 !important;
	top:0 !important;
	left:0 !important;
	width:100% !important;
	margin-top:0 !important;
}

.content table:nth-child(2) tr:nth-child(2){visibility:collapse !important;}

.content table:nth-child(3) {
    margin-top:19px !important;
   	box-shadow: 0px 8px 8px rgba(5,5,5,0.8);
}

/* hide the lower nav bar since we no longer need it */
.content table:nth-child(6) {visibility:collapse !important;}
}
/* ---- END OF GENERIC DOMAIN STYLE ----*/

/* --- THREADS --- */
@-moz-document url-prefix("http://forum.thegamecreators.com/?m=forum_view") {
.content table:nth-child(3),
.content table:nth-child(4) {
	background-color:rgba(5,5,5,0.8) !important;
	position:fixed !important;
   	z-index:1 !important;
	top:0 !important;
	left:0 !important;
	width:100% !important;
	margin-top:0 !important;
}

.content table:nth-child(3) tr:nth-child(2){visibility:collapse !important;}

.content table:nth-child(4) {
    margin-top:19px !important;
   	box-shadow: 0px 8px 8px rgba(5,5,5,0.8);
}

/* hide the lower nav bar since we no longer need it */
.content table:nth-child(13) {visibility:collapse !important;}

}
