css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("iplaywinner.com") {
#adshome {
    display: none;
}
body {
    background-color: rgb(0, 0, 0)!important;
    background-image: url(data:)!important;
    background-repeat: repeat-x;
}
#bannerAreaWrapper {
    display: none;
}
#pageFooter {
    font-size: 11px;
    border-top: 1px dotted rgb(200, 200, 200)!important;
    text-transform: uppercase;
    line-height: 1em;
    padding-left: 12px;
    padding-top: 10px;
    padding-bottom: 0px;
    margin-top: 10px;
    color: rgb(153, 153, 153);
    border-color: rgb(0, 0, 0);
}
#canvasWrapper {
    font-family: Tahoma,Arial,Helvetica,Geneva,sans-serif;
    margin-right: auto;
    margin-left: auto;
    color: rgb(200, 200, 200)!important;
}
.journal-entry-wrapper {
    padding-bottom: 0px;
    margin-right: 0px;
    margin-top: 0px;
    background-color: rgb(20, 20, 20)!important;
    border-color: rgb(0, 230, 255);
    background-repeat: no-repeat;
}
#gamesnav {
    font-family: 'Trebuchet MS','Trebuchet','Verdana','sans-serif';
    font-size: 11px;
    border: 1px solid rgb(185, 185, 185);
    text-transform: uppercase;
    text-align: center;
    line-height: 1.55em;
    font-weight: bold;
    font-style: normal;
    padding: 5px;
    margin-top: 10px;
    margin-bottom: 10px;
    background-color: rgb(20, 20, 20)!important;
    color: rgb(179, 179, 179)important;
}
a:link, a:visited, a:active {
    font-weight: bold;
    text-decoration: none;
    color: rgb(77, 166, 255)!important;
}
a:hover {
    text-decoration: underline;
    color: rgb(0, 171, 238)!important;
}
h2, .h2, .journal-entry .title a {
    text-decoration: none!important;
    color: rgb(51,113,196)!important;
}
h2, .h2, .journal-entry .title a:hover {
    color: rgb(0, 171, 238)!important;
}
.verticalNavigationBar .section {
    font-family: 'Trebuchet MS',Trebuchet,Verdana,sans-serif;
    font-size: 12px;
    font-weight: normal;
    font-style: normal;
    padding: 5px;
    margin-bottom: 5px;
    background-color: rgb(20, 20, 20)!important;
    border-color: transparent;
    background-position: 0px -100px;
    background-repeat: repeat-x;
}
#SmallList {
    font-family: 'Trebuchet MS','Trebuchet','Verdana','sans-serif';
    font-size: 11px;
    border: 1px solid rgb(181, 181, 181);
    text-transform: uppercase;
    font-weight: bold;
    font-style: normal;
    padding: 5px;
    margin-top: -5px;
    margin-bottom: 10px;
    background-color: rgb(10, 10, 10)!important;
}
.streamName {
    font-family: 'Trebuchet MS','Trebuchet','Verdana','sans-serif';
    font-size: 11px;
    border: 1px solid rgb(186, 186, 186);
    font-weight: normal;
    font-style: normal;
    padding: 3px;
    background-color: rgb(20, 20, 20)!important;
}
#dsq-combo-widget.grey #dsq-combo-content .dsq-combo-box {
    background: none repeat scroll 0% 0% rgb(20, 20, 20)!important;
}
#dsq-combo-widget.grey #dsq-combo-tabs li.dsq-active {
    background: none repeat scroll 0% 0% rgb(0, 0, 0)!important;
}
#dsq-combo-widget.grey #dsq-combo-tabs li {
    background: none repeat scroll 0% 0% rgb(10, 10, 10)!important;
}
.onlineStream {
    font-family: 'Trebuchet MS','Trebuchet','Verdana','sans-serif';
    border: 1px solid rgb(149, 149, 149);
    text-transform: uppercase;
    text-align: center;
    font-weight: normal;
    font-style: normal;
    padding: 2px;
    background-color: rgb(51,113,196)!important;
    color: rgb(255, 255, 255);
}
h2 {
    font-family: 'Trebuchet MS',Trebuchet,Verdana,sans-serif;
    border-bottom: 0px solid rgb(195, 195, 195)!important;
    font-weight: bold;
    margin-bottom: 5px;
    border-color: rgb(20, 20, 20)!important;
}
.journal-entry-tag-post-body {
    font-size: 12px;
    border-top: 0px solid rgb(208, 208, 208)!important;
    text-transform: uppercase;
    text-align: right;
    font-weight: bold;
    padding-top: 5px;
    border-color: rgb(208, 208, 208);
}
.journal-entry-tag-post-title {
    font-family: 'Trebuchet MS',Trebuchet,Verdana,sans-serif;
    font-size: 10px;
    text-transform: uppercase;
    font-weight: bold;
    font-style: normal;
    color: rgb(100, 100, 100)!important;
}
.paginationControlWrapper {
    font-family: 'Trebuchet MS',Trebuchet,Verdana,sans-serif;
    font-size: 12px;
    text-transform: uppercase;
    letter-spacing: 0px;
    font-weight: bold;
    font-style: normal;
    padding: 0px 5px 5px 0px;
    margin-top: 0px;
    margin-bottom: 5px;
    background-color: rgb(0, 0, 0)!important;
    color: rgb(100, 100, 100)!important;
    border-color: rgb(215, 215, 215);
}
#google-ad-insertion {
    display: none!important;
    height: 250px;
}
}
