*
*/
}

@-moz-document
url-prefix(https://userstyles.org/)
{
html{
padding:0 25px !important;
background-color:#B86F43 !important;
min-width:960px !important;
}
body{
display:table !important;
width:960px !important;
margin:0 auto 50px auto !important;
padding:0 0 20px 0 !important;
border:none !important;
border-radius:0 0 8px 8px !important;
box-shadow:0 0 16px #000 !important;
background:#fff url("data:image/png;base64,") 0 0 repeat-y !important;
color:#222 !important;
}
a{
outline:none !important;
text-decoration:none !important;
}
a:link{
color:#46a !important;
}
a:visited{
color:#64a !important;
}
a:hover{
color:#a90606 !important;
}
#front-page-best ul li.additional-link a:visited,
#front-page-newest ul li.additional-link a:visited,
#start-discussion:visited,
#miscellaneous-info td a[href^="/users/"]:visited{
color:#46a !important;
}
#front-page-best ul li.additional-link a:hover,
#front-page-newest ul li.additional-link a:hover,
#start-discussion:hover,
#style-author a[href^="/users/"]:hover{
color:#a90606 !important;
}
a[href="https://addons.mozilla.org/en-US/firefox/addon/2108/"],
a[href="https://addons.mozilla.org/firefox/addon/2108"],
a[href="https://chrome.google.com/extensions/detail/fjnbnpbmkenffdnngjfgmeleoegfcffe"]{
color:#609300 !important;
font-weight:bold;
text-decoration:underline !important;
}
a[href="https://addons.mozilla.org/en-US/firefox/addon/2108/"]:hover,
a[href="https://addons.mozilla.org/firefox/addon/2108"]:hover,
a[href="https://chrome.google.com/extensions/detail/fjnbnpbmkenffdnngjfgmeleoegfcffe"]:hover{
text-decoration:none !important;
}
ul li{
padding:1px 0 !important;
}
header{
position:relative !important;
z-index:20 !important;
margin:0 0 0 191px !important;
padding:25px 20px 5px !important;
border-bottom:1px solid #666 !important;
border-radius:0 !important;
box-shadow:0 2px 7px rgba(0,0,0,.6) !important;
background-color:#C9E683 !important;
background-image:-moz-linear-gradient(left,#C9E683 33%,#C0DE6F 50%,#90C706 100%);
}
header h1{
margin:12px 0 0 !important;
padding:0 !important;
font-size:28px !important;
font-style:italic !important;
font-weight:normal !important;
color:#333 !important;
}
header h1 a{
position:absolute !important;
top:0; left:19px;
margin:0 !important;
font-size:26px !important;
font-style:normal !important;
text-decoration:none !important;
text-shadow:1px 2px 2px rgba(90,135,110,0.3) !important;
}
header h1 a:link,header h1 a:visited{
color:#68c !important;
}
header h1 a:hover{
color:#a46 !important;
}
.style-brief-text header{
margin:0!important;
padding:15px!important;
border:none!important;
}
#content-wrapper{
margin:0 0 0 0px!important;
padding:0 0 0 0px!important;
width:760px!Important;
float:right!important;
}
#main-article{
margin:0!important;
}
article{
padding:10px 20px !important;
}
article section{
padding:0 !important;
}
article section h2{
margin:10px 0 !important;
color:#222 !important;
font-size:22px !important;
font-style:italic !important;
font-weight:normal !important;
text-shadow:1px 1px 2px rgba(000,000,000,.2) !important;
}
article section p,article #page-description{
padding:0 10px !important;
}

#miscellaneous-info{
margin:25px 5px 50px 0 !important;
width:300px !important;
}
#miscellaneous-info tr{
line-height:24px !important;
margin:0 !important;
padding:0 !important;
}
#miscellaneous-info th{
display:table-cell !important;
width:160px !important;
margin:0 !important;
padding:0 0 0 8px !important;
font-weight:normal !important;
color:#222 !important;
}
#miscellaneous-info th:after{
content:":";
}
#miscellaneous-info td{
display:table-cell !important;
position:absolute !important;
left:410px;
margin:0 !important;
}
#miscellaneous-info tr:first-child{
position:absolute !important;
margin:-35px 0 0 !important;
padding:0 0 25px 0 !important;
line-height:20px !important;
white-space:nowrap !important;
}
#miscellaneous-info tr:first-child th{
display:none !important;
}
#miscellaneous-info tr:first-child td{
display:inline !important;
margin-left:-410px !important;/* neg value of #m-i td left */
padding:0 !important;
font-style:normal !important;
}
#miscellaneous-info tr:first-child td:before{
content:"by";
padding-right:5px !important;
color:#222 !important;
font-size:14px !important;
font-style:italic !important;
font-weight:normal !important;
}
#miscellaneous-info td a[href^="/users/"]{
font-size:20px !important;
font-weight:normal !important;
}
#screenshots{
float:right !important;
margin:0px!important;
padding:6px 0 2px 0px !important;
min-height:150px !important;
min-width:320px !important;
background:none !important;
border:none !important;
border-radius:0 !important;
font-size:11px !important;
}
#screenshots figure{
margin:0 0 10px !important;
padding:0 !important;
text-align:center !important;
color:#888 !important;
}
#screenshots img{
max-width:330px !important;
max-height:200px !important;
margin:0 !important;
padding:5px !important;
border:1px solid #ccc !important;
border-radius:4px !important;
background:#ddd !important;
}
#more-screenshots span{
display:inline-block !important;
margin-bottom:3px !important;
color:#555 !important;
}
#overlay{
position:fixed !important;
height:100% !important;
width:100% !important;
}
#lightbox #outerImageContainer{
min-height:50px !important;
min-width:250px !important;
padding-bottom:2px !important;
border-bottom:1px solid #bbb !important;
}
#lightbox #imageDataContainer{
min-width:250px !important;
padding-top:12px !important;
}
#lightbox #prevLink,#lightbox #nextLink{
height:100% !important;
}
#control-panel{
margin:20px 0 15px !important;
width:350px !important;
}
#control-panel a{
color:#36C !important;
}
#control-panel a:hover{
color:#600 !important;
}
#control-panel a[href$="/edit"],
#control-panel a[href^="/styles/delete"]{
display:inline-block !important;
width:170px !important;
line-height:0 !important;
overflow:hidden !important;
visibility:hidden !important;
padding:0 !important;
}
#control-panel a[href$="/edit"]:after,
#control-panel a[href^="/styles/delete"]:after{
display:block !important;
width:150px !important;
padding:5px 0 !important;
line-height:1em !important;
visibility:visible !important;
font-size:14px !important;
text-align:center !important;
text-shadow:1px 1px 2px #ccc !important;
border:4px solid #000 !important;
border-radius:8px !important;
}
#control-panel a[href$="/edit"]:after{
background-color:#B5DCEF !important;
content:"Edit this Style";
}
#control-panel a[href^="/styles/delete"]:after{
background-color:#FFFF66 !important;
content:"Delete this Style";
}

#main-style-info{
margin:0 !Important;
padding:0!important;
width:300px!important;
float:right;
position:relative;
left:-30px
}
#left-info{
margin:0 20px 0 -20px!important;
}
#additional-info{
clear:both;
}
#info-tables{
float:left!important;
}
#style-author-info, #style-install-info{
display:block!important;
margin:0!important;
width:300px!important;
}
#style-info:before{
display:block !important;
margin:0 !important;
font-size:22px !important;
font-style:italic !important;
text-decoration:none !important;
text-shadow:1px 1px 2px rgba(000,000,000,.2) !important;
content:"  Style Description";
}
#show-code{
clear:both;
}
#install{
clear:both !important;
width:auto !important;
max-width:677px !important;
margin:0 !important;
padding:0 !important;
background:#fff !important;
}
.install-status{
padding:8px 10px 10px 10px!important;
}
#post-install-ad{
display:none!important;
}
#style-options{
margin:0 !important;
padding:0 !important;
text-align:left !important;
}
#style-options fieldset{
text-align:left !important;
margin:10px 3px !important;
padding:8px 10px !important;
font-weight:normal !important;
}
#style-options fieldset legend{
padding:1px 6px !important;
font-weight:bold !important;
color:#fff !important;
background:#444 !important;
text-align:center !important;
line-height:1.4em !important;
}
#obsoletion-message,
#install div:not([id="style-options"]){
border:1px solid #666 !important;
border-radius:15px !important;
box-shadow:4px 5px 10px rgba(0,0,0,.3) !important;
}
#obsoletion-message{
margin:15px 0 25px 1px !important;
 
background-color:#ff3 !important;
color:#000 !important;
}
#install div:not([id="style-options"]){
clear:both !important;
margin:20px 0 0 !important;
padding:5px 15px !important;
color:#222 !important;
background-color:#c9e683 !important;
}
#switch-browser{
clear:both !important;
float:left !important;
margin:20px 0 5px 15px !important;
} 
 
#more-info{
clear:left !important;
float:left !important;
margin:40px 0 0 !important;
}
#discussions-area{
clear:left !important;
float:left !important;
margin:20px 0 30px!important;
}
#discussions-area #social{
margin:20px 25px 0 !important;
padding:0 !important;
}

#donate p{
margin-left:15px !important;
line-height:24px !important;
}
#donate input[alt="Donate"],#pledgie{
display:inline-block !important;
vertical-align:top !important;
opacity:.8 !important;
}
#donate input[alt="Donate"]:hover,#pledgie:hover{
opacity:1 !important;
}
#discussions-area h3{
margin:20px 10px 16px !important;
padding:0 !important;
visibility:hidden !important;
}
#discussions-area h3:before{
content:"Discuss it!";
font-size:15px !important;
font-weight:normal !important;
font-style:italic !important;
visibility:visible !important;
}
#discussions-area #no-discussions{
margin:0 15px 15px !important;
}
#discussions-area #start-discussion{
text-decoration:underline !important;
}
#discussions-area #discussions{
margin:0 0 10px 15px !important;
padding:0 30px !important;
}
#front-page-best ul,
#front-page-newest ul{
padding:0 15px !important;
}
#front-page-best ul li,
#front-page-newest ul li{
margin:0 !important;
}
#front-page-best ul li.additional-link,
#front-page-newest ul li.additional-link{
list-style:none !important;
margin-top:10px !important;
font-size:12px !important;
font-weight:bold !important;
font-style:italic !important;
}
#subcategory-list{
list-style-type:decimal !important;
line-height:1.2em !important;
padding:1px 35px !important;
font-size:12px !important;
color:#999 !important;
}
#search-options{
position:relative;
z-index:19 !important;
margin:-10px -14px 10px !important;
padding:6px 10px 4px !important;
border:1px solid #800 !important;
border-top:none !important;
border-radius:0 0 15px 15px !important;
box-shadow:0 1px 2px rgba(0,0,0,.3) !important;
background-color:#C92626 !important;
color:#fff !important;
font-family:Arial,sans-serif !important;
font-size:11px !important;
line-height:2em !important;
}
#search-options select,
#search-options input{
font-family:Arial,sans-serif !important;
font-size:12px !important;
}
#search-options input[name="search_terms"]{
width:100px !important;
}
#style-list{
/* Column settings. Sample max-width/column-count:
** 550 for 1 col | 1100 for 2 col | 1650 for 3 col
*/
-moz-column-count:2 !important;
/*max-width:1100px !important;*/
border-left:1px dotted #ccc !important;
-moz-column-rule:1px dotted #ccc !important;
list-style-type:none !important;
line-height:13px !important;
margin:10px 10px 25px 1px !important;
padding:0 0 0 6px !important;
font-size:12px !important;
}
#style-list li{
display:inline-block !important;
width:90% !important;
margin:1px 0 !important;
padding:1px 0 !important;
}
#style-list li:hover{
background:#e9f0f3 !important;
color:#000 !important;
}
#style-list li.obsolete{
text-decoration:none !important;
}
#style-list li.obsolete a{
text-decoration:line-through !important;
}
#style-list li.good-rating:before,
#style-list li.bad-rating:before,
#style-list li.ok-rating:before,
#style-list li.no-rating:before{
content:attr(average-rating) !important;
display:inline-block !important;
width:21px !important;
margin:0 4px 0 -24px !important;
font-size:9px !important;
text-align:center !important;
vertical-align:top !important;
background-color:#ff0 !important;
color:#000 !important;
outline:1px solid #990 !important;
visibility:hidden !important;
}
#style-list li.no-rating:before{
content:"\00A0";
}
#style-list li.good-rating:hover:before,
#style-list li.bad-rating:hover:before,
#style-list li.ok-rating:hover:before{
visibility:visible !important;
}
#style-list li:after{
float:right;
margin-left:6px !important;
margin-right:0 !important;
font-size:10px !important;
color:#a9b663 !important;
content:attr(weekly-install-count) "/" attr(total-install-count) !important;
}
#style-list li:hover:after{
color:#666 !important;
}
#style-list a.edit-link,
#style-list a.delete-link{
display:block !important;
float:right !important;
margin-left:4px !important;
margin-bottom:-3px !important;
line-height:20px !important;
height:16px !important;
width:16px !important;
overflow:hidden !important;
visibility:hidden !important;
}
#style-list a.edit-link:before{/* Icon by FamFamFam - www.famfamfam.com */
content:url("data:image/png;base64,") !important;
visibility:visible !important;
}
#style-list a.delete-link:before{/* Icon by FamFamFam - www.famfamfam.com */
content:url("data:image/png;base64,") !important;
visibility:visible !important;
}
/****************
** Compatibility styling | moz-only | userscripts.org
** Enhancer #80666, Table View Plus #100937
** Enhancer2 is no longer supported by this theme.
*/
article > ul:first-child + h2{
display:block !important;
}
#table-container{
margin:5px !important;
border:none !important;
}
#summaryTable{
width:710px !important;
margin:-20px 0 -35px !important;
}
#optionTable,#optionTable button,#optionTable2 button{
text-shadow:none !important;
font-size:11px !important;
}
#optionTable{
margin-bottom:-110px !important;
top:20px !important;
left:155px !important;
}
#optionTable2{
margin:80px 0 -160px -325px !important;
}
#style-table{
width:710px !important;
margin:0 0 10px 0 !important;
font-family:Arial,Verdana,sans-serif !important;
font-size:13px !important;
color:#666 !important;
}
#style-table th{
padding:5px 3px !important;
overflow:hidden !important;
border-color:#e9f0f3 !important;
background:#B5DCEF !important;
color:#248 !important;
outline:1px solid rgba(115,186,222,0) !important;
}
#style-table th:hover{
outline:1px solid rgba(115,186,222,1) !important;
}
#style-table .metalink-open,#style-table .metalink-close{
opacity:.5 !important;
}
#style-table .metalink-open:hover,#style-table .metalink-close:hover{
opacity:1 !important;
}
#style-table icon#optBtn{
margin-right:30px !important;
}
icon#openbtn,icon#closebtn,icon#optBtn{
margin-top:-2px !important;
}
#style-table td{
border-top:none !important;
border-color:#e0e6e9 !important;
padding:0 2px !important;
}
#style-table tr:hover td{
background:#e9f0f3 !important;
color:#000 !important;
}
#style-table .subaffects{
font-style:italic !important;
}
#popup_container{
z-index:9999 !important;
}


/***********/
.pagination{
margin:0 !important;
font-size:12px !important;
color:#888 !important;
}
.pagination a,.pagination .current,.pagination .prev_page,.pagination .next_page{
margin:0 !important;
padding:1px 6px 2px !important;
border:1px solid #46a !important;
border-radius:4px !important;
background:#fff !important;
}
.pagination .current{
border:none !important;
box-shadow:none !important;
background:#46a !important;
color:#fff !important;
}
.pagination a:visited{
color:#46a !important;
}
.pagination a:hover{
background:#e9f0f3 !important;
