}
textarea#css{
margin-bottom:20px !important;
}
button[onclick^="addSetting(event)"]{
margin-bottom:20px !important;
}
input[name="style\[screenshot_type_preference\]"],input[name="style\[screenshot_url_override\]"]{
margin-bottom:30px !important;
}
input[name="style\[before_screenshot\]"]{
margin-bottom:40px !important;
}
input[name="style\[after_screenshot\]"]{
margin-bottom:20px !important;
}
/****************
** Rating-Images
*/
#style-list li.no-rating,
#style-list li.good-rating,
#style-list li.ok-rating,
#style-list li.bad-rating{
padding-left:25px !important;
background-position:6px 3px !important;
background-repeat:no-repeat !important;
}
#miscellaneous-info .good-average-rating,
#miscellaneous-info .bad-average-rating,
#miscellaneous-info .ok-average-rating{
