padding:0 0 16px 16px !important;
background-position:0 4px !important;
background-repeat:no-repeat !important;
}
#style-list li.no-rating{
background-image:url("data:image/png;base64,") !important;
}
#style-list li.good-rating,
#miscellaneous-info .good-average-rating{
background-image:url("data:image/png;base64,") !important;
}
#style-list li.ok-rating,
#miscellaneous-info .ok-average-rating{
background-image:url("data:image/png;base64,") !important;
}
#style-list li.bad-rating,
#miscellaneous-info .bad-average-rating{
background-image:url("data:image/png;base64,") !important;
}
#discussions .no-rating{
list-style-image:url("data:image/png;base64,") !important; 
}
#discussions .good-rating{
list-style-image:url("data:image/png;base64,") !important; 
}
#discussions .ok-rating{
list-style-image:url("data:image/png;base64,") !important; 
}
#discussions .bad-rating{
list-style-image:url("data:image/png;base64,") !important; 
}
