@-moz-document
url-prefix(https://userstyles.org/users/)
{
h2{
margin-top:40px !important;
}
article ul:first-child{
display:block !important;
width:auto !important;
list-style:none !important;
margin:-11px -17px 0 !important;
padding:0 10px !important;
background-color:#393 !important;
border:1px solid #363 !important;
border-top:none !important;
border-radius:0 0 15px 15px !important;
box-shadow:0 1px 2px rgba(0,0,0,.3) !important;
font-size:11px !important;
font-weight:bold !important;
text-align:center !important;
}
article ul:first-child li{
display:inline-block !important;
padding:0 8px !important;
text-align:center !important;
}
article ul:first-child li a{
display:block !important;
padding:2px 0 1px !important;
line-height:2em !important;
white-space:nowrap !important;
color:#eee !important;
}
article ul:first-child li a:hover{
color:#ff0 !important;
text-decoration:none !important;
}
article ul:first-child li:last-child{/* "Discussions" link. */
display:block !important;
margin:0 !important;
width:100% !important;
height:0 !important;
border:none !important;
text-align:right !important;
}
article ul:first-child li:last-child a[href^="http://forum.userstyles.org/?Discussion/StyleAuthorID="]{
float:left !important;
margin:0 20px 0 -2px !important;
font-size:12px !important;
font-weight:normal !important;
font-style:italic !important;
color:#248 !important;
}
article ul:first-child li a[href^="http://forum.userstyles.org/?Discussion/StyleAuthorID="]:after{
content:" \00BB";
}
article ul:first-child li a[href^="http://forum.userstyles.org/?Discussion/StyleAuthorID="]:hover{
color:#a90606 !important;
}
.author-styles td{
border:1px dashed #339933;
}
.author-styles tbody tr:hover{
background:#B5DCEF;
}
/*end*/}

