css:
@-moz-document url-prefix("http://primeshare.tv/"), domain("primeshare.tv") {
#header, .top, #footer, .addthis_toolbox, #sb-container, #divExoLayerWrapper, iframe, .notification, .ad {
display: none !important;
}

body, html, #content, #content .box {
background: black;
}

#content .box {
  margin: 0 auto;
  border: none;
  -moz-box-shadow: none;
  -webkit-box-shadow: none;
  box-shadow: none;
}

#content .box h1 {
color: #00E0FF;
  text-shadow: none;
}

div {
height: auto !important;
}

#player {
height: 518px !important;
}
}
