css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("rue89.com") {

body {
    font-size: large !important;
}

#article .stats, #article .tags {
    display: none !important;
}

#head_section ul,
.metas.article, #sidebar_left,
#header, #footer,
#article_tools,.partner,
#rebonds, .thanks, .tags, .stats, #plus_loin, 
#commentaires, .outbrain, #sidebar, #rue89-conjugueur,
#sidebar_right, #footer_blog, #blog_alert, #nav_articles
 {
    display: none !important;
}

#content, #main, #article, .content
{
    font-size: medium !important;
}

#wrapper,
#content, #main, 
#article, .content  {
    float: none !important;
    width: auto !important;
    max-width: 100% !important;
    border: none !important;
    background: none !important;
    padding: 0 !important;
}

#content {
    max-width: 800px !important;
    margin: auto;
}

}
