/*  
********************************** 
    Casemon Minimal Dark Theme 
    By mrburitto 
**********************************
*/

/* Cleaning Up */

.x-grid3-hd-row td {
    border-left: none !important;
    border-right: none !important;
}

.x-panel {
    background: transparent;
}

.x-panel-header {
    background-image: none !important;
    background: transparent !important;
}

.x-splitbar-proxy,.x-layout-split {
    background-color: transparent !important;
}

.x-panel-body {
    background: transparent !important;
    border-width: 0px !important;
}

.x-tab-panel-header-plain .x-tab-strip-spacer, .x-tab-panel-footer-plain .x-tab-strip-spacer, .x-panel-body-noheader {
    border: none !important;
    background:transparent !important;
}

.x-panel-header {
    color: #D0D0D0 !important;
    font-weight: bold;
    font-size: 12px;
    border: 0px solid !important;
    background-image: none !important;
    background: transparent !important;
    display: none !important; /* Remove This Line To Show Headers/Titles */
}

.x-panel.x-panel-noborder {
    padding: 0px !important;
}

.x-toolbar {
    display: none !important;
}

ul.x-tab-strip-top {
    border-bottom: 0px solid !important;
}
.x-tab-strip-spacer {
    display: none !important;
}

ul.x-tab-strip li {
    margin-left: 0px !important;
}

.x-box-item {
    background: transparent !important;
}

x-panel {
    border: none !important;
}

.x-tab-panel-body {
    border: 0px solid !important;
    background: transparent !important;
}

.x-grid3-header {
    background: transparent !important;
    padding: 3px !important;
}

.x-grid3 {
    background: transparent !important;
}
td.ux-grid-hd-group-cell {
    background: transparent !important;
}

.x-grid3-hd-inner {
    padding: 0px !important;
}

#east-panel .x-grid3-header-offset /*> table:nth-child(2)*/ {
    display: none !important;
}
#profileList + hr {
    display: none !important;
}
#taskList {
    display:none !important;
}

/* Backlog */
.x-panel-tbar-noheader .x-toolbar, .x-panel-mc .x-panel-tbar .x-toolbar {
    border-top: 0px solid !important;
}

.x-toolbar {
    background: transparent !important;
    background-image: none !important;
}

.x-grid-group-hd {
    border-bottom: 0px solid !important;
}





/* Case View Styles */

body, td {
    font-family: Arial,Helvetica,sans-serif;
    color: #D0D0D0 !important;
}
#profileGrid .x-grid3-row td {
    font-size: 12px !important;
}

.x-tab-panel-bwrap {
    background-color: #202020 !important;
    padding-bottom: 0px !important;
    border-radius: 5px !important;
}

.x-grid3-viewport {
    background-image: linear-gradient(rgba(40, 40, 40, 0.2), rgb(64, 64, 64) 20%, rgba(10, 10, 10, 0.3));
}

.x-tab-strip-top .x-tab-right, .x-tab-strip-top .x-tab-left, .x-tab-strip-top .x-tab-strip-inner { /*tabs*/
    background-image: -moz-linear-gradient(rgba(5, 5, 5, 0.9), #404040 20%, rgba(55,55,55, 0.6)) !important;
    padding-top: 2px !important;
    padding-left: 8px !important;
    padding-bottom: 2px !important;
    padding-right: 8px !important;
    -moz-transition: box-shadow .4s ease-out !important;
    -moz-transition: background-image .4s ease-out !important;
}

.x-tab-strip-top .x-tab-right:hover {
    box-shadow: 0px 0px 3px 2px #000 !important;
    border-radius: 2px !important;
    background-image: -moz-linear-gradient(rgba(25, 25, 25, 0.8), #505050 20%, rgba(45,45,45, 0.6)) !important;
    -moz-transition: background-image .4s ease-out !important;
    -moz-transition: box-shadow .5s ease-out !important;
}

.x-tab-strip-top .x-tab-right {
    background: -moz-linear-gradient(rgba(5, 5, 5, 0.9), #404040 20%, rgba(50,50,50, 0.5)) !important;
    -moz-transition: background-image .4s ease-out !important;
    -moz-transition: box-shadow .5s ease-out !important;
}

.x-tab-strip-active .x-tab-strip-text,.x-tab-strip span.x-tab-strip-text  {
    font-size: 15px !important;
    padding-top: 2px !important;
    padding-left: 8px !important;
    padding-bottom: 2px !important;
    padding-right: 8px !important;
    color: #000 !important;
    font-weight: bold !important;
    text-shadow: 0px 1px 1px rgba(200,200,200,.2) !important;
    letter-spacing: 1px !important;
    -moz-transition: color .8s ease-out !important;
}

.x-tab-right:hover span.x-tab-strip-text,.x-tab-right:hover .x-tab-strip span.x-tab-strip-text {
    color: #001B49 !important;
    -moz-transition: color .3s ease-out !important;
}


#tabPanel {
    top: 1px !important;
    background-color:#101010 !important;
    border-radius: 5px !important;
}

.x-grid3-row-alt {
    background-color: #202020 !important;
}
.x-grid3-row {
    border-color: #101010 !important;
}
#personalGridView .x-grid3-row {
    height:30px !important;
}

#personalGridView .x-grid3-row-first {
    height: 40px !important;
}

.x-border-layout-ct {
    background-color: #101010 !important;
}

.x-box-inner {
    background-color: #101010 !important;
}
#personalGridView .x-grid3-hd-row td,#collaborationGridView .x-grid3-hd-row td {
    font-size: 13px !important;
    color: #000 !important;
    padding-top: 5px !important;
    padding-bottom: 5px !important;
    text-shadow: 0px 1px 1px rgba(250,250,250,.2);
}

div.x-grid3-cell-inner.x-grid3-col-actions #acc, div.x-grid3-cell-inner.x-grid3-col-remove input[value="Accept"] {
    padding: 1px !important;
    box-shadow: 0px 0px 3px 1px #000 !important;
    color: #000 !important;
    border-radius: 3px !important;
    opacity: .9 !important;
    margin-left: 4px !important;
}
div.x-grid3-cell-inner.x-grid3-col-actions input,.x-grid3-col-remove input {
    padding: 1px !important;
    box-shadow: 0px 0px 2px 1px #680000 !important;
    color: #000 !important;
    border-radius: 3px !important;
    opacity: .9 !important;
    margin-left: 4px !important;
}

.x-grid3-col-12 a {
    color: #FFF !important;
}

.x-grid3-col-cseowner a {
    color: #FFF !important;
}

.sev1 {
    opacity: .4 !important;
}
.sev2 {
    opacity: .4 !important;
}
.sev3 {
    background-color: #009900 !important;
    opacity: .4 !important;
}
.sev4 {
    opacity: .4 !important;
}
.processed-row-sev2 {
    border: 1px solid rgb(255, 215, 0) !important;
    background: inherit !important;
}
.processed-row-sev1 {
	background: #500000 !important;
	border: 1px solid red !important;
}
#profileGridView .srGREEN,#profileGridView .srPURPLE,#profileGridView .srRED,#profileGridView .srYELLOW {
    color: #FFF !important;
    font-size: 12px !important;
    letter-spacing: 1px !important;
    text-shadow: 0px 1px 10px rgba(0,0,0,1) !important;
}
#profilegrid .x-panel-body {
    margin-bottom: 20px !important;
}

.x-grid-empty {
    color: rgb(21, 66, 139) !important;
    font-size: 16px !important;
    font-weight: bold !important;
    text-shadow: 0px 1px 2px rgba(10,10,10,1) !important;
    text-align: center !important;
}

#personalGridView .x-grid3-scroller {
    min-height: 100px !important;
}

/* Backlog Styles */

#backlogGridView .x-grid3-row {
    height: 60px !important;
    border-top-width: 3px !important;
    border-bottom-width: 1px !important;
    border-top-color: #101010 !important;
    border-bottom-color: rgba(0,0,0,.8) !important;
    margin-bottom: 0px !important;
}
#backlogGridView .x-grid3-row td {
    font-weight:bold !important;
}
#backlogGridView .x-grid3-viewport {
    background-image: linear-gradient(rgba(60, 60, 60, 0.4), rgb(64, 64, 64) 20%, rgba(10, 10, 10, 0.3));
}
#backlogGridView .x-grid3-header * {
    padding-top: 2px !important;
    font-size:12px !important;
    font-weight: bold !important;
}
#backlogGridView div[id$="newWorkType-ENG-hd"] {
    display: none !important;
}
a[title^="Email "] {
    color: #FFF !important;
    -moz-transition: color .3s ease-out !important;
    text-shadow: -1px 1px 0px rgba(0,0,0,0) !important;
    font-size: 12px !important;
    text-decoration: none !important;
    font-weight:bold !important;
    box-shadow: 0px 0px 5px 2px rgba(0,0,0,1)!important;
    border-radius: 5px !important;
    padding-right: 5px !important;
    padding-left: 5px !important;
    padding-top: 6px !important;
    padding-bottom: 8px !important;
    border: 0px solid #000 !important;

}
#backlogGridView .srGREEN,#backlogGridView .srRED,#backlogGridView .srYELLOW,#backlogGridView .srPURPLE {
    color: #FFF !important;
    font-size: 12px !important;
    letter-spacing: 1px !important;
    text-shadow: 0px 1px 8px rgba(0,0,0,1) !important;
}
#backlogGridView .x-grid3-row-alt {
    background-color: #303030 !important;
}
#backlogGridView .x-grid-group-hd .x-grid-group-title {
    color: #D0D0D0 !important;
    font-size:14px !important;
    text-align: center !important;
    text-shadow:
     1px -1px 0 rgba(200,200,200,.2),  
     1px 1px 0 rgba(200,200,200,.1) !important;
    letter-spacing: 4px !important;
    font-weight: bold !important;
}
#backlogGridView .x-grid-group-hd[id$="CE Pending-hd"] .x-grid-group-title {
    color:#002C5F !important;
}
#backlogGridView .x-grid-group-hd {
    background-color: rgb(0,0,0) !important;
    box-shadow: 0px 0px 5px 1px rgb(200,200,200) !important;
}
/* Images */
#backlogGridView img[title~=View\/Update] {
    opacity: 0 !important;
}
#backlogGridView img[title~=Email] {
    opacity: .2 !important;
    margin-top:10px !important;
    display: none !important;
}
#backlogGridView img[title~=Call] {
    opacity: .2 !important;
    display: none !important;
}
#backlogGridView img[title~=Start] {
    opacity: .2 !important;
    display: none !important;
}
#wrkGrpGridId img[title~=Send] {
    opacity: .2 !important;
}
#backlogGridView img[title~=Transfer] {
    opacity: .2 !important;
}
#backloggrid div[title~=Click] {
    opacity: .2 !important;
}
#backlogGridView img[title~=Use] {
    opacity: .2 !important;
}
#backlogGridView img[title~=No] {
    opacity: .5 !important;
}

/* Right Panel Styles */

#east-panel {
    top: 34px !important;
    background-color: #101010 !important;
    z-index: 100 !important;
    position: fixed !important;
}
#east-panel .x-panel-body,#east-panel .x-panel-bwrap,#east-panel .x-panel,#east-panel .x-viewport,#east-panel .x-grid3,#east-panel .x-grid3-scroller {
    min-width: 100% !important;
}

#east-panel .x-grid3-viewport {
    background-image: none !important;
    background: transparent !important;
}

.x-grid3-col-userid a {
    color: #FFF !important;
    font-size: 12px !important;
}

.x-box-layout-ct .x-btn {
    z-index: 100 !important;
    top: 1px !important;
    box-shadow: 0px -1px 4px 2px #680000 !important;
    border-radius: 2px !important;
    background-color: #101010 !important;
    opacity: .8 !important;
}
.x-box-layout-ct .x-btn-text {
    color: #000 !important;
}
input#searchSRorCase {
    top: 1px !important;
}
#teamAvailGrid .x-grid3-col-userid img {
    display:none !important;
}
#teamAvailGrid .x-grid3-col-userid {
    margin-right: -20px !important;
}

#teamAvailGrid .x-grid3-td-1{
    width: 0px !important;
}
