css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("scienceblogs.com") {

blockquote{
color:#444444 !important;
font:12px/1.5 Verdana,sans-serif !important;
}
div.blogMain div.entry p.ref{
margin:10px !important;
text-indent:0 !important;
}
p.commentFooter,body,.evencomment{
background:#ffffff !important;
}
span.commentNumber{
color:#aaaaaa !important;
padding:0 5px 0 0 !important;
}
textarea{
font:12px/1.4 Verdana,sans-serif !important;
height:400px !important;
padding:5px !important;
width:930px !important;
}
.authorComment,.authorComment p{
background-color:#fdfcd6 !important;
}
.blogContainer,#sbMainContainer{
background:none !important;
border:none !important;
margin:0 auto !important;
width:1500px !important;
}
.blogMain{
width:950px !important;
}
.blogMain a{
color:#0054a6 !important;
text-decoration:none !important;
}
.blogMain a:visited{
color:#630099 !important;
}
.blogMain div.entry{
background:none !important;
}
.blogMain li{
font-family:Georgia,serif !important;
font-size:13px !important;
line-height:1.6 !important;
}
.blogMain ol,.blogMain ul{
margin:10px 0 20px 40px !important;
}
.blogMain p{
font-family:Georgia,serif !important;
font-size:13px !important;
line-height:1.7 !important;
margin:0 0 15px !important;
}
.comment{
border-top:1px dotted #666666 !important;
}
.comment-content{
background:#eeeeee !important;
padding:20px 10px !important;
}
.comment-content p{
font-family:Georgia,serif !important;
font-size:13px !important;
line-height:1.7 !important;
}
.comments-content h3.metaHeader{
background:none !important;
font-style:italic !important;
}
.creationist p{
font-family:"Comic Sans MS",sans-serif !important;
}
.entry{
font-family:Georgia,serif !important;
font-size:13px !important;
line-height:1.7 !important;
}
.entryMeta{
background-color:#ffffff !important;
}
.leaderboard,.seedBanner,#topPromoContainer,#trackbackInfo,#trackback,table[width*="444px"]{
display:none !important;
}
.mainCol{
width:1190px !important;
}
#mainNav{
display:none !important;
}

}
