css:
/* AGENT_SHEET */
#main-window[tabsintitlebar]:not([inFullscreen]) #toolbar-menubar[inactive] ~ #TabsToolbar{
height: 24px !important;
margin-bottom: -2px !important;
margin-top: -3px;
margin-left: -4px;
background-image: -moz-linear-gradient(#f7f7f7, #f0f0f0) !important;
}

/*REMOVE CLOSE, MINIMIZE, MAXIMIZE*/
#titlebar-buttonbox-container { display:none!important; }

/*HIDE NEW TAB BUTTON*/
.tabs-newtab-button {
  opacity: 0 !important;
}

/*TAB STYLES*/
.tabbrowser-tab[fadein]:not([pinned]) {
min-width: 100px !important;
max-width: 100px !important;
max-height: 19px !important;
margin-right: -2px !important;
background-image: -moz-linear-gradient(#fbfbfb, #d5d5d5) !important;
}
.tabbrowser-tab[fadein][pinned] {
min-width: 28px !important;
max-width: 43px !important;
max-height: 19px !important;
margin-right: -2px !important;
background-image: -moz-linear-gradient(#fbfbfb, #d5d5d5) !important;
}
.tabbrowser-tab[fadein]:not([pinned]):not([selected]) {
min-width: 100px !important;
max-width: 100px !important;
max-height: 19px !important;
margin-right: -2px !important;
background-image: -moz-linear-gradient(#d5d5d5, #fbfbfb) !important;
}
.tabbrowser-tab[fadein][pinned]:not([selected]) {
min-width: 28px !important;
max-width: 43px !important;
max-height: 19px !important;
margin-right: -2px !important;
background-image: -moz-linear-gradient(#d5d5d5, #fbfbfb) !important;
}


/*CLOSE TAB BUTTON ONLY SHOWING ON HOVER*/
.tabbrowser-tab:not(:hover) .tab-close-button { display: none !important; }
.tabbrowser-tab[selected="true"][pinned="true"]:hover .tab-close-button,
.tabbrowser-tab:not([pinned]):hover .tab-close-button { display: -moz-box !important; }


.tab-throbber{
max-width: 15px !important;
min-width: 15px !important;
margin-left: 3px !important;
margin-top: -2px !important;
height: 15px !important;
}
.tab-icon-image{
max-height: 15px !important;
min-height: 15px !important;
margin-top: -2px;
box-shadow:  0px 0px 2px 0px #9f9e9e;}


/*URLBAR STYLE*/
#urlbar, #urlbar-container {
min-width: 250px !important;
max-width: 250px !important;
height: 21px !important;
background: #eaeaea !important;
box-shadow: inset 1px 1px 9px 3px #d0cfcf;
margin-left: -2px !important;
margin-right: -3px !important;
padding-left: 2px !important;}

/*REMOVE SOME BUTTONS FROM THE ADDRESS BAR*/
#go-button, .search-go-button {display: none !important;}
#star-button, .autocomplete-history-dropmarker {display: none !important;}
#identity-box  {display: none;}

/*REMOVE FIREFOX ORANGE BUTTON*/
#appmenu-button
{ display: none !important; }

/*SWAP WHITE ICONS FOR BLACK ONES*/
#main-window :-moz-any(#back-button, #forward-button, #reload-button, #stop-button, #home-button, #print-button, #downloads-button, 
#history-button, #bookmarks-button, #bookmarks-menu-button, #new-window-button, #cut-button, #copy-button, #paste-button, #fullscreen-button, #zoom-out-button, #zoom-in-button, #sync-button:not([status="active"]), #feed-button, #new-tab-button) > .toolbarbutton-icon
	{list-style-image: url("chrome://browser/skin/Toolbar.png") !important; }

#main-window #alltabs-button
	{list-style-image: url("chrome://browser/skin/mainwindow-dropdown-arrow.png") !important; }

#main-window #tabview-button

	{list-style-image: url("chrome://browser/skin/tabview/tabview.png") !important; }

#main-window #TabsToolbar #new-tab-button > .toolbarbutton-icon

	{list-style-image: url("chrome://browser/skin/tabbrowser/newtab.png") !important; }

#main-window .scrollbutton-up,
#main-window .scrollbutton-down

	{list-style-image: url("chrome://browser/skin/tabbrowser/tab-arrow-left.png") !important; }

/*BUTTON STYLES*/
#home-button, #back-button, #forward-button, #downloads-button, #bookmarks-button, #bookmarks-menu-button, #stop-button, #reload-button{
background-image: -moz-linear-gradient(#f7f7f7, #f0f0f0) !important;
}

#home-button:hover, #back-button:hover, #forward-button:hover, #downloads-button:hover, #bookmarks-button:hover, #bookmarks-menu-button:hover, #stop-button:hover, #reload-button:hover{
box-shadow: inset 0px 0px 7px 2px rgba(0, 0, 0, 0.2);
}
#home-button[disabled]:hover, #back-button[disabled]:hover, #forward-button[disabled]:hover, #downloads-button[disabled]:hover, #bookmarks-button[disabled]:hover, #bookmarks-menu-button[disabled]:hover, #stop-button[disabled]:hover, #reload-button[disabled]:hover{
box-shadow: inset 0px 0px 7px 2px rgba(0, 0, 0, 0);
}

/*CHANGE CLOSE-TAB BUTTON*/
.tab-close-button {

  -moz-appearance: none!important;

  pointer-events: auto!important;

  list-style-image: url(data:image/png;base64,)!important;

  -moz-image-region: rect(0, 12px, 12px, 0)!important;

}



.tab-close-button:hover,

.tab-close-button:hover[selected="true"] {

  -moz-image-region: rect(0, 24px, 12px, 12px)!important;

}



.tab-close-button:hover:active,

.tab-close-button:hover:active[selected="true"] {

  -moz-image-region: rect(0, 36px, 12px, 24px)!important;



  }
.tab-close-button{
margin-top: -2px !important;
margin-right: 3px !important;}

/*SCROLLBAR*/
#defaultFontSize
{
    width:45px !important;
}

scrollbar
{
    -moz-appearance: none !important;
    -moz-transition: opacity .2s !important;
    background: transparent !important;
    opacity: 1 !important;
    
}
    scrollbar[orient="vertical"]
    {
        width: 10px !important;
        background-color: #b9b8b8 !important;
    }
    
    scrollbar[orient="horizontal"]
    {
        height: 10px !important;
        background-color: #b9b8b8 !important;
    }

    scrollbar:hover
    {
        background-color: #b9b8b8 !important;
        opacity: 1 !important;
    }

    scrollbar:active
    {
        background-color: #b9b8b8 !important;
    }

scrollbarbutton[sbattr="scrollbar-up-top"],
scrollbarbutton[sbattr="scrollbar-down-top"],
scrollbarbutton[sbattr="scrollbar-up-bottom"],
scrollbarbutton[sbattr="scrollbar-down-bottom"]
{
    -moz-appearance: none !important;
    display: none !important;
}

scrollbar thumb
{
    -moz-appearance: none !important;
    -moz-transition: background-color .2s !important;
    background-color: #808080 !important;
    border: none !important;
}
    scrollbar thumb[orient="horizontal"]
    {
        min-width: 28px !important;
    }

    scrollbar thumb[orient="vertical"]
    {
        min-height: 28px !important;
    }
    
    scrollbar thumb:hover
    {
        background-color: #5d5d5d !important;
        
    }

    scrollbar thumb:active
    {
        background-color: #5d5d5d !important;}
        
/*FIND TOOLBAR*/
.browserContainer > findbar{
position: fixed;
top: 4.3%;
right: 78.8%;
width: 265px;
background: -moz-linear-gradient(#f7f7f7, #f0f0f0) !important;
border-radius: 3px !important;
box-shadow: 0px 0px 3px 0px #a7a7a7;
box-shadow: 0px 0px 3px 0px #a7a7a7;
}

#find-next> .toolbarbutton-text, .findbar-find-next.tabbable .toolbarbutton-text,
#find-previous > .toolbarbutton-text, .findbar-find-previous.tabbable .toolbarbutton-text,
#highlight > .toolbarbutton-text, .findbar-highlight.tabbable .toolbarbutton-text,
#find-invert > .toolbarbutton-text{
display: none !important;
}

#FindToolbar label[value="Find:"]{
display: none !important;
}

#FindToolbar checkbox{ 
display: none !important;
}

/*FIND TOOLBAR TEXTBOX STYLE*/
#FindToolbar .findbar-textbox{
height: 18px;
border-radius: 4px;
background: #fafafa !important;}

.findbar-textbox[status="notfound"]{color: red !important}

/*SOME UNNECESARY ELEMENTS REMOVED FROM THE FIND TOOLBAR*/
#FindToolbar label[class="checkbox-label"],
#FindToolbar label[value="Quick Find:"],
#FindToolbar label[value="Find:"],
#FindToolbar label[value="Clear"],
#FindToolbar .findbar-find-fast.findbar-find-status,
#FindToolbar .findbar-container>image{
   display: none !important; 
} 

#FindToolbar toolbarbutton{
   padding-left: 5px !important;
   padding-right: 0px !important;
   margin-left: -1px !important;
}

#FindToolbar label[value="Highlight all"]{
   opacity: 0;
   margin:-34px!important
}

#FindToolbar label[value="Next"]{
   opacity: 0;
   margin:-13px!important
}

#FindToolbar label[value="Previous"]{
   opacity: 0;
   margin:-23px!important
}

#FindToolbar label[value="(Case sensitive)"]{
   opacity: 0;
   margin-right:-76px!important
}

#FindToolbar .findbar-textbox{
   -moz-appearance: none !important; 
   border: 1px solid rgba(0,0,0,.3) !important;
   box-shadow: inset 0px 1px 0px 0px rgba(0,0,0,0.15) !important;
   margin-right: 9px !important;
}
/*  Images for the buttons  */

/*  Next button  */

#FindToolbar toolbarbutton image[label="Next"]{ 

   list-style-image: none !important; 

   background: url(data:image/png;base64,) center no-repeat !important; 

}

/*  Previous button  */

#FindToolbar toolbarbutton image[label="Previous"]{ 

   list-style-image: none !important; 

   background: url(data:image/png;base64,) center !important; 

}



/*  Highlight button  */

#FindToolbar toolbarbutton image[label="Highlight all"]{ 

   list-style-image: none !important; 

   background: url(data:image/png;base64,) center no-repeat !important; 

}

/*THROBBER*/
.tab-throbber[progress], .tab-throbber[busy]

{

	list-style-image: url(data:image/gif;base64,) !important;

}

/*CHANGE SELECTION COLOR TO GREY*/
::-moz-selection {
	background: #b9b8b8;
}

/*FIX NOTIFICATION POP-UP BOX*/
#notification-popup-box{
margin-left: -2px !important;
margin-right: 0px !important;
}
