css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("uchan.to") {


/* container */

  html {
    background:#222 !important;
    color:#ccb !important;  
    text-align:center !important;
  }

  body { 
    background:none !important;
    color:inherit !important;
    display:inline-block !important;
    line-height:20px !important;
    max-width:1000px !important;
    text-align:left !important;
  }


/* decor */

  a, .postername, .commentpostername, .filetitle {
    color:#0a0 !important;
  }

  .barsadmin[href] strong {
    text-decoration:underline !important;
  }

  .replytitle {
    color:#69d !important;
  }

  input, textarea, .postblock, .reply, .omittedposts {
    color:inherit !important;
  }

  input, textarea, .postblock, .reply, .post_button {
    background-color:#444 !important;
    border-color:#555 !important;
  }

  input {
    line-height:22px !important;
  }

  textarea {
    line-height:18px !important;
    padding:2px 3px !important;
  }

  a[href] + blockquote ol, a[href] + blockquote ul {
    display:table !important;
  }

  h1 {
    font-size:32px !important;
    line-height:36px !important;
  }

  hr, .thread, .omittedposts + table, table + table {
    clear:both !important;
  }

  hr {
    border:0 !important;
    background-color:#444 !important;
    color:#444 !important;
  }

  hr, table[border], table[border] td {
    border:0 !important;
  }

  .texts .Author {
    background:none !important;
    padding:0 !important;
  }


/* hide */

  #showme,
  #addthisBlock,
  #gbBlock,
  .addthisBlock,
  .gbBlock,
  .box-outer1 center:first-child a,
  #hidememenu br,
  span img[height="18"],
  hr + hr,
  .logo + hr,
  .postarea>a:first-child,
  .rules,
  a[href="/zaibalo/"] {
    display:none !important;
  }


/* logo */

  .logo {
    line-height:34px !important;
  }

  div.logo {
    margin:20px 0 -5px !important;
  }

  a.logo, p.logo {
    margin-top:4px !important;
  }


/* sagespoiler */

  font[color="red"] {
    color:#f62 !important;
  }

  .spoiler {
    background:#222 !important;
    color:transparent !important;
  }

  .spoiler:hover {
    color:inherit !important;
  }

  .highlight .spoiler {
    background:#333 !important;
  }


}
