css:
@-moz-document domain("checkvist.com") {
html, body {
    background-color: #131818 !important;
}

.main_div {
    background-color: #339999 !important;
    padding-bottom: 1px !important;
}


#toolbar {
    height: 27px !important;
    box-shadow: 0px 0px 0px 0 !important;
    -moz-box-shadow: 0 !important;
    -webkit-box-shadow: 0px 0px 0px 0 !important;
    background-color: #2F5BA0 !important;
    color: #7F9ECF !important;
    border-radius: 0px !important;
}

#toolbar a {
    float: left !important;
    padding: 0 12px !important;
    margin: 0 !important;
    height: 27px !important;
    line-height: 27px !important;
    border: 0 !important;
}

#h1Id {
    text-shadow: 0 0 0 black !important;
}

.updLine, .updLine a {
    color: #492299 !important;
}

#toolbar a {
    color: #7F9ECF !important;
}
#toolbar a.printer, #toolbar a.helpPopupIcon, #toolbar a.digest {
    color: #7F9ECF !important;
}
a, a:visited, a:active {
    color: #003366 !important;
}
span.task_closed {
    color: #F2AD36 !important;
    text-decoration: line-through !important;
}
.task_closed a, .task_invalidated a {
    color: #E83349 !important;
}
div.coreDiv {
    position: relative !important;
    padding: 2px 4px !important;
    margin: 3px 0px !important;
    border: 1px #307368 dotted !important;
    min-height: 1.37em !important;
}
.userContent a, .userContent a:visited {
    text-decoration: none !important;
}

.userContent a:hover {
    text-decoration: underline !important;
}

h1 {
    color: #222 !important;
}

.newbut {
    color: #017af0 !important;
    padding: 5px 12px !important;
    margin: 0 !important;
    font-family: Helvetica Neue, Helvetica, trebuchet, sans-serif !important;
    font-size: 13px !important;
    font-weight: 500 !important;
    text-shadow: 0 0 0 black !important;
    -webkit-transition: border-color .218s !important;
    -moz-transition: border .218s !important;
    -o-transition: border-color .218s !important;
    transition: border-color .218s !important;
    background: #2F5BA0 !important;
    color: #628CCF !important;
    border: none !important;
    border-radius: 0 !important;
}

#findInput {
    width: 300px !important;
    border: none !important;
    padding: 5px 18px 5px 5px !important;
    margin: 0 3px !important;
    font-size: 13px !important;
    background: #006666 !important;
}

div.coreDiv.selectedTask {
    color: white !important;
    background-color: #336fcb !important;
    box-shadow: 4px 4px 3px #222 !important;
    border: 1px #336fcb dotted !important;
}

.tag {
    padding: 1px !important;
    line-height: 1 !important;
    border-radius: 0 !important;
    border: none !important;
    font-weight: normal !important;
    font-family: Helvetica Neue, Helvetica, trebuchet, sans-serif !important;
    background-color: inherit !important;
    color: inherit !important;
}

.tagClass_0, a.tagClass_0 {
    box-shadow: none !important;
    color: #DAB77A !important;
    background-color: #003366 !important;
    border-color: #003366 !important;
}

div.sidenotes {
    display: none !important;
}

.profilePopupLink {
    background-color:  #339999 !important;
    background-size: 0 !important;
}

#ffContainer {
    border: none !important;
    display: inline-block !important;
    background: #006666 !important;
    margin-right: 0.5em !important;
    border-radius: 0 !important;
}

.topbar td.leftLogo {
    width: 5% !important;
    padding-right: 30px !important;
    vertical-align: middle !important;
    display: none !important;
}

.dueDate.overdue{
    background-color: #164242;
    color: #ccc !important;
}

.dueDate {
    display: inline-block !important;
    padding-right: .3em !important;
    border: none !important;
    border-radius: 0 !important;
    cursor: pointer !important;
    white-space: nowrap !important;
    color: #993299 !important;
    font-weight: 600 !important;
}

.logo {
    margin: 40px auto 50px !important;
    height: 90px !important;
    width: 485px !important;
    background: none !important;
}

.switchForms h2.activeForm, .highlightedUnder {
    padding-bottom: 15px !important;
    background: none !important;
    font-style: inherit !important;
    color: darkred !important;
}


}
