css:
@-moz-document url-prefix("http://www.tumblr.com"), url-prefix('https://www.tumblr.com')   {
body { background: url('http://24.media.tumblr.com/aa748c94012384995d6167aafece8f55/tumblr_mqvoraOCjW1qa90vno4_r2_1280.png') center right #000000 no-repeat fixed !important;}

#logo, #logo a {
  position: relative;
  z-index: 1;
  width: 315px !important;
  height: 149px !important;
  background-repeat: no-repeat;
  background: url("http://media.tumblr.com/8e5fded615cdd26ea71d40c208dccbd9/tumblr_inline_mqvqf53Kfj1qz4rgp.png")!important;
}

#popover_blogs .tab_notice:after{display:none}

div.l-header .tab.iconic > a {background-image:url('http://media.tumblr.com/ce77dcc91fecce8bf5e448dc956564ba/tumblr_inline_mqverl6lEW1qz4rgp.png') !important;
  border-radius: 3px !important;}


.new_post_label .new_post_label_icon {
background-image: url('http://media.tumblr.com/7dbad47818094ba23980f91e431d605b/tumblr_inline_mqvt24CsrU1qz4rgp.png') !important;
background-size: 520px 100px !important;
margin-top: 0px !important;
margin-right: 15px !important;
opacity: 1 !important;
}

  
div.l-content #new_post .new_post_label {
color: rgba( 255, 255, 255, .9) !important;
line-height: 1.4em;
font-weight: bold !important;
font-size: .9em !important;

}

#posts .post .post_content {
  background-color: rgba(253, 182, 200,0) !important;
}


#posts .post {
  background: rgba(255, 255, 255, 0.8) !important;
  }


ol#posts li blockquote,
body.mceContentBody blockquote {
margin-left: 0 !important;
margin-right: 0 !important;
padding-left: 10px !important;
border-width: 4px !important;
border-color: #87a2a2 !important;
}



ol#posts li.notification.single_notification {
border-bottom: 1px solid #000000;
}

#return_to_top .return_to_top_icon{
background: url('http://media.tumblr.com/8e21398507add6f93bd272e106660608/tumblr_inline_mqvgmyX8Yl1qz4rgp.png') no-repeat!important}

#dashboard_index #left_column, #right_column {background-color: transparent !important;}

div.l-content.l-content.l-content {
background: transparent !important;
}

#left_column, #right_column {background-color: transparent !important;}

div.l-content.l-content.l-content {background: transparent !important;
-webkit-border-radius: 20px;
-moz-border-radius: 20px;
border-radius: 20px;
margin: auto;
padding: 40px auto;
}


.popover_menu_item {
  background: rgba(130, 200, 200,0.75) !important;
  border-bottom: none solid #9D9EA8 !important;
  
}


.tracked_tags {
background-color: rgba(255, 255, 255, 0.5)  !important;
border-radius: 0px !important;
}

.selection_nipple:not([class="selection_nipple white"]) {

  border-bottom: solid 11px rgba(212, 0, 141,1)  !important;

}

div#right_column:after {

  background: none !important;

}

h1 {
color: #000000;
text-shadow: 0 0 3px #ffffff;
}


.no_posts_found, h1.dashboard_header {
color: #666666;
}

img#content_top, img#content_bottom {
display: none;
}

#tumblr_radar {display: none !important;}


#sidebar_footer_nav {
 display: none !important;
}


#right_column a#dashboard_switch_blog_menu_current #dashboard_switch_blog_arrow_current {
background: none;
}

#right_column .dashboard_nav_item {
background: none repeat-x;
background-attachment: initial;
background-position-x: 30%;
background-position-y: 100%;
background-origin: initial;
background-clip: initial;
background-color: #82dfd1;
}

#right_column .dashboard_nav_item #hide_radar {
background-image: none;
background-repeat-x: no-repeat;
background-repeat-y: repeat;
background-attachment: initial;
background-position-x: 0%;
background-position-y: 0%;
background-origin: initial;
background-clip: initial;
background-color: #82dfd1;
}

#right_column .controls_section li a,

#right_column .controls_section li a .count {

  color: #d4008d !important;
  background-color: rgba(43,43,43,0)!important;
  border-radius: 10px !important; 
}



#right_column .controls_section li a:hover {

  color: #d4008d !important;
  background-color: rgba(82, 200, 225,.8)!important;
  border-radius: 3px !important;
}


#right_column .dashboard_nav_item .dashboard_controls_radar_media.photo, #right_column .dashboard_nav_item .dashboard_controls_radar_media.photoset, #right_column .dashboard_nav_item .dashboard_controls_radar_media.video {

background: rgba(0,0,0,0.5);
}

#nav .nav_item.active {
background: rgba(218,118,118,0.7) !important;
  }

#nav .nav_item .nav_item_nipple .nav_item_nipple_pixel {
    background: rgba(218,118,118,0.7) !important;
}

ol#posts li.notification.alt {
 background-color: rgba(0,15,35,0.5);
}


ol#posts li.notification.first_notification {
    background-color: rgba(0,15,35,0.5);
}


ol#posts li.notification {
    background-color: rgba(0,15,35,0.5);
    border-bottom: 1px solid #434c5f;
}

ol#posts li.notification.last_notification {
    border-bottom: 1px solid #434c5f;
}

ol#posts li.notification a {
color: #434c5f;
border-bottom: 1px solid #434c5f;
}


ol#posts li.post .permalink {
    background: rgba(233,149,149,1);
}

#right_column .dashboard_nav_item #dashboard_controls_suggested_blogs {
background: rgba(0,0,0,0.8);
border: solid #000000;
}


ol#posts li.post .post_avatar{
border-bottom: 0px solid #0A2939;
}


#right_column .dashboard_nav_item #dashboard_controls_suggested_blogs .dashboard_controls_suggested_blog {
border-top: 1px solid #000000;
border-bottom: 1px solid #000000;
}


#right_column a#dashboard_switch_blog_menu_current:hover {
background: none;
}

#right_column a#dashboard_switch_blog_menu_current:hover #dashboard_switch_blog_arrow_current {
background: none;
}

form.dashboard_options_form {

background-color: rgba(0,0,0,0.7);
border-bottom: 1px solid #000000;
border-bottom-left-radius: 10px 10px;
border-bottom-left-radius: 10px 10px;
border-bottom-right-radius: 10px 10px;
border-bottom-right-radius: 10px 10px;
border-top-left-radius: 10px 10px;
border-top-left-radius: 10px 10px;
border-top-right-radius: 10px 10px;
border-top-right-radius: 10px 10px;
color: #cccccc;
display: block;
font-size: 13px;
margin-bottom: 25px;
padding: 15px 22px;
}



form.dashboard_options_form .option_container {
border-top: 1px solid #000000;
}

.no_posts_found, h1.dashboard_header {
color: #333333;
}


.answer_container {

background-color: #000000;
border-bottom: 0px solid #24394E;
}




#nav .nav_item .nav_menu {
background-color: #000000;
border-bottom: 2px solid #333333;
}

#nav .nav_item .nav_menu a {
border-top: 1px solid #333333;

}


#auto_pagination_loader {
background: rgba(0,0,0,0.05) none repeat-x 30% 0%;
border-top: 0px solid transparent;
}



#right_column .dashboard_nav_item a:hover {
color: #cccccc;
}

#right_column .dashboard_nav_item a {
color: #000000;
}


#right_column .dashboard_nav_item ul.dashboard_subpages li a:hover {
color: #cccccc;
}

#right_column .dashboard_nav_item ul.dashboard_subpages li a {
color: #000000;
}


#right_column .dashboard_nav_item .dashboard_nav_title {
color: #000000;
}

#right_column {
color: #000000;
}



#right_column a#dashboard_switch_blog_menu_current {
color: #f2f2f2;
}

#nav .nav_item {
color: #000000;
}

#nav .nav_item:hover {
color: #333333;
}

.new_post_notice {
background-color: #333333;
}



.new_post_notice img {
opacity: 0;
}


#search_box input {
border: 1px solid #cccccc;
}

#footer {
color: #000000;
}

#footer a {
color: #000000;
}


.new_post_label {
font-size:2px !important;
padding-top:10px !important;
font-family:century gothic!important; 
color:transparent!important;
letter-spacing:1px !important
}



#right_column .dashboard_nav_item .dashboard_controls_radar_media {
background: rgba(255,255,255,0.8) repeat-x 30% 0%;
color: #000000;
font: normal normal normal 12px/normal 'Lucida Grande', Verdana, sans-serif;
line-height: 16px;
margin: 10px auto 0px;
overflow: hidden;
padding: 4px 6px;
position: relative;
width: 190px;
word-wrap: break-word;
}

#dashboard_controls_radar_media_fold {
opacity: 0;
}


#edit_post_controls #slug_url {
color: #000000;
}


h2 .optional {
color: #000000;
}

h2 {
color: #000000;
}


#promos .promo .tidbit {
color: #000000;
}


#edit_post_controls .advanced_option label {
color: #000000;
}

}

@-moz-document url-prefix("http://www.tumblr.com/dashboard/iframe"), url-prefix('https://www.tumblr.com/dashboard/iframe')   {
html > body {background:none!important;}
}
