css:
@-moz-document url("http://www.tumblr.com/"), url("http://www.tumblr.com/dashboard"), url("http://www.tumblr.com/likes"), url("http://www.tumblr.com/following"), url("http://www.tumblr.com/inbox"), url("http://www.tumblr.com/spotlight/culture"), url("https://www.tumblr.com/register"), url("https://www.tumblr.com/login"), url("http://www.tumblr.com/explore"), url("https://www.tumblr.com/lookup"), url("https://www.tumblr.com/preferences"), url("http://www.tumblr.com/help"), url-prefix("http://www.tumblr.com/blog/"), url-prefix("http://www.tumblr.com/tagged/"), url-prefix("http://www.tumblr.com/following") , url("https://www.tumblr.com/"), url("https://www.tumblr.com/dashboard"), url("https://www.tumblr.com/likes"), url("https://www.tumblr.com/following"), url("https://www.tumblr.com/inbox"), url("https://www.tumblr.com/spotlight/culture"), url("https://www.tumblr.com/explore"), url("https://www.tumblr.com/help"), url-prefix("https://www.tumblr.com/blog/"), url-prefix("https://www.tumblr.com/tagged/"), url-prefix("https://www.tumblr.com/following") {
body {
   background:url("http://25.media.tumblr.com/51a37d4bd01d36f1670dab07d98f7653/tumblr_mt4jyr9jZw1r0iaydo4_r1_1280.png")no-repeat fixed  !important;
   background-color: #f81583 !important;
   background-position: 0% 60% !important;
   background-size: 100% 100%  !important;
 }

div.l-header .tab.iconic a{
  background-color: rgba(241, 205, 12, 1) !important;
  border-radius: 60px !important;  
}

#left_column, #right_column {background-color: transparent !important;}

div.l-content.l-content.l-content {  
   font-family: Arial; color: #f81583;
   background: rgba(0,0,0, 0.65) !important;

 }


.notification  {
  	background: #000000 !important; border: none !important; 
	color: #f81583 !important; font-size: 11px !important;
  }

.notification :link, .notification :active, .notification :visited {
  	color: #f81583 !important;
    text-decoration: none !important;
    border: none !important;
  }

.new_post_label .new_post_label_icon {
background-image: url('http://25.media.tumblr.com/bdbb195c1ecf5ee68f409d507cacf81d/tumblr_mt4jyr9jZw1r0iaydo6_r1_1280.png') !important;
background-size: 520px 100px !important;
margin-top: 0px !important;
margin-right: 15px !important;
opacity: 1 !important;
}

div.l-content #new_post .new_post_label {
color: rgba(255,255,255,.9) !important;
line-height: 1.4em;
font-weight: bold !important;
font-size: .9em !important;

}

#right_column .controls_section li a,

#right_column .controls_section li a .count {

  color: #ffffff !important; 
}

#logo, #logo a {
  position: relative;
  z-index: 1;
  width: 250px !important;
  height: 63px !important;
  background-repeat: no-repeat;
  background: url("http://25.media.tumblr.com/1dba478ec7b8f021d411f8befc215c2f/tumblr_mt4jyr9jZw1r0iaydo8_r1_250.png")!important;
}

.controls_section li .posts:before {

  position: absolute !important;
  height: 30px !important;
  line-height: 28px !important;
  width: 100px !important;
  font: inherit !important;
}

a.posts  div.hide_overflow{
  visibility: hidden !important;
}

.controls_section li .posts:before {
  content: "Fairytales" !important;
}.controls_section li .followers:before {

  position: absolute !important;
  height: 30px !important;
  line-height: 28px !important;
  width: 100px !important;
  font: inherit !important;
}

a.followers  div.hide_overflow{
  visibility: hidden !important;
}

.controls_section li .followers:before {
  content: "Lost Humans" !important;
}.controls_section li .drafts:before {

  position: absolute !important;
  height: 30px !important;
  line-height: 28px !important;
  width: 100px !important;
  font: inherit !important;
}

a.drafts  div.hide_overflow{
  visibility: hidden !important;
}

.controls_section li .drafts:before {
  content: "Drafts" !important;
}.controls_section li .queue:before {

  position: absolute !important;
  height: 30px !important;
  line-height: 28px !important;
  width: 100px !important;
  font: inherit !important;
}

a.queue  div.hide_overflow{
  visibility: hidden !important;
}

.controls_section li .queue:before {
  content: "Queue" !important;
}

.selection_nipple.white, .selection_nipple:not([class="selection_nipple white"]) {

  background: none !important;

  width: 0px !important;

  height: 0px !important;

  margin-top: 0px !important;

  border-bottom: solid 11px rgba(0, 0, 0, 0.65) !important;

  border-right: 11px solid transparent !important;

  border-left: 11px solid transparent !important;

}

.return_to_top_icon{
background: url('http://25.media.tumblr.com/a805934dcb3de77e986f213c19263a19/tumblr_mt4jyr9jZw1r0iaydo7_r1_250.png') no-repeat  
transparent!important;
width:140px!important;
height:180px!important}
}
