css:
@-moz-document domain("rotogrinders.com") {
header#layout-header { 
  float: left; 
  width: 240px; 
  background: #333 !important;
  height: 106px !important;
  padding: 0px 5px !important;
  border-bottom: 0px;
  margin-top: -4px;
}

#main-banner,
#mainnav-home,
ul.logos_reviews,
#forum-filter,
#discussion-listing header
{ 
  display: none !important; 
}

section>header {
  background: none !important;
  border: none !important;
  height: auto !important;
  box-shadow: none !important;
  height: 30px !important;
}

#new-topic {
  position: absolute !important;
  top: -50px !important;
  right: 0px !important;
}

#mainnav { margin: 0px 0 0 250px; }

#mainnav>div.top-level-nav { 
  border-radius: 0px; 
  height: 50px;
}
#mainnav>div.drawer { 
  min-height: 0 !important;
}
#mainnav>div.drawer.open { 
  min-height: 50px !important;
}

#mainnav div ul>li { 
  padding: 10px 2% !important;
  height: 30px;
}
#mainnav-dailyresearch {
  background: none !important;
}

#wrapper {
  width: auto;
}

#main-content {
  width: auto;
  margin-right: 280px;
}

#discussion-listing thead { display: none; }

#discussion-listing td {
  vertical-align: top !important; 
}  

#discussion-listing .reply-count {
  width: 230px !important; 
  border: 1px blue;
  padding: 0px 3px 0px 0px !important;
  text-align: left !important;
}
#discussion-listing .reply-count div {
  display: inline !important;
  margin-left: 5px;
}
#discussion-listing .reply-count div.replies-counter {
  display:block !important;
  text-align: right !important;
}
#discussion-listing .author-name { 
  width: 1px !important; 
}
c#discussion-listing .author-name:before { content: "by"; }
#discussion-listing .author-name a {
  position: absolute !important;
  margin-left: 5px;
}
  
aside {
  position: absolute !important;
  right: 0px;
  top: 150px;
  width: 270px !important;
}

@media (min-resolution:200dpi) {
  aside {
    position: static !important;
    right: auto;
    top: auto;
    width: auto !important;
  }

  #main-content {
    margin-right: 0;
  }
	
  #mainnav:hover {
    width: 100%; height: 100px;
  	padding-top:105px;
    margin-left: 0px;
  }
  #mainnav div {
  	display: none;
  }

  #mainnav:before {
  	position: absolute;
  	top: 0px;
  	right: 0px;
    color: #ddd;
    display: block;
    content: "\2630";
    font-size:80px;
    width: 1em;
    padding: 10px;
    height: 80px;
  	line-height: 80px;
  }

  #mainnav:hover div
  {
    display: block;
  }
}
}
