css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("tizen-centrum.pl") {

body{
background: url("http://subtlepatterns.com/patterns/bo_play_pattern.png") repeat scroll 0% 0% rgb(138, 207, 255) !important;
margin: 100px !important;
margin-top: 0px !important;
margin-bottom: 0px !important;
font-family: Verdana !important;
}

#container {
    width: 100% !important;
    background: rgb(22, 22, 22) !important;
    color: rgb(255, 255, 255) !important;
}

.unheader {
    background: rgb(50, 50, 50) !important;
}
.logo {
background: url("http://tizen-centrum.pl/images/logo.png") !important;
background-repeat:no-repeat !important;
}
.logo img {
opacity:0 !important;
}
.border{
background: none repeat scroll 0% 0% rgb(22, 22, 22) !important;
border-radius: 0px 0px 0px 0px !important;
border-color: rgb(220, 220, 220) !important;
}
.thead {
    background: none rgb(22, 22, 22) !important;
    border-radius: 0px 0px 0px 0px !important;
    border: none !important;
}
.expander{
visibility: hidden !important;
}
.pasek {  
background: rgb(22, 22, 22) !important;
}
.tfoot {
background: rgb(22, 22, 22) !important;
}
#content {
    background: none repeat scroll 0% 0% rgb(50, 50, 50) !important; 
}
#header {
    background: rgb(50, 50, 50) !important;
}
.navigation {
    background: none !important;
    border: none !important;
}
.bottommenu {
    background: none !important;
     border: none !important;
}
a:hover, a:active {
    color: rgb(220, 220, 220) !important;
    text-decoration: underline;
}
a:link {
    color: rgb(220, 220, 220) !important;
    text-decoration: none;
}
a:visited {
    color: rgb(220, 220, 220) !important;
	text-decoration: none;
	}
a {
color: rgb(220, 220, 220) !important;
cursor: pointer;
text-decoration: none;
}
.menu ul a:link {
    color: rgb(220, 220, 220) !important;
	text-decoration: none;
}

.menu ul a:visited {
    color: rgb(220, 220, 220) !important;
	text-decoration: none;
}

.menu ul a:hover, .menu ul a:active {
    color: rgb(220, 220, 220) !important;
	text-decoration: none;
}
.gora {
    background-color: rgb(22, 22, 22) !important;
    }
#paneluser2 {
   background-color: rgb(22, 22, 22) !important;
   color: rgb(150, 150, 150) !important;
}
.tekst {
background-color: rgb(22, 22, 22) !important;
}
.trow1 {
    background: rgb(90, 90, 90) !important;
}
.trow2 {
    background: rgb(70, 70, 70) !important;
}
.tcat {
    background: rgb(10, 10, 10) !important;
    color: rgb(220, 220, 220) !important;
    }
.post_body, .triangle-border {
 background: rgb(30, 30, 30) !important;
    color: rgb(220, 220, 220) !important;
}
.post_body a {
color: rgb(120, 150, 255) !important;
}
#facebook {
top: 150px !important;
}
#shoutbox_e .trow1{
 background-color: rgb(220, 220, 220) !important;
}
#shoutbox_e a{
color: rgb(50, 100, 250) !important;
}
.stat {
    background: rgb(30, 30, 30) !important;
    color: rgb(220, 220, 220) !important;
    border: none !important;
    }
.trow_sep {
    background: none !important;
    color: rgb(220, 220, 220) !important;
    }
.post_author_info {
 color: rgb(220, 220, 220) !important;
}
.pagination a, .pagination_current {
    background: none !important;
    color: rgb(220, 220, 220) !important;
    }
.tbut {
    background: none !important;
    border-color: rgb(220, 220, 220) !important;
    }
.szukaj {
background: none rgb(200, 200, 200) !important;
}
.modnotice {
background: none rgb(200, 200, 200) !important;
}
#shoutbox_data
{
height:400px !important;
}
