height: 36px;
background-color: #114081;
position: fixed;
width: 100%;
z-index: 10001;
top: 0;
}

#navigation-container a.btn-logo, #navigation-menu a.btn-logo:visited, #navigation-container a.btn-logo:hover {
background: url(http://i.imgur.com/61U83wD.png) no-repeat top;
display: inline-block;
width: 116px;
height: 35px;
float: left;
margin-right: 4px;
}

.subMenu {
background: #BE1E79;
border: none;
color: white;
height: 25px;
margin: 0 auto;
position: relative;
text-align: center;
width: 970px;
z-index: 200;
}
div.mySubmenuFixed {
top: 36px;
background-color: #BE1E79;
height: 25px;
}

th {
background-color: #EF4E91;
}

#Footer.LanguageRedesign .legal {
border-top: 1px solid #EF4E91;
padding-top: 20px;
}

.normalTextSmall a:visited, .normalTextSmallBold a:visited, .normalTextSmaller a:visited {
color: #EF4E91;
}

select {
font-weight: bold;
background-color: #EF4E91;
border-color: black;
}
.ad-annotations {
font-size: 0px;
}
a.ad img{
opacity:0;
}
.Legalese{
color: #BE1E79 !important;
}
#TrusteLogo{
height: 0px;
width: 0px;
}
.title{
text-align: center;
 }
.title>span{
color: #BE1E79; 
}
.verticaltab{
background-color: rgba(248, 185, 206, .5);
}
.verticaltab.selected{
background-color:rgba(239, 78, 145, .5)!important;
border-color:#BE1E79;
border-left: 0px solid #BE1E79;
}
.divider-right{
border-color:#BE1E79!important;
border-left: 1px solid #BE1E79;
border-top: 1px solid #BE1E79;
border-bottom: 1px solid #BE1E79;
}
.stat-label{
color:#EF4E91!important;
}
.Detail .notranslate{
color:#BE1E79!important;
}
.Label{
color:#EF4E91!important; 
}
.Detail a{
color:#BE1E79!important;  
}
.AssetThumbnail{
border: solid 1px #BE1E79!important;    
}
}

@-moz-document url-prefix("http://www.roblox.com/Forum/Default"), url-prefix("http://www.roblox.com/Forum/ShowForumGroup") {
.table-header th {
border:none;
border-right: 1px solid #BE1E79;
}
.table-header .first {
border:none;
border-top: 1px solid #BE1E79; 
border-left: 1px solid #BE1E79;
border-right: 1px solid #BE1E79;
}
.table td, .table th {
border-top: 1px solid #BE1E79; 
}
.forum-table-row td{
border-right: 1px solid #BE1E79!important;
}
.forum-table-row td:first-child{
border-left: 1px solid #BE1E79!important;
border-right: 1px solid #BE1E79!important;
}
.forum-table-row:last-child td{
border-bottom: 1px solid #BE1E79!important;     
}
}

@-moz-document url-prefix("http://www.roblox.com/Forum/ShowForum.aspx"), url-prefix("http://www.roblox.com/Forum/User/MyForums.aspx") {
.forum-table-row td{
border:none;
border-right: 1px solid #BE1E79!important;
border-bottom: 1px solid #BE1E79!important;
}

.forum-table-row:nth-child(2) td{
border-top: 1px solid #BE1E79!important;
}

.forum-table-row td:first-child{
border-left: 1px solid #BE1E79!important;
}
  
.forum-table-row td:nth-child(2){
border-right: none!important;
}        
}
