css:
@-moz-document url-prefix("http://www.tumblr.com/"), url-prefix("https://www.tumblr.com/") {

a.posts     div.hide_overflow:before,
a.followers div.hide_overflow:before,
a.queue     div.hide_overflow:before,
a.drafts    div.hide_overflow:before,
a.activity  div.hide_overflow:before,
a.likes     div.hide_overflow:before,
a.customize div.hide_overflow:before,
a.following div.hide_overflow:before,
a.spotlight div.hide_overflow:before
{
  position: absolute;
  /*display: inline-block;*/
}

a.posts div.hide_overflow,
a.followers div.hide_overflow,
a.queue div.hide_overflow,
a.drafts div.hide_overflow,
a.activity div.hide_overflow,
a.likes div.hide_overflow,
a.customize div.hide_overflow,
a.following div.hide_overflow,
a.spotlight div.hide_overflow
{
  width: 0;
}

a.posts      div.hide_overflow:before { content: "Fluffy shit" }
a.followers  div.hide_overflow:before { content: "Alpacas" }
a.queue      div.hide_overflow:before { content: "Floating fluffs" }
a.drafts     div.hide_overflow:before { content: "Dandelions" }
a.activity   div.hide_overflow:before { content: "Heartbeats" }
a.likes      div.hide_overflow:before { content: "Fuwa fuwa" }
a.customize  div.hide_overflow:before { content: "My world" }
a.following  div.hide_overflow:before { content: "Bunnies" }
a.spotlight  div.hide_overflow:before { content: "Komorebi" }

}
