css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain(rawstory.com)
{
a:link {color:#FFB57C !important;}
a:visited {color:#FFB57C !important;}
a:hover {color:#674324 !important;}
a:active {color:#0000FF !important;}

/*Background*/

html,body
{
background: url(http://i.imgur.com/Lojl6LH.jpg) !important;
background-repeat: no-repeat !important;
background-attachment: fixed !important;
}

/*Background Transparency Fix*/

thead, .banner ,th, tr, td, blockquote, li, ul, h1, h2, h3, h4, h5, h6, p, form, 
footer, #mid-slider, div#mainmenu, div.wrappershadow, 
#secondarycontent .colleft_mid
{ 
background: transparent !important;
} 

/*Transparency*/

#header,div.wrapper,.wrapper,#mid-slider,#container,a,li,.mainnav#menu-mainmenu,ul
{
background: transparent !important;
background-color: transparent !important;
}

select, button, div.dsq-comment-header

{
background: black !important;
}

div, span, a

{
background-color: black !important;
}

select, input, span, code, textarea, pre, label, div, dd

{
color: white !important;
}

/*Img Opacity*/
img
{
opacity: 1.00 !important;
}

/*Logo Height*/
.logo
{
background-color:transparent !important;
}

/*Opacity*/

#footer,#main,div#main,#sidebar,#mid-slider,#primarycontent,#secondarycontent,
#subnews,#mainmenu
{
opacity: 0.85 !important;
}

/*Sidebar 1st Portion*/
div.widgetinner,.widgettitle,#raw-exclusives,
#mid-slider-sidebar
{
margin-left: 5.00px !important;
margin-right: 10.00px !important;
}

/*Sidebar 2nd Portion*/
.featuredpost.lastpost
{
margin-left: 20.00px !important;
margin-right: 10.00px !important;
}

/*Centered Sidebar Text*/
span,.catame
{
text-align: center !important;
}

/*Main Menu*/
div#mainmenu,#menu-main-menu
{
  border: 0px !important;
  width: 870px !important;
  background: transparent !important;
  background-color: transparent !important;
}

.wrapper,#mainmenu
{
  background: transparent !important;
}

.clear
{
height: 0px !important;
}

.fea-slides-wrapper,#mid-slider-pagination,#mid-slider-nav,ul,#mid-slider,li
{
border: 0px !important;
border-top-left-radius: 0px !important;
border-top: 0px !important;
border-top-right-radius: 0px !important;
border-bottom: 5px !important;
}

.wrapper
{
border: 0px !important;
}


/*Hidden*/
img.ulpnxuffsyakruudwhmt,#sharebar,#nmWidgetContainer,div.textwidget,div.banner
{
display:none !important;
}

}
