css:
@-moz-document url-prefix("http://followshows.com/") {

body, #content, #watch-headline-container, #watch-video-container, #watch-main-container, .footerr, #mymedia #view-pane .settings, #mymedia #view-pane .actions, #mymedia #table .video.even, #mymedia #table .video-details.even, #mymedia #table .video.odd, #mymedia #table .video-details.odd, #mymedia #view-pane .footer, #mymedia #table, #no-playlists, .searchFooterBox .yt-uix-pager, input:not([class*="html5"]), .feedmodule-anchor, textarea, #record-tab-content, .vert-bar, .message td, .msg-subject-open .subject, .shows-table, .shows-table .alt td, #google_companion_ad_div, #watch-show-brand-div, #watch-channel-brand-div, #user_playlist_navigator, #main-channel-content, .watch-next-list-actions, .ux-thumb-96, .multiple-promoted, #quicklist-bar-container, #vm-pageheader-container h1, #vm-page-subheader, #vm-video-actions-inner, #yt-admin h1, #folder_title, #theForm *, #footer-container, #vm-playlist-header, #page.watch-branded #watch-sidebar, #yt-admin-content h2, #mymedia #table, #yt-admin #table, #page, #watch-description .expand, #watch-description .collapse, #yt-admin.recommendations, #watch-owner-container, #annotator-div, #annotator-select-menu .yt-uix-button-menu-item td
{
    background: black !important;
}

/*Follow Shows Title Fix*/
span.follow
{
color: #1D9ADE !important;
}

/*Colors White*/
.module-head.clearfix,h3,p,hr,div
{
color: #FFFFFF !important;
}

/*Centered*/
.module-head.clearfix
{
text-align: center !important;
}

div.module-head.clearfix
{
left: 0.00px !important;
}

#column_right
{

}

.btn-calendar,.btn,.btn-group
{
color: white !important;
background: black !important;
border-color: black !important;
}

html,body
{
background: url(http://i.imgur.com/Q4Z03nk.gif) !important;
background-repeat: no-repeat !important;
background-attachment: fixed !important;
}

/*Black Background*/
.navbar,#actions,.module-contents,.video-infos,#videos,.module-head,#stats,
div#tracker-wrapper,div.tracker-header,#holder-user.old.clearfix,
#holder-user.settings.clearfix,ul#queue,#holder-user.profile-page.clearfix,
#stats,.subnavbar,div#user-content-header.clearfix,
div.home-section-title.home-watchlist-section-title
{ 
background-color: #000000 !important; 
background: #000000 !important; 
}
.episodes
{ 
background: #000000 !important;
}

/*User Content Header*/
div#user-content-header.clearfix,#user-content-header,h3
{
color: #FFFFFF !important;
height: 38px !important;
opacity: 0.85 !important;
border-bottom: 0px !important;
border: none !important;
}

/*Progress*/
.progress
{
background: #000000 !important
}

td.episode,td,tr,span
{
color: white !important;
}

/*My Profile*/
#profile-header,#holder-user,#stats,.module-head
{
background: 000000 !important; 
}

/*Settings Background*/
#user-content-column,.form-actions
{
background:black !important;
}

/*Border Aqua 1px*/
input#email,select#timezone,select#notificationHour,
select#notificationMinute,input#mytvrss-id,
input#search.ui-autocomplete-input,div.thumbnail,.videos-grid-more,.videos-grid-less
{
border-color: #1D9ADE !important;
}

/*Season Selected*/
a.season.selected
{
background: #1D9ADE !important;
background-color: #1D9ADE !important;
}

/*Table UpToDate Background*/
tr.uptodate,.uptodate
{
background: #000000 !important;
background-color: #000000 !important;
}

/*Table Striped*/
table#tracker.table.table-striped,tr,td
{
background: #146591 !important;
background-color: #146591 !important;
}

/*Table UpToDate Text*/
tr.uptodate,.uptodate
{
color: #000000 !important;
}

/*Transparency*/
#content.clearfix,#holder-user.new.clearfix,#videos
{
background: transparent !important;
}

/*SubNavBar*/
.subnavbar,li.selected,.container,a
{
color: white !important;
text-shadow: 0px 0px 0px !important;
}

.calendar,#column_right,#watchlist,ul.show-grid.clearfix,.home-section-title,
.shows-grid.clearfix,.subnavbar,#content-about,#content-modules,
#content-comments,select#timezone,select#notificationHour,
select#notificationMinute,#holder-user,ul.shows-grid.clearfix,
.home-section-title,div#watchlist
{
background: black !important;
}

/*Home Section -Calendar-*/
.home-section-title.home-calendar-section-title
{
border-bottom: 0px solid !important;
border-color: #1D9ADE !important;
opacity: 0.85 !important;
width: 715px !important;
}

.btn
{
border-color: #1D9ADE !important;
}

/*opacity*/
#watchlist,.calendar,#column_right,ul.show-grid.clearfix,
.shows-grid.clearfix,#content-about,#content-modules,
#content-comments,#holder-user,#user-content-column
{
opacity: 0.85 !important;
background:transparent !important;
}


/*opacity None*/
#user-content-column.tracker,#holder-user.new.clearfix
{
opacity: 100.00 !important;
}

.video
{
background: black !important;
background-color: black !important;
}

/*Queue*/
#queue
{
background: 000000 !important; 
}

/*Dropdown*/
.dropdown-menu,.btn-group,.popover,ul.dropdown-menu
{
color: rgb(64, 64, 64);
background: 000000 !important; 
background-color: black !important;
border: 1px solid !important;
border-color: #1D9ADE !important
}

/*Borders 2px*/
.videos-grid-more,.videos-grid-less
{
border: 1px solid !important;
}

/*Borders*/
#holder-user.new,#column_right,.module,.module-contents
{
border: none !important;

}

.popover-title,.video-title,.video-date,.video-label
{ 
background: transparent !important;
background-color: black !important;
opacity: 1.55 !important;
}

.popover
{
border: 4px solid !important;
border-color: #1D9ADE !important;
}
}
