css:
@-moz-document domain("hacks.mozilla.org") {
@media print {
    .masthead
    ,#newsletterForm
    ,section.discussion
    ,section.promo
    ,footer
    { display: none !important; }

    body { margin: 1rem; }
    
    #content, #content-main, article.post {
        min-width: auto !important;
        max-width: 100% !important;
        width: auto !important;
        margin: 0 !important;
        padding: 0 !important;
        float: none;
    }
}

pre {
    white-space: pre-wrap !important;
}
}
