/***** NEW SCROLLBARS *****/



div ::-webkit-scrollbar {

  width: 12px}

div ::-webkit-scrollbar-track {

  box-shadow: inset 0 0 6px rgba(0,0,0,0.3);

  border-radius: 10px}

div ::-webkit-scrollbar-thumb {

  border-radius: 10px;

  box-shadow: inset 0 0 6px rgba(0,0,0,0.5)}

div ::-webkit-scrollbar-thumb:hover {

  box-shadow: inset 0 0 6px rgba(0,0,0,1)}



div scrollbarbutton {

  -moz-appearance: none !important;

  background-color: transparent !important;

  border: none !important}

div thumb {

  -moz-appearance: none !important;

  background-color: transparent !important;

  border: none !important;

  border-radius: 10px;

  box-shadow: inset 0 0 6px rgba(0,0,0,0.5)}

div scrollbar {

  -moz-appearance: none !important;

  background: transparent !important;

  box-shadow: inset 0 0 6px rgba(0,0,0,0.3);

  border-radius: 10px}

div thumb:hover {

  box-shadow: inset 0 0 6px rgba(0,0,0,1)}



div scrollbar[orient="vertical"] thumb {min-width: 12px !important; max-width: 12px !important; min-height: 14px !important}

div scrollbar[orient="horizontal"] thumb {min-height: 12px !important; max-height: 12px !important}

div scrollbar[orient="vertical"] {min-width: 12px !important; max-width: 12px !important}

div scrollbar[orient="horizontal"] {min-height: 12px !important; max-height: 12px !important}



}





