}

.topboxhead {
background: none repeat scroll 0% 0%  rgb(0, 0, 0) !important;

border-top-left-radius: 10px;
}
.topboxhead .wrap {
background: none !important;
padding: 3px 0px 2px 10px !important;
}

.topboxcontent {
background: none repeat scroll 0% 0%  rgb(52, 52, 52) !important;

border: 2px solid black;
border-bottom: none;
position: relative;
}

.topboxcontent .wrap {
background: none !important;
border: 1px solid rgb(96, 96, 96);
border-bottom: none;

}

.topboxbase {
background: none repeat scroll 0% 0%  rgb(52, 52, 52) !important;
border: 2px solid black;
border-top: none;
border-bottom-left-radius: 20px;
border-bottom-right-radius: 20px;
padding-bottom: 1px;
}
.topboxbase .wrap {
background: none !important;

border: 1px solid rgb(96, 96, 96);
border-top: none;
border-bottom-left-radius: 20px;
border-bottom-right-radius: 20px;
}

#topbox1 .topboxbase {
border-bottom-right-radius: 0px;
}
#topbox1 .topboxbase .wrap {
border-bottom-right-radius: 0px;
}

#topbox2 .topboxbase {
border-bottom-left-radius: 0px;
border-bottom-right-radius: 0px;
}

#topbox2 .topboxbase .wrap {
