css:
@-moz-document domain("www.maitre-eolas.fr") {
@media print {
    #prelude,
    #content .main-content .post-info a,
    #sidebar2, #footer, #extra,
    #comments-feed, #comment-form
    { display: none !important; }
    
    #main, #carcasse, .main {
        min-width: auto !important;
        max-width: 100% !important;
        width: auto !important;
        margin: 0 !important;
        padding: 0 !important;
        float: none !important;
    }
}
    
@media screen and (max-width: 1024px) {
    #main, #carcasse, .main {
        min-width: auto !important;
        max-width: 100% !important;
        width: auto !important;
        margin: 0 !important;
        padding: 0 !important;
        float: none !important;
    }
    
    #prelude,
    #content .main-content .post-info a,
    #sidebar2, #footer, #extra
    { display: none !important; }
    
    #top, #h1 { height: initial !important;}
    .post-title { line-height: initial !important; }
    #page, #top { background: none !important; }
    body { font-size: x-large !important; }

}
}
