css:
/* somehow derived from "Dark Shiny Blue" / preston.scheuneman@gmail.com */

@-moz-document
	 url-prefix('http://userstyles.org/'),
	 url-prefix('https://userstyles.org/') {

#main-header,
#left-sidebar > * {background-color: rgb(38,85,95) !important} /*++*/

body {
	background: url(http://i.imgur.com/Yq3YGV6.jpg)/*url("http://www.hdwallpapers.in/walls/dark_flame_hd-wide.jpg")*/ #222222 fixed repeat left top !important;
	color: #ffffff !important;
	font-family: arial, verdana !important;
}

p, .pagination, #other-links, #style-list a[class=" "], #style-show, #breadcrumbs, #page-description, #sidebar li, #style-list li, li {
/*--    text-shadow: #000000 1px 1px 3px;--*/
}

button, input, textarea {
	-moz-appearance: none!important;
	color: #99AAFF;
}

.DiscussionTopic a, .CategoryName a {
	color: #99ccff !important;
}

#other-links a {
/*--    text-shadow: #000000 1px -1px 5px, #000000 -1px 1px 5px, #000000 -1px -1px 5px, #000000 1px 1px 5px    !important;--*/
}

.bad-rating a {
	color: #666666 !important;
}

textarea,input, .obsolete, .obsolete a {
	color: #666666 !important;
}

code, #Header li a {
	color: #ffffff!important;
}

* {
	text-decoration: none !important;
	color: #bbbbbb !important;
	border-color: #000000 !important;
}

table {
	background: none !important;
}

tbody, fieldset, .current-page, #description, h2 {
	background: url("http://lh3.google.com/preston.scheuneman/R1m-ja98gjI/AAAAAAAAADI/KGg9nz8KuPI/GlassShiny.png") fixed repeat left center !important;
	text-decoration: none !important;
}

.postmetadata {
	color: #99ffcc !important;
}

td {
	color: #6699FF !important;
	border-color: #000000 !important;
}

a, input {
	background: none !important;
	color: #6699FF !important;
	text-decoration: none !important;
}

button, #search-submit, #category-search-submit, input[value="Save"], input[type="submit"], input[type="button"] {
	background: url("http://lh3.google.com/preston.scheuneman/R1m-ja98glI/AAAAAAAAADY/yDshTcUrO2M/gmail_button_normal.png") no-repeat !important;
	background-color: transparent !important;
	border: none !important;
	color: #AACCFF !important;
	font-weight: normal !important;
	height: 30px !important;
	margin: 0px 0px 10px 0px !important;
	padding: 0px 0px 0px 0px !important;
	width: 100px !important;
	font-size: 12px !important;
}

button:hover, #search-submit:hover, #category-search-submit:hover, input[value="Save"]:hover, input[type="submit"]:hover, input[type="button"]:hover {
	background: url("http://lh3.google.com/preston.scheuneman/R1m-ja98gkI/AAAAAAAAADQ/pjAbo3b1RZs/gmail_button_alt.png") no-repeat !important;
	color: #99ccff !important;
/*--    text-shadow: #ffffff 0px 0px 1px !important;--*/
}

#logo a:hover, #account-link a:hover, #sidebar li a:hover, a:hover, #Header li a:hover, #other-links a:hover {
	color: #99ccff !important;
/*--    text-shadow: #99ccff 0px 0px 1px, #ffffff 0px 0px 2px !important;--*/
	text-decoration: none !important;
}

#category-search-submit {
	font-size: 8px !important;
}

#stylish-update button {
	font-size: 9px !important;
}

h2 a, h1 a, .popular, .good-rating a {
	color: #66bbff !important;
/*--    text-shadow: #ffffff 0px -0px 1px;--*/
}

.good-rating a {
	color: #99ccff !important;
/*--    text-shadow: #ffffff 0px 0px 1px;--*/
}

h1 {
	color: #99ccff !important;
/*--    text-shadow: #99ccff 0px 0px 3px, #000000 0px 0px 5px;--*/
}

h2 {
	color: #ffffff !important;
/*--    text-shadow: #ffffff 0px 0px 3px, #000000 0px 0px 5px;--*/
	padding-left: 5px !important;
}

#header-bar, a[title="Current Projects"]:hover, a[title="Hire Me"]:hover, #pagestripe, #pageblock, #sidebar .cat-item:hover, #sidebar li li:hover, .TabOn, .CommentHeader, #Categories ul:hover, .Discussion ul:hover, h1 {
	background: url("http://lh3.google.com/preston.scheuneman/R1m-ja98gjI/AAAAAAAAADI/KGg9nz8KuPI/GlassShiny.png") fixed repeat left center !important;
}

#logo {
	padding: 5px 25px 5px 25px !important;
	margin: 4px 0px 0px 10px !important;
/*--    text-shadow: #ffffff 0px -0px 1px;--*/
}

#logo a:hover {
/*--    text-shadow: #ffaaaa 0px -0px 1px;--*/
}

#main-text {
	margin: 35px 370px 0px 20px !important;
	padding: 10px 10px 10px 10px !important;
}

#navigation {
	padding: 3px 3px 3px 3px !important;
	margin: -12px 0px 0px 0px !important;
}

blockquote *, #long-description {
	font-size: 12px;
}

#logo a, #account-link a {
	color: #99ccff !important;
}

#sidebar li a {
}

#sidebar {
	margin-right: 5px !important;
}

#navigation li a {
	font-size: 12px;
	border: none !important;
}

#stylish-install button {
	font-size: 9px !important;
}

#userjs-install, #userjs-ie-install {
	font-size: 9px !important;
}

legend {
	color: #99ccff !important;
}

.unranked, .no-rating {
	color: #aaaaaa !important;
}

.Button[value="Add your comments"] {
	font-size: 0.75em !important;
}

#frmPostDiscussion, #frmAccountPersonal, #view-code, #main-text, #logo, #sidebar,  h3, .review-container, #style-meta, #navigation li, #navigation, #style-list, legend, #page, .pagination, #content, #header, pre, .entry, a[title="Current Projects"], a[title="Hire Me"], .alt, .navigation, #miscellaneous, #Header li:not(.TabOn), #AboutVanilla, #Discussions, .Unread, .Unread ul, #ContentBody, .CommentBody, #frmPostComment, .ContentInfo, #AccountProfile, #SearchSimple, .Category, #Categories ul, code, ul, #long-description, blockquote, #other-links a, #discussions li:nth-child(odd), #style-list li:nth-child(odd) {
	background: rgba(20,20,20,.2) !important;
}

#install, #Header {
	background: rgba(20,20,20,.4) !important;
}

#Panel, #other-links {
	background: rgba(20,20,20,.6) !important;
}

fieldset table *, .Read ul, .Read, .CommentHeader ul, #header ul {
	background: transparent !important;
}

#username, #password, input, select, input[name="search_terms"], #search-terms, textarea, input[type="text"], #password {
	background: rgba(0,0,0,.5) !important;
	-moz-appearance: none!important;
	color: #999999 !important;
	border: #000000 !important;
	padding: 2px !important;
}

#search-terms {
	width: /*220px*/93.17% !important;
	font-size: 1.1em !important;
	padding: 5px !important;
	/*--margin-left: 20px !important;--*/
}

#other-links {
	padding: 10px;
	margin-top: 13px;
	margin-right: 30px !important;
}

#other-links + h1 {
	width: 100% !important;
}

.obsolete {
	text-decoration: line-through !important;
}

input[type="hidden"] {
	visibility: hidden !important;
}

#sidebar, h2, #other-links, h1, .CommentHeader, #Panel, #Categories {
	/*-moz-*/box-shadow: 0px 0px 1em #000000;
}

#install, .ContentInfo, #Comments {
	/*-moz-*/box-shadow: 0px 0px 3em #000000;
}

.TabOn {
	/*-moz-*/box-shadow: inset 0px 0px 5px 0px #000000, 0px 0px 5px 0px #000000 !important;
}

.no-rating a {
	color: #aaaaaa !important;
}

.no-rating a:hover {
	color: #99ccff !important;
}

li[total-install-count]:after {
	font-size: .70em !important;
/*--    text-shadow: 1px 1px 1px #000000 !important;--*/
	color: #888888 !important;
	content: " (Installs: " attr(total-install-count) ", " attr(weekly-install-count) " wk)";
}

li:hover, #style-list li:hover {
	background: url("http://lh3.google.com/preston.scheuneman/R1m-ja98gjI/AAAAAAAAADI/KGg9nz8KuPI/GlassShiny.png") fixed repeat left center !important;
	text-decoration: none !important;
}

.ad, #post-install-ad {display: none !important}

}
