css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url-prefix("chrome://fastdial/content/fastdial.html")
{

body {
    background: url("http://zgaming-pro.com/other_resource/userstyles_org/fd-pirates_bay/bg.jpg") center no-repeat !important;
    background-color: black !important;
}

#layout {
	height: 70% !important;
}

div.thumbnail {
    background-color: transparent !important;
    border: 5px solid transparent !important;
    border-radius: 12px !important;
    box-shadow:
        0 0px 2px rgba(255, 255, 255, 0.4) inset,
        0 4px 6px rgba(255, 255, 255, 0.4) inset,
        0 11px 0 0px rgba(255, 255, 255, 0.2) inset,
        0 13px 8px rgba(80, 80, 80, 0.3) inset,
        19px 19px 19px rgba(0, 0, 0, 0.3) !important;
    margin:5px !important;
}

div.thumbnail:hover .title img, .hover .title img {
	display: none !important;
}
div.thumbnail:hover, .hover {
    background: url("http://zgaming-pro.com/other_resource/userstyles_org/fd-pirates_bay/plus.png") center no-repeat !important;
    background-color: rgba(255, 255, 255, 0.4) !important;
    border: 5px solid transparent !important ;
    border-radius: 12px !important;
}

.title {
    background-color: rgba(255, 255, 255, 0.4) !important;
    visibility: visible !important;
    border: 1px solid transparent !important;
}   

.title img {
    display: none !important;
}

.image {
    border-radius: 5px 5px 0px 0px !important;
    opacity:0.7 !important;
}

.image:hover {
    opacity:1 !important;
}
}
