css:
@-moz-document url-prefix("http://www.youtube.com/watch"), url-prefix("https://www.youtube.com/watch")
{

#watch-discussion > *, #watch-related > .related-list-item:nth-child(n+19), #watch-more-related > .related-list-item:nth-child(n+19), #yt-masthead-signin, #masthead-upload-button-group, #yt-uix-clickcard-card1, #guide-container, #footer-container {
  display:none !important;
}
* {
    font-family: sans-serif !important;
}
.watch-title {
    font-size: x-large !important;
    font-family: serif !important;
    font-weight: normal !important;
    text-shadow: 1px 1px 1px #DDD; 
}
#watch-headline-show-title {
    font-size: x-large !important;
    font-family: serif !important;
    font-weight: normal !important;
    text-shadow: 1px 1px 1px #BBB;
    color: darkred !important;
}    
#player {
    margin:0 auto !important;
    padding:0 !important;
    display:table !important;
}
#player-api {
    width: 1280px !important;
    height: 720px !important;
    padding: 0 !important;
    margin:0 !important;
}
#watch7-main-container {
    position:static !important;
    width: 94% !important;
    padding:0 !important;
    margin-left:3% !important;
    margin-right:3% !important;
}
#watch7-content {
    margin: 10px 0 10px 0 !important;
    padding-bottom: 100px !important;
}
#watch7-content, #action-panel-details {
    width:100% !important;
    
}
#watch7-action-panels {
    padding: 20px 20px 0 20px !important;
}
li.related-list-item {
  width: 30%;
  display: inline-block;
  margin: 15px 1%;
  vertical-align: top;
}
#watch-more-related-button {
  width: 100%;
  margin-top: 10px;
}
#watch7-sidebar {
    margin-top: 0 !important;
    margin-left: 0 !important;
    max-width:100% !important;
}
#watch-description-extra-info {
    float:right !important;
}
#watch7-branded-banner, #player-branded-banner {
    display: none !important;
}
#watch7-headline    {
    border-top-width: 1px !important;
}
.watch7-playlist-bar-right {
    position: static !important;
}
.watch7-playlist-bar-left {
    width: 853px !important;
}
.watch7-playlist-bar-right {
    max-width: 427px !important;
    width: 427px !important;
}
#watch7-playlist-tray-container {
    /*position: static !important;*/
    width: 100% !important;
    max-width: 1280px !important;
}
#watch7-playlist-tray-mask, #watch7-playlist-tray {
    height: 380px !important;
    width: 1280px !important;
}
#playlist-tray {
    padding-top: 720px !important;
}
.html5-main-video, .html5-video-content, .html5-video-player .video-click-tracking, .html5-video-player .video-stream {
	width: 1280px !important;
	height: 720px !important;
	left: 0 !important;
	top: 0 !important;
}
}
