css:
@-moz-document domain("web.tweetdeck.com") {

/*********************************************
 *  this style based on following style, written by dalboz and Optimized for tweetdeck Persian/Farsi users
    http://userstyles.org/styles/78667/tweetdeck-metro-light-with-column-colors
 *********************************************/

/* fonts & white bg colors */
body, #container {
    font-family: 'Segoe UI', 'Verdana', 'Arial', 'Helvetica', sans-serif;
    background: #fff !important;
    text-shadow: none;
}
textarea, p{font-family: "Droid Arabic Naskh", "iranian sans", tahoma !important; direction:rtl; text-align:right;}

.app-columns {
    background: #fff
}
/* begone, evil rounded corners*/
*{  border-radius: 0 !important; }

/*********************************************
 *  COLUMN LAYOUT 
 *********************************************/

/* column width tweaks - adjust for your monitor */ 
.column {   
    width: 319px !important;
    box-shadow: none !important;
}
/* settings link tabs */
.column-options{
    background: #fff !important;
}
.column-settings-link,
.is-options-open .column-settings-link  {
    background: #fff;
}
.column-settings-link:hover{
    background: transparent;
}
.column-header:hover .column-settings-link, .column-header-link{
    background:transparent;
}
.is-options-open .column-settings-link .icon{
    background: #fff !important;
    color:#ddd !important;
}

/*********************************************
 *  BUTTONS & INPUTS
 *  Note: styling the buttons proved to be 
 *  too much work so I have left them as is
 *********************************************/

/* button layout tweaks */
.btn-with-icon-small {
    padding: 4px 10px !important;
}
.menu-button{
    padding-top:3px !important;    
}

/* search box */
.app-search-input {
    box-shadow: none !important;
}

.column-title,.column-title .attribution {
    color:#444 !important;
}
.column-header-link i{
    color:#fff;
}
.column-header-link i:hover{
    color:#444;
}
.column-number {
    color: #444 !important;
}

/*********************************************
 *    COLUMN COLOURS - style as you wish
 *********************************************/
 
 .is-selected-tweet 
 {
    background: #fff !important;
 }

/* header, cols 1,2 */
section:nth-child(1) .column-header,
section:nth-child(2) .column-header
{
    background: #20CA86 !important
}
/* sidebar cols 1,2 */
.column-nav-item:nth-child(1) .column-nav-link .icon,
.column-nav-item:nth-child(2) .column-nav-link .icon
{
    color: #20CA86;
}
/* tweets, cols 1,2 */
section:nth-child(1) .column-scroller,
section:nth-child(2) .column-scroller 
{
    background: #efe;
}

/* header, cols 3,4,5 */
section:nth-child(3) .column-header,
section:nth-child(4) .column-header, 
section:nth-child(5) .column-header 
{
    background: #ce46ff !important;
}
/* sidebar cols 3,4,5 */
.column-nav-item:nth-child(3) .column-nav-link .icon,
.column-nav-item:nth-child(4) .column-nav-link .icon,
.column-nav-item:nth-child(5) .column-nav-link .icon 
{
    color: #ce46ff;
}

/* tweets, cols 3,4,5 */
section:nth-child(3) .column-scroller,
section:nth-child(4) .column-scroller, 
section:nth-child(5) .column-scroller 
{
    background: #fef;
}

/* header, cols 6,7 */
section:nth-child(6) .column-header,
section:nth-child(7) .column-header 
{
    background: #d22 !important;
}
/* sidebar cols 6,7 */
.column-nav-item:nth-child(6) .column-nav-link .icon,
.column-nav-item:nth-child(7) .column-nav-link .icon 
{
    color: #d22;
}

/* tweets, cols 6,7 */
section:nth-child(6) .column-scroller,
section:nth-child(7) .column-scroller 
{
    background: #fee;
}

}
