css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("trello.com") {
html, body, input, select, textarea, button{font:12px tahoma!important;}
h1,h2,h3,h4,h5{font-family:'droid arabic naskh','b yekan',arial!important;}
.member .member-gold-badge,.list-cards,.window-main-col-content,.board-canvas{direction:rtl!important;}
#board .list{float:right!important;}
.list:first-child{margin-left:5px!important; margin-right:10px!important;}
.list-card-title{padding:0 0 0 18px!important;}
.badges,.badge .badge-text,.badge .icon-sm{float:right!important;}
.badge {float: right!important; margin: 0 0px 4px 4px!important; padding: 0 0 0 4px!important;}
.badge.image-only .icon-sm{margin-right:auto!important; margin-left:0!important;}
.list-card-members {float: left!important; margin: 2px 0 -6px -13px!important;}
.list-card-members .member {margin: 0 0 2px 2px!important;}
.member {float: right!important; margin: 0 0 1px 1px!important;}
.member .status,.member .member-gold-badge{right:auto!important; left:1px!important;}
.member .member-type{right:1px!important; left:auto!important;}
.list-gradient-top, .list-gradient-bottom,.checklist-progress-percentage,.task-progress-total .progress-current{left:auto!important;right:0!important;}
.cc-controls input[type="submit"]{float:right!important;}
input[type="submit"], input[type="button"], button, .button{margin:8px 0 0 4px!important;}
.cc-controls .icon-lg,.cc-controls .icon-lg{float:right!important;}
.cc-controls .cc-opt{float:left!important;}
.list-title {margin: 2px 7px 0 26px!important; direction:rtl!important;}
.list-title h2{margin: 0 0 0 3px!important;  font-family:'droid arabic naskh','b yekan',arial!important; font-size:13px!important;}
.list-title .num-cards{margin:3px 0 0 3px!important;}
.list-title .icon-menu{left:-2px!important; right:auto!important;}
.list-card .card-operation,.window-module-title .icon-menu{left:0!important; right:auto!important;}
.list-card .card-labels .card-label{float:right!important;}
.card-label{margin-right:auto!important; margin-left:3px!important;}
.list{float:right!important;}
.window-main-col{float: right!important;}
.window-sidebar {float: left!important; padding: 6px 0 12px 12px!important;}
.window-header-utils{right:auto!important; left:4px!important;}
.window-header .window-header-icon{right:-28px!important; left:auto!important;}
.window-header{direction:rtl!important;}
.window-title h2{margin-left:4px!important; margin-right:auto!important;}
.window-header .edit {float: right!important; margin-left: 40px!important; margin-right:auto!important;}
.edit-controls input[type=submit], .add-controls input[type=submit]{float:right!important;}
input[type="submit"], input[type="button"], button, .button {margin: 8px 4px 0 0!important;}
.windows.chrome .icon-lg{float:left!important;}
.window-header .window-header-inline-content {margin: 4px 0 0 6px!important;}
.gutter{margin-right:40px!important; margin-left:auto!important;}
.card-detail-metadata .badge {padding: 5px 5px 5px 8px!important;}
.card-detail-metadata .badge .icon-sm{margin-left:5px!important; margin-right:auto!important;}
.card-detail-metadata .badge.image-only{padding-left:5px!important;}
.card-detail-metadata .badge.image-only .icon-sm{margin-left:0!important; margin-right:auto!important;}
textarea{direction:rtl!important;}
.board-description .helper, .card-detail-edit .helper{right:auto!important; left:-2px!important;}
.window-module-title{margin:0 40px 6px 0!important;}
.window-module-title .window-module-title-icon{left:auto!important; right:-40px!important;}
.window-module-title h3{margin:0 0 0 2px!important;}
.new-comment {margin: 0 40px 20px 0!important;}
.new-comment .member{right:-40px!important; left:auto!important;}
.new-comment-input{margin:4px 0 6px 4px!important;}
.edit-controls .option, .add-controls .option{float:left!important;}
.phenom{padding:8px 40px 8px 0!important;}
.phenom .creator{right:0!important; left:auto!important;}
.action-comment{margin:4px 0 5px 4px!important;}
.task {margin: 0 4px 4px 0!important; padding: 0 36px 4px 0!important;}
.task .checkbox{right:6px!important; left:auto!important;}
.checklist .editable h3{margin-right:0!important; margin-left:40px!important;}
.phenom-desc li{margin-left:0px!important;margin-right:15px!important;}
.calendar-wrapper{direction:ltr!important;}
.calendar-day-list{direction:rtl!important;}
}
