css:
@-moz-document domain("saggiamente.com"){


	/*Header*/


	#header{
		height: 8em;
	}

	#top-header{
		display: none;
	}

	#logo {
		margin-left: auto;
		margin-right: auto;
		width: 25em !important;
	}

	#menu-review{
		border-right: 0;
	}

	#menu-review *{
		text-align: center;
		box-sizing: border-box;
		background-position: 50px 6px;
	}

	#menu-review ul li, #menu-review div{
		width: 210px !important;
		margin: 0 !important;
	}	

	#menu-review li, #menu-review li:hover{
		padding-left: 40px;
	}	

	.post-select h1{
		margin-right: 0;
	}


	/*Articolo*/


	body{
		background: #efefef !important;
	}

	#page{
		width: 65em !important;
		padding-top: 0 !important;
	}

	#related{
		margin-top: 2em;
	}

	#related > *{
		width: 64em;
	}

	#related-bnr{
		display: none;
	}

	.socialbar-desktop *{
		display: none;
	}

	.socialbar-desktop{
		height: 0;
		border-bottom: none;
	}


	/*Sidebar*/


	#sidebar{
		width: auto;
		margin: 20px 0 0 0;
	}
}
