css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url-prefix("http://craftsite.pl/forum") {
/************************\
* Nieruchomy górny pasek *
\************************/
html {
    margin-top: 16px !important;
    }
#header_bar {
    transition: opacity 0.4s;
    position: fixed !important;
    z-index: 100 !important;
    top:0px !important;
    width: 100% !important;
    height: 35px !important;
    opacity: 0.4;
    }
#header_bar:hover {
    opacity: 1;
    }
#header_bar .ipsList_inline {
    position: absolute !important;
    top:-1px !important;
    margin-left:10px !important;
    }
/********************\
* Przełączanie stron *
\********************/
.pagination {
    transition: opacity 0.4s;
    position: fixed !important;
    z-index: 101!important;
    background: url("http://craftsite.pl/forum/public/style_images/master/user_navigation.png");
    padding: 5px !important;
    bottom:0 !important;
    opacity: 0.4;
    border-radius: 5px;
    }
.pagination:hover {
    opacity: 1;
    }
}
