css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("www.riemurasia.net") {

P.voteOverall.plusComment{color: rgb(110, 255, 0) !important;}

#searchresults a span{color: #000000 !important;}

.time{color: #ffffff !important;}

#topComments{border-bottom: 12px dotted rgb(147, 197, 255) !important;}

.oldWrapper{background: transparent !important;}

.userMsgOld{background: transparent !important; }

a{color:  #ffffff !important;}
#shout_all ul li a:link{color: #000000;}
#shout_all ul li a:visited {color: #000000 !important;}
a.nick_m{color: rgb(79, 109, 124) !important;}
a.nick_f{color: rgb(222, 120, 222) !important;}
a.nick_h{color: rgb(15, 15, 15) !important;}

#shout_all ul li a.nick_f:visited {color: rgb(222, 120, 222) !important;}
#shout_all ul li a.nick_m:visited {color: rgb(79, 109, 124) !important;}
#shout_all ul li a.nick_h:visited{color: rgb(15, 15, 15) !important;}

#header .headerContent .menuItem .menuContent{background: #455060 !important;}

.commentBox{background: transparent !important; }

.main-div .users-container .users .usersRight .searchBox {background: transparent !important;}

.main-div .users-container .users .usersLeft .browse .navigation{background: transparent !important;}

#header .headerContent .headerBlock span.active{color: rgb(120,190,225) !important;}

span{color:  #ffffff !important;}

span.message{color: #000000 !important;}

.main-div .upload-container .upload .uploadRight .uploadDesc p{color:  #ffffff !important;}

.upload-container .upload .uploadLeft .chooseSource {background: transparent !important;}

.main-div .container .jylppy .jylppyRight .bestDayComment .bestbox .bestcomment {color:  #ffffff !important;}

.uploadLeft .uploadInstructions p {color:  #ffffff !important;}

.main-div .bannerTop {color:  #ffffff !important;}

.main-div .container .landing .landingLeft .whatisrr span{color:  #ffffff !important;}

.main-div .mediaBrowse .mediaNav .subCategories {background: transparent !important;}

.mediaRow:hover, .main-div .mediaBrowse .mediaNav #newestList .mediaRow:hover {background: rgb(80,80,100) !important;}

.mediaRow{background: rgb(79, 109, 124) !important;}

#header .headerContent {background-color: #455565 !important;}

#header .headerBackground{background-color: #455565 !important;}

.jylppyLeft .browse .navigation {background-color: rgb(79, 109, 124) !important;}

.main-div{background-color: rgb(79, 109, 124) !important;}

html, body {background: rgb(79, 109, 124) !important;}

h1{color: rgb(240,240,240) !important;}

h2{color:  rgb(76, 170, 230) !important;}

h3{color: rgb(240,240,240) !important;}

h4{color: rgb(76, 170, 230) !important;}

h5{color:  #ffffff !important;}

.main-div .jylppy-container .jylppy .jylppyLeft .videoBox{background-color: rgb(79, 109, 124) !important;}

.genderBlue{
background: transparent !important;
color: rgb(222,222,222) !important;
border-bottom: 0px rgb(50, 175, 221) solid !important;
border-left: 0px rgb(50, 175, 221) solid !important;
}

.genderRed{
background: transparent !important;
color: rgb(222,222,222) !important;
border-bottom: 0px rgb(222, 120, 222) solid !important;
border-left: 0px rgb(222, 120, 222) solid !important;
}

.genderGrey{
background: transparent !important;
color: rgb(210,210,235) !important;
border-bottom: 0px rgb(133,133,133) solid !important;
border-left: 0px rgb(133,133,133) solid !important;
}

p{  color:  #ffffff !important;}

a.red {color: #FA64FF !important; font-weight:bold !important;}

a.blue {color: #64ADFF !important; font-weight:bold !important;}

.jylppyLeft .comments .userMsg p.time {color: #ffffff !important;}

.jylppyLeft .videoBox .videoInfo{color: #ffffff !important;}

.jylppyLeft .videoBox .videoInfo .videoStatistics span {color: #ffffff !important;}

.jylppyLeft .videoBox .videoInfo .videoStatistics p.keywords {color: #ffffff !important;}

.mediaItem .mediaDetails p{color:  #ffffff !important;}

}
