/*  edited by BC__ on september 18th 2013  */
@-moz-document domain('wikipedia.org'){

  @font-face {
    font-family: 'Cinzel Decorative';
    font-style: normal;
    font-weight: 400;
    src: local('CinzelDecorative-Regular'), url(http://themes.googleusercontent.com/static/fonts/cinzeldecorative/v1/fmgK7oaJJIXAkhd9798yQt0Zu-Kfjb9GTaHd117aS3o.woff) format('woff');
  }
  @font-face {
    font-family: 'Droid Sans';
    font-style: normal;
    font-weight: 400;
    src: local('Droid Sans'), local('DroidSans'), url(http://themes.googleusercontent.com/static/fonts/droidsans/v3/s-BiyweUPV0v-yRb-cjciBsxEYwM7FgeyaSgU71cLG0.woff) format('woff');
  }

  #footer{  
    position: relative;
    top: 10px;
    left: 250px !important;
    width: 1000px;
    background-color:#F5F5F5;
    border:2px;
	border-radius:10px;
    box-shadow:0px 0px 0px 0px #454545;
    }

  body{
    background: #525252 !important;
    font-family: 'Droid Sans', sans-serif !important;;
  }
  
  #content{
    width: auto !important;
    max-width:1200px;
    padding: 22px 30px !important;
    background: #F5F5F5 !important;
    position: relative;
    left: 160px;
    border:2px;
    border-color: black;
	border-radius:10px;
    box-shadow:0px 20px 100px 20px #000000;
  }
 
 #mw-panel{
    position: fixed;
    top:-20px !important;
    left:70px !important;
    overflow: hidden;
    background-color:#F5F5F5;
    border:2px;
    border-color: black;
	border-radius:10px;
    box-shadow:0px 20px 50px 20px #454545;
  }  

  
  h1,h2,h3,h4,h5,h6,h1>span{
    font-family: 'Cinzel Decorative', cursive !important;
      color: black !important;
      background-color:transparent !important;
      border:none !important;
      border-bottom:1px solid #ccc !important;
  }
  a{
    color: #1C519C !important;
    background-color:transparent !important;
    padding-left:2px;
    padding-right:2px;
    -moz-transition:all 0.3s !important;
    -webkit-transition:all 0.3s !important;
    -o-transition:all 0.3s !important;
    transition:all 0.3s !important;
    text-decoration:none !important;
  }
  a:hover{
    color:white !important;
      background-color:#999 !important; 
  }
  .firstHeading{
    font-size:52px !important;
  }
   
 #p-search form{
    position: relative;
    min-width: 500px;
    right:972px;
    bottom:40px;

 }
 

#right-navigation{
  position: absolute;
    top: -12px;
    left: 1300px !important;
    height: 61px !important;
    padding-top: 10px !important;
  }
    
#left-navigation{
  position: relative;
    top: -12px;
    left: 1030px !important;
    height: 61px !important;
    padding-top: 10px !important;
  }

  .ambox{
    display:none !important;
  }

  #head {
    left: 50px !important;
    right: 50px !important;
    width: auto !important;
    height: 61px !important;
    padding-top: 10px !important;
  }

  #head-base,
  #page-base,
  
  #mw-head-base{
  height:100px;
  }
  #mw-page-base {
    height: 80px !important;
    background: transparent !important;
  }
div#p-personal ul { 
    position:relative;
    background-color:#F5F5F5;
    height: 25px;
    width:550px;
    border:2px;
	border-radius:10px;
    box-shadow:0px 0px 0px 0px #454545;
    }

#pt-login{
    display:inline;
    position: absolute;
    right: 620px;
    }
#pt-createaccount{
    display:inline;
    position: absolute;
    right: 520px;
    }

#pt-notifications{
    position: relative;
    right: 250px;
    display:block;
    } 
#pt-userpage{
    position: relative;
    right: 100px;
    display:block;
    }
    
#pt-mytalk{
    position: relative;

    right: 120px;
    display:block;
    } 
#pt-mycontris{
    position: relative;

    right: 120px;
    display:block;
    }    


#pt-mysandbox{
    position: relative;

    right: 120px;
    display:block;
    } 
#pt-preferences{
    position: relative;

    right: 120px;
    display:block;
    } 
#pt-watchlist{
    position: relative;

    right: 120px;
    display:block;
    } 
#pt-logout{
    position: relative;

    right: 120px;
    display:block;
    } 

  #p-logo,
  #p-logo a {
    display: block;
    position: relative !important;
    top: 0 !important;
    width: 200px !important;
    height: 150px !important;
    left:-35px;
    color: #333 !important;
    text-shadow: 0 3px 2px #fff;
  }
  
  #firstHeading {
    font-family: 'Cinzel Decorative' !important;
  }
  #siteSub {
    color: #888 !important;
    font-size: 11px !important;
    line-height: 1.5 !important;
  }
  #bodyContent p {
    margin: 19px 0 !important;
    line-height: 1.5 !important;
  }

  .plainlinks>tbody>tr>td>a>img{
  display:none !important;
  }
  .infobox>tbody>tr>th,.infobox>tbody>tr{
    background-color: transparent !important;
      border:none !important;
      border-bottom:1px solid #ccc !important;
  }

}
