css:
/* AGENT_SHEET */
@namespace url(http://www.w3.org/1999/xhtml);

/*==== Global ====*/
@-moz-document regexp("https?://(?!(.*\.t[xu]mblr\.com)).*")
,regexp("https?://www\.tumblr\.com/(?!customize/).*")
,url-prefix(file://),url-prefix(ftp://),url-prefix(javascript) {

  html,body{
    color:#aaaaad !important; background:none #181819 !important;}
  body *{
    border-color:transparent !important; outline-color:transparent !important;
    text-shadow:none !important; -webkit-box-shadow:none !important; box-shadow:none !important;}
  a,input,textarea,select,button,pre,p,span,td,th,blockquote
  ,img,h1,h2,h3,h4,h5,h6,h7,h8,h9{border:none 0 !important;}

  font[color="black"],font[color="#000000"],font[style*="color:#000000"],font[style*="color:black"]
  ,font[style*="color:Black"],font[style*="color:BLACK"],span[color="black"],span[color="#000000"]
  ,span[style*="color:#000000"],span[style*="color:black"],span[style*="color:Black"],span[style*="color:BLACK"]
  ,font:not([color]):not([bgcolor]):not([style*="color"]):not([style*="background"])
  ,a:not([href]):not([onclick]),table,article,tr,form
  ,span:not([class]):not([color]):not([bgcolor]):not([style*="color"]):not([style*="background"]):not([id^="rgn_button"])
  ,p,b,i,u,big,small,s,strike,tt,sub,sup,ruby,em,strong,dfn,ins,address,blockquote,q,cite,code,var
  ,samp,kbd,abbr,acroym,caption,td,th,ol,ul,dl,li,dt,dd,dir,menu,div,plaintext,pre,xmp,listing{
    color:inherit !important;}
  font,a:not(href):not([onclick]),p,b,i,u,big,small,s,strike,tt,sub,sup,ruby,em,strong,dfn,ins,address,blockquote,q,cite,code,var
  ,samp,kbd,abbr,acroym,caption,ol,ul,dl,li,dt,dd,dir,menu,div,plaintext,pre,xmp,listing,tr,form{
    background-color:transparent !important;}
  body *[bgcolor],body *:not(a):not(td):not(tr):not(span):not(div):not([class*="_avatar"])[style*="background"]:not([style*="position"]){
    background-image:-webkit-linear-gradient(top,rgba(36,36,37,.7),rgba(36,36,37,.7)) !important;
    background-image:-moz-linear-gradient(top,rgba(36,36,37,.7),rgba(36,36,37,.7)) !important;
    background-image:linear-gradient(to bottom,rgba(36,36,37,.7),rgba(36,36,37,.7)) !important;
  }
  pre,time,code,var,kbd,textarea,tt,plaintext,xmp,listing{font-family:monospace !important;}
  a[href],a[href] *,[onclick*="window.open"],[onclick*="window.open"] *,input[type="file"]
  ,input[type="range"],input[type="button"],input[type="submit"],input[type="reset"]
  ,input[type="radio"],input[type="checkbox"],label[for],label:not(:empty){cursor:pointer !important}

/*---- block ----*/
  :not(input):not(textarea):not(select)>div,ul,ol,header,footer,table,article,section{
    -webkit-box-shadow:0 0 1px rgba(255,255,255,.05)inset,0 1px 2px rgba(0,0,0,.3) !important;
    box-shadow:0 0 1px rgba(255,255,255,.05)inset,0 1px 2px rgba(0,0,0,.3) !important;}
  nav,aside,dl,figure,blockquote{
    -webkit-box-shadow:0 1px 2px rgba(0,0,0,.3)inset,0 0 1px rgba(255,255,255,.05) !important;
    box-shadow:0 1px 2px rgba(0,0,0,.3)inset,0 0 1px rgba(255,255,255,.05) !important;}
  article,aside,dl:nth-of-type(4n+0):not(:empty),table
  ,section:nth-of-type(4n+1):not(:empty),ol:nth-of-type(4n+2):not(:empty)
  ,div:nth-of-type(4n+3):not(:empty),ul:nth-of-type(4n+0):not(:empty){
    background-color:#181819 !important;}
  nav,dl:nth-of-type(4n+1):not(:empty)
  ,section:nth-of-type(4n+3):not(:empty),ol:nth-of-type(4n+0):not(:empty)
  ,div:nth-of-type(4n+2):not(:empty),ul:nth-of-type(4n+3):not(:empty){
    background-color:#1c1c1d !important;}
  header,footer,blockquote,dl:nth-of-type(4n+2):not(:empty)
  ,section:nth-of-type(4n+2):not(:empty),ol:nth-of-type(4n+3):not(:empty)
  ,tr:nth-of-type(2n+0)>td:not([bgcolor]):not([style*="background"])/*
  ,tr:nth-of-type(2n+0)>td:nth-of-type(2n+0):not([bgcolor]):not([style*="background"])
  ,tr:nth-of-type(2n+1)>td:nth-of-type(2n+1):not([bgcolor]):not([style*="background"])*/
  ,div:nth-of-type(4n+1):not(:empty),ul:nth-of-type(4n+2):not(:empty){
    background-color:#202021 !important;}
  figure,dl:nth-of-type(4n+3):not(:empty)
  ,tr:nth-of-type(2n+1)>td:not([bgcolor]):not([style*="background"])/*
  ,tr:nth-of-type(2n+0)>td:nth-of-type(2n+1):not([bgcolor]):not([style*="background"])
  ,tr:nth-of-type(2n+1)>td:nth-of-type(2n+0):not([bgcolor]):not([style*="background"])*/
  ,section:nth-of-type(4n+0):not(:empty),ol:nth-of-type(4n+1):not(:empty)
  ,div:nth-of-type(4n+0):not(:empty),ul:nth-of-type(4n+1):not(:empty){
    background-color:#242425 !important;}

  td[width][height]{vertical-align:top !important;}
  td{-webkit-box-shadow:1px 1px 1px rgba(0,0,0,.1) !important; box-shadow:1px 1px 1px rgba(0,0,0,.1) !important;}
  td[bgcolor],td[style*="background"]{
    background-image:-webkit-linear-gradient(left,#242425,rgba(36,36,37,.9),rgba(36,36,37,.9)) !important;
    background-image:-moz-linear-gradient(left,#242425,rgba(36,36,37,.9),rgba(36,36,37,.9)) !important;
    background-image:linear-gradient(to right,#242425,rgba(36,36,37,.9),rgba(36,36,37,.9)) !important;}
  th{background-color:transparent !important;
    background-image:-moz-radial-gradient(rgba(255,255,255,.05)20%,rgba(255,255,255,.1)) !important;
    background-image:radial-gradient(rgba(255,255,255,.05)20%,rgba(255,255,255,.1)) !important;
    -webkit-box-shadow:0 0 2px rgba(255,255,255,.2)inset,0 1px 2px rgba(0,0,0,.3) !important;
    box-shadow:0 0 2px rgba(255,255,255,.2)inset,0 1px 2px rgba(0,0,0,.3) !important;}

/******/
  button[class]>div:not([xyz]),div[id*=lightbox][style],div#lightbox div
  ,tr[style*="url"]>td[align="center"]:nth-of-type(n),div.gallery-nav:not(:empty)
  ,div>div.gallery-close-target:empty,div[id*="PrevNext"]:not([xyz])
  ,li:not([style*="url"]),form
  ,div[id*="hover"],div[class*="hover"],#category p{
    background:none transparent !important; -webkit-box-shadow:none !important; box-shadow:none !important;}
  div:not([style*="url("]):not([class*="post_"]):not([class*="promo"]):not([class*="webstore"]):not([title]):not([class*="icon"])
  ,figure,dl,section,ol,ul,header,footer,blockquote,table,nav,article,aside{
    background-image:none !important;}
  div#lightbox>section,div#jquery-overlay
  ,li[data-ctx="home"]>a,nav>ul>li[id]>a+ul:nth-of-type(n){
    background-color:transparent !important;
    background-image:-webkit-linear-gradient(top,rgba(24,24,25,.95),rgba(0,0,0,.75)) !important;
    background-image:-moz-linear-gradient(top,rgba(24,24,25,.95),rgba(0,0,0,.75)) !important;
    background-image:linear-gradient(to bottom,rgba(24,24,25,.95),rgba(0,0,0,.75)) !important;
    -webkit-box-shadow:0 0 1px rgba(255,255,255,.1)inset,0 2px 3px rgba(0,0,0,.3) !important;
    box-shadow:0 0 1px rgba(255,255,255,.1)inset,0 2px 3px rgba(0,0,0,.3) !important;}
  p,span{background-color:transparent !important;}
  label.checked>div[class*=checkbox]{background-color:#24c !important;}
/*******/
  h1,h2,h3,h4,h5,h6,h7,h8,h9,figcaption,caption{
    color:rgba(192,192,197,.8) !important; padding-left:8px !important;
    text-shadow:1px 2px 3px rgba(0,0,0,.4) !important;
    background-color:#181819 !important;
    background-image:-webkit-linear-gradient(top,rgba(255,255,255,.25)10%,rgba(255,255,255,.2)45%,rgba(255,255,255,0)50%,rgba(255,255,255,.1)90%) !important;
    background-image:-moz-linear-gradient(top,rgba(255,255,255,.25)10%,rgba(255,255,255,.2)45%,rgba(255,255,255,0)50%,rgba(255,255,255,.1)90%) !important;
    background-image:linear-gradient(to bottom,rgba(255,255,255,.25)10%,rgba(255,255,255,.2)45%,rgba(255,255,255,0)50%,rgba(255,255,255,.1)90%) !important;
    -webkit-box-shadow:0 1px 2px rgba(255,255,255,.3)inset,0 2px 3px rgba(0,0,0,.3) !important;
    box-shadow:0 1px 2px rgba(255,255,255,.3)inset,0 2px 3px rgba(0,0,0,.3) !important;}
  hr{width:99% !important; height:1px !important;
    border:none 0 !important; background-color:transparent !important;
    background-image:-webkit-linear-gradient(left,#88ffcc,#ffffcc,#88ffcc) !important;
    background-image:-moz-linear-gradient(left,#88ffcc,#ffffcc,#88ffcc) !important;
    background-image:linear-gradient(to right,#88ffcc,#ffffcc,#88ffcc) !important;
    -webkit-box-shadow:0 0 3px 1px rgba(136,255,204,.4) !important; box-shadow:0 0 3px 1px rgba(136,255,204,.4) !important;}
  [role*="title"],[itemprop*="title"],h1,h2,h3,h4,h5,h6,h7,h8,h9{text-indent:0 !important;}

/*---- link ----*/
  h1 a,h2 a,h3 a,h4 a,h5 a,h6 a,h7 a,h8 a,h9 a,td>a,th>a
  ,a h1,a h2,a h3,a h4,a h5,a h6,a h7,a h8,a h9{text-decoration:none !important;}
  h3 a,h4 a,h5 a,h6 a,h7 a,h8 a,h9 a,a h3,a h4,a h5,a h6,a h7,a h8,a h9{font-weight:normal !important;}
  [class*="Menu"] a,li:empty{background-image:none !Important;}

  a[href],:link,:link *{color:#4cf !important; background-color:transparent !important;}
  a:visited,a:visited *{color:#6c4 !important; background-color:transparent !important;}
  a[href$=".jpg"] *,a[href$=".jpeg"] *,a[href$=".png"] *,a[href$=".gif"] *,a[href$=".bmp"] *
  ,a[href$=".jpg"],a[href$=".jpeg"],a[href$=".png"],a[href$=".gif"],a[href$=".bmp"],a[href^="#"],a[href^="#"] *{color:#c8f !important;}
  a:not([href]):not([name]),a:not([href]):not([name]) *,span[id^="rgn_button"]
  ,[onclick*="://"],[onclick*="://"] *{color:#ca3 !important; background-color:transparent !important;}

  :link:hover,:link:hover *,:link:focus,:link:focus *
  ,:visited:hover,:visited:hover *,:visited:focus,:visited:focus *{
    color:#d0f2ff !important; background-color:transparent !important;
    text-shadow:0 0 4px rgba(68,204,255,.8) !important;}
  a[target="_blank"]:hover,a[target="_blank"]:hover *,a[target="_blank"]:focus,a[target="_blank"]:focus *
  ,a[rel="external"]:hover,a[rel="external"]:hover *,a[rel="external"]:focus,a[rel="external"]:focus *
  ,[onclick*="window.open"]:hover,[onclick*="window.open"]:hover *,[onclick*="window.open"]:focus,[onclick*="window.open"]:focus *{
    color:#d7ffca !important; background-color:transparent !important;
    text-shadow:0 0 4px rgba(102,204,68,.8) !important;}
  span[id^="rgn_button"]:hover,span[id^="rgn_button"]:focus
  ,[onclick*="://"]:hover,[onclick*="://"]:hover *
  ,[onclick*="://"]:focus,[onclick*="://"]:focus *
  ,a:not([href]):not([name]):hover,a:not([href]):not([name]):hover *
  ,a:not([href]):not([name]):focus,a:not([href]):not([name]):focus *{
    color:#ffefbf !important; background-color:transparent !important;
    text-shadow:0 0 4px rgba(255,207,64,.8) !important;}
  a[href^="#"]:hover,a[href^="#"]:hover *,a[href^="#"]:focus,a[href^="#"]:focus *
  ,a[href$=".jpg"]:hover *,a[href$=".jpeg"]:hover *,a[href$=".png"]:hover *,a[href$=".gif"]:hover *,a[href$=".bmp"]:hover *
  ,a[href$=".jpg"]:hover,a[href$=".jpeg"]:hover,a[href$=".png"]:hover,a[href$=".gif"]:hover,a[href$=".bmp"]:hover
  ,a[href$=".jpg"]:focus *,a[href$=".jpeg"]:focus *,a[href$=".png"]:focus *,a[href$=".gif"]:focus *,a[href$=".bmp"]:focus *
  ,a[href$=".jpg"]:focus,a[href$=".jpeg"]:focus,a[href$=".png"]:focus,a[href$=".gif"]:focus,a[href$=".bmp"]:focus{
    color:#ebd6ff !important; background-color:transparent !important;
    text-shadow:0 0 4px rgba(179,103,255,.8) !important;}

  :link:empty,:link img{-webkit-box-shadow:0 0 2px 1px #4cf inset,0 0 2px #4cf !important; box-shadow:0 0 2px 1px #4cf inset,0 0 2px #4cf !important;}
  :visited:empty,:visited img{-webkit-box-shadow:0 0 2px 1px #6c4 inset,0 0 2px #6c4 !important; box-shadow:0 0 2px 1px #6c4 inset,0 0 2px #6c4 !important;}
  [href$=".jpg"]:empty,[href$=".jpeg"]:empty,[href$=".png"]:empty,[href$=".gif"]:empty,[href$=".bmp"]:empty,[href^="#"]:empty
  ,[href$=".jpg"] img,[href$=".jpeg"] img,[href$=".png"] img,[href$=".gif"] img,[href$=".bmp"] img,[href^="#"] img{
    -webkit-box-shadow:0 0 2px 1px #c8f inset,0 0 2px #c8f !important; box-shadow:0 0 2px 1px #c8f inset,0 0 2px #c8f !important;}
  a:not([href]):not([name]):empty,a:not([href]):not([name]) img{
    -webkit-box-shadow:0 0 2px 1px #ca3 inset,0 0 2px #ca3 !important; box-shadow:0 0 2px 1px #ca3 inset,0 0 2px #ca3 !important;}

  a:empty:hover,div.goog-slider-thumb
  ,:link:hover img[src],:link:focus img[src]
  ,:visited:hover img[src],:visited:focus img[src]{background-color:#fff !important;
    -webkit-box-shadow:0 0 1em #d0f2ff inset,0 0 2px 1px #d0f2ff,0 0 8px rgba(68,204,255,.8) !important;
    box-shadow:0 0 1em #d0f2ff inset,0 0 2px 1px #d0f2ff,0 0 8px rgba(68,204,255,.8) !important;}
  a[target="_blank"]:hover img[src],a[target="_blank"]:focus img[src]
  ,a[rel="external"]:hover img[src],a[rel="external"]:focus img[src],[onclick*="window.open"]:hover img[src]
  ,[onclick*="window.open"]:focus img[src]{background-color:#fff !important;
    -webkit-box-shadow:0 0 1em #d7ffca inset,0 0 2px 1px #d7ffca,0 0 8px rgba(102,204,68,.8) !important;
    box-shadow:0 0 1em #d7ffca inset,0 0 2px 1px #d7ffca,0 0 8px rgba(102,204,68,.8) !important;}
  a:not([href]):not([name]):hover img[src],a:not([href]):not([name]):focus img[src]{background-color:#fff !important;
    -webkit-box-shadow:0 0 1em #ffefbf inset,0 0 2px 1px #ffefbf,0 0 8px rgba(255,207,64,.8) !important;
    box-shadow:0 0 1em #ffefbf inset,0 0 2px 1px #ffefbf,0 0 8px rgba(255,207,64,.8) !important;}
  [href^="#"]:hover img[src],[href^="#"]:focus img[src]
  ,[href$=".jpg"]:hover *,[href$=".jpeg"]:hover img[src],[href$=".png"]:hover img[src],[href$=".gif"]:hover img[src],[href$=".bmp"]:hover img[src]
  ,[href$=".jpg"]:focus *,[href$=".jpeg"]:focus img[src],[href$=".png"]:focus img[src],[href$=".gif"]:focus img[src],[href$=".bmp"]:focus img[src]{
    background-color:#fff !important;
    -webkit-box-shadow:0 0 1em #ebd6ff inset,0 0 2px 1px #ebd6ff,0 0 8px rgba(179,103,255,.8) !important;
    box-shadow:0 0 1em #ebd6ff inset,0 0 2px 1px #ebd6ff,0 0 8px rgba(179,103,255,.8) !important;}

  a[class*="button"]:not([class*="PIN"]),a[role*="button"],nav[class^="btn"] a,nav[class^="cmd"] a{
    transition-duration:.01s !important; transition-delay:0s !important;
    border-radius:.8em !important; text-shadow:0 1px 3px rgba(0,0,0,.4) !important;
    background-image:-webkit-linear-gradient(top,rgba(255,255,255,.25)10%,rgba(255,255,255,.2)45%,rgba(255,255,255,0)50%,rgba(255,255,255,.1)90%) !important;
    background-image:-moz-linear-gradient(top,rgba(255,255,255,.25)10%,rgba(255,255,255,.2)45%,rgba(255,255,255,0)50%,rgba(255,255,255,.1)90%) !important;
    background-image:linear-gradient(to bottom,rgba(255,255,255,.25)10%,rgba(255,255,255,.2)45%,rgba(255,255,255,0)50%,rgba(255,255,255,.1)90%) !important;
    -webkit-box-shadow:0 1px 2px rgba(255,255,255,.3)inset,0 2px 3px rgba(0,0,0,.3) !important;
    box-shadow:0 1px 2px rgba(255,255,255,.3)inset,0 2px 3px rgba(0,0,0,.3) !important;}
  a[class*="button"]:not([class*="PIN"]):active,a[rol*="button"]:active,nav[class^="btn"] a:active,nav[class^="cmd"] a:active{
    border-radius:.8em !important;
    background-image:-webkit-linear-gradient(top,rgba(255,255,255,.25)10%,rgba(255,255,255,.2)45%,rgba(255,255,255,0)50%,rgba(255,255,255,.1)90%) !important;
    background-image:-moz-linear-gradient(top,rgba(255,255,255,.25)10%,rgba(255,255,255,.2)45%,rgba(255,255,255,0)50%,rgba(255,255,255,.1)90%) !important;
    background-image:linear-gradient(to bottom,rgba(255,255,255,.25)10%,rgba(255,255,255,.2)45%,rgba(255,255,255,0)50%,rgba(255,255,255,.1)90%) !important;
    -webkit-box-shadow:0 1px 4px rgba(0,0,0,.4)inset,0 0 1px rgba(0,0,0,.2)inset !important;
    box-shadow:0 1px 4px rgba(0,0,0,.4)inset,0 0 1px rgba(0,0,0,.2)inset !important;}

  a[id^="lightbox-nav-btn"][style*="block"],a[id][style*="block"][style*="url"]
  ,a[id$="Link"][href="#"]{-webkit-box-shadow:none !important; box-shadow:none !important; border-radius:2% !important;}
  a#lightbox-nav-btnPrev[style*="block"]:hover,a#prevLink:hover{
    background-image:-webkit-linear-gradient(left,rgba(179,103,255,0),rgba(236,214,255,1)3%,rgba(179,103,255,0)) !important;
    background-image:-moz-linear-gradient(left,rgba(179,103,255,0),rgba(236,214,255,1)3%,rgba(179,103,255,0)) !important;
    background-image:linear-gradient(to right,rgba(179,103,255,0),rgba(236,214,255,1)3%,rgba(179,103,255,0)) !important;
    background-color:transparent !important;}
  a#lightbox-nav-btnNext[style*="block"]:hover,a#nextLink:hover{
    background-image:-webkit-linear-gradient(right,rgba(179,103,255,0),rgba(236,214,255,1)3%,rgba(179,103,255,0)) !important;
    background-image:-moz-linear-gradient(right,rgba(179,103,255,0),rgba(236,214,255,1)3%,rgba(179,103,255,0)) !important;
    background-image:linear-gradient(to left,rgba(179,103,255,0),rgba(236,214,255,1)3%,rgba(179,103,255,0)) !important;
    background-color:transparent !important;}

  a.selectOption.optionSelect{
    background-image:-webkit-linear-gradient(top,#138,#249) !important;
    background-image:-moz-linear-gradient(top,#138,#249) !important;
    background-image:linear-gradient(to bottom,#138,#249) !important;}


/*---- form ----*/
  input,textarea,select,button,progress[value][max],fieldset,label,legend{
    -moz-appearance:none !important;
    appearance:none !important; -webkit-appearance:none !important; -o-appearance:none !important; -ms-appearance:none !important;
    font-size:medium !important; transition-duration:.01s !important; transition-delay:0s !important;
    vertical-align:0 !important; outline:none !important;
    padding:2px 4px !important; color:#8b8b8e !important;}
  fieldset{border:groove 2px rgba(36,36,38,.3) !important; background:none transparent !important;}
  button::-moz-focus-inner,input::-moz-focus-inner{border:none 0 !important;}
  inout>button{font-size:90% !important; padding:2px 0 !important;}

  span.jfk-checkbox,input:not([type="image"]),textarea,select{
    background:none #444446 !important; border-radius:2px !important;
    -webkit-box-shadow:0 0 0 1px rgba(0,0,0,.2),0 1px 1px rgba(0,0,0,.4)inset !important;
    box-shadow:0 0 0 1px rgba(0,0,0,.2),0 1px 1px rgba(0,0,0,.4)inset !important;
    text-shadow:none !important;}
  input:hover,textarea:hover,select:hover,input[type="checkbox"]:focus{background-color:#77777b !important;}
  input:focus,textarea:focus,select:focus{color:#c8c8cc !important;}

  button,input[type="button"],input[type="submit"],input[type="reset"]{
    color:#aaaab0 !important; text-shadow:none !important; border-radius:2px !important;
    background-color:#333 !important;
    background-image:-webkit-linear-gradient(top,#343436,#242426) !important;
    background-image:-moz-linear-gradient(top,#343436,#242426) !important;
    background-image:linear-gradient(to bottom,#343436,#242426) !important;
    -webkit-box-shadow:0 0 1px rgba(255,255,255,.15)inset,0 0 0 1px rgba(0,0,0,.2),0 1px 1px rgba(0,0,0,.4) !important;
    box-shadow:0 0 1px rgba(255,255,255,.15)inset,0 0 0 1px rgba(0,0,0,.2),0 1px 1px rgba(0,0,0,.4) !important;}
  button:hover,input[type="button"]:hover,input[type="submit"]:hover,input[type="reset"]:hover{
    color:#c3ff50 !important; text-shadow:0 0 2px rgba(225,255,168,.4) !important;
    background-image:-webkit-linear-gradient(top,#38383a,#2a2a2c) !important;
    background-image:-moz-linear-gradient(top,#38383a,#2a2a2c) !important;
    background-image:linear-gradient(to bottom,#38383a,#2a2a2c) !important;}
  button:focus,input[type="button"]:focus,input[type="submit"]:focus,input[type="reset"]:focus{
    color:#c3ff50 !important; text-shadow:0 0 1px rgba(225,255,168,.4) !important;}
  button:active,input[type="button"]:active,input[type="submit"]:active,input[type="reset"]:active{
    color:#f0ffd3 !important; text-shadow:0 0 3px rgba(195,255,80,.7) !important;
    padding:3px 4px 1px !important; vertical-align:-1px !important;
    background-image:-webkit-linear-gradient(top,#343436,#242426) !important;
