css:
@-moz-document domain("inoreader.com") {
.article_full_contents{background-color: #FDFDFD;}
#tree_pane{background-color: #FDFDFD;}
.article{background-color: #FDFDFD;}
#wraper{background-color: #FDFDFD;}

/*font*/
body { font-family: acta-display, Georgia, Times, minion-pro, Palatino, tahoma, arial, "Hiragino Sans GB", "Microsoft YaHei", "WenQuanYi Micro Hei", sans-serif; }

.article_current{background-color: #E2E9F5 !important;}
.article{background-color:#FDFDFD;}
.article_footer{border-bottom: 1px solid #E6E6E6;background:transparent;}


.app_logo_div{display:none;}

.feed_favicon{display:none;}
#linkdiv_favourites img{display:none;}
#parent_channel_wraper{display:none;}

.article_title>img{display:none;}
.view_toolbar_inner{display:none;}

/* article */
.article_title_link{color:#333 !important; font-weight:bold;
font-size: 1.6em;
color: #333;
font-weight: normal;
}



/* text */
.article_author, .article_author span{color:#777 !important;
font-style: normal !important;
margin-bottom: 1em;
opacity: 0.30;
}
.article_content {text-align:justify !important;}
.article_content blockquote{
border-left: 2px solid #A5ABAB;
font-size: 1em;
margin: 1.8em .8em;
padding: 0 1em 0 1em;
color: #666;
}
.article_content p{
line-height: 1.635em;
margin: .7em 0 1em 0;
font-size: 1.3em;
color: #333;
-webkit-font-smoothing: antialiased;
}

.article_content h3 {
font-size: 1.8em;
color: #666;
font-weight: normal;
line-height: 1em;
margin-top: 1.5em;
text-align: center;
}
  .article_content ol {margin-left:2em;
list-style-type: decimal;
padding: .1em 0 0 1.2em;
margin-top: .5em;
margin-bottom: 1em;
font-size: 1.3em;
-webkit-font-smoothing: antialiased;
}
    .article_content li {
line-height: 1.5em;
padding: .2em 0 0 0;
color: #333;
}

.article_content ul {
list-style-type: disc;
padding: .1em .0 0 1.1em;
margin-top: .5em;
margin-bottom: 1em;
font-size: 1.3em;
-webkit-font-smoothing: antialiased;
margin-left:2em;
}

pre{
background-color: #F8F8F8;
border: 1px solid #DDD;
font-size: 12px;
line-height: 1;
overflow: auto;
padding: 3px;
border-radius: 3px;}

#reader_pane{overflow-x: hidden;word-break:break-all;}

/* List Mode */
.article_short_contents{font-size:12px;opacity:0.6;}

.article,.article_unreaded{padding: 10px;}
.article_unreaded{border-left:none;position:relative;
  font-weight:normal;
	}

.article_unreaded:before{content:"";display:block;
background:#4285F4;width:6px;height:6px;opacity:0.8;
  position:absolute;left:1px;top:12px; border-radius:3px;}

.article_header_text img{display:none;}
.article_content p img{max-width:100% !important;}
}
