css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("cloud.feedly.com") {

@font-face {
	font-family: 'Source Sans Pro';
	font-style: normal;
	font-weight: 700;
	src: local('Source Sans Pro Bold'), local('SourceSansPro-Bold'), url(http://themes.googleusercontent.com/static/fonts/sourcesanspro/v5/toadOcfmlt9b38dHJxOBGFkQc6VGVFSmCnC_l7QZG60.woff) format('woff');
}
	div#feedlyFrameHeader,
	div#feedlyTabsHolder, /* right side bar */
	div#feedlyProBar,
	div#feedlyMessageBar,
	span#pageActionLayouts,
	input#searchBox,
	div.bottomWikiWidget, /* social widget + share */
	div.topWikiWidget, /* same in Save For Later */
	span.meta, /* engagement */
	span.action, /* keep unread and preview */
	img[src^="http://static.pcinpact.com/"],
	span.performance,
	div#sideArea,
	a.websiteCallForAction
	{display: none !important;}
	div#feedlyFrame,
	div#feedlyPart,
	div#feedlyPage
	{width: 100% !important;}
	div#mainBar,
	div#feedlyPart0,
	div#feedlyPart,
	div.u100Frame
	{margin: 0 !important;
	padding: 0 !important;}
	div#feedlyPart0 {padding: 0 .5% !important;}
	div#feedlyPageHeader {margin: .5% 0 0 !important;}
	div#feedlyPage {margin-top: -4% !important;}
	div#section0_column0 {margin-top: -1.5% !important;}
	div.u100Frame { /*news container */
		padding: .7% !important;
		margin-bottom: .5% !important;
		border-radius: 3px !important;
		border-color: #ccc !important;
	}
	a.title {
		letter-spacing: 0 !important;
		font-family: 'Source Sans Pro', sans-serif !important;
		margin: 0.5ex 0 !important;
		max-width: 100% !important;
	}
	.entryBody {
		color: #333 !important;
		line-height: 1.5 !important;
		margin: 0 !important;
		max-width: 100% !important;
	}
	.read {color: #444 !important;}
	.area,
	.u100Frame,
	.u100Entry,
	.entryHolder,
	body, 
	div#floatingBar,
	.inlineFrame
	{background-color: rgb(248,250,248) !important;}
	span.hhint {display: inline !important;} /* nb unread and perf */
	div.metadata > span:before {content: ' â€• '; }
	.selectedEntry {
		border-width: 2px !important;
		border-color: #000 !important;
	}
	#floatingBar {
		margin: 0 1.2% !important;
		padding: 0 !important;
		/* padding-top: .4% !important; */
		width: 97.6% !important;
	}
	#floatingBar h1 {
		border: 0 !important;
		padding-bottom: 1.3em !important;
	}
	.entryBody a {
		display: inline !important;
		color: CornflowerBlue !important;
	}
	.entryBody img {
		margin: 0 !important;
		float: none !important;
	}
	.entryBody br {
		display: none;
	}
	.inlineFrame {
		background-image: none !important;
		padding: .8% 0 0 !important;
	}
	.condensed .entryholder .u100entry {
		max-width: 98% !important;
	}
	#popupPart span.action {
		display: inline !important;
	}
}
