css:
@-moz-document url-prefix("http://www.riemurasia.net/"), url-prefix("http://riemurasia.net/"), url-prefix("https://www.riemurasia.net/"), url-prefix("https://riemurasia.net/") {
.banners,
.main-div .bannerTop { display: none; }

#footer,
.main-div { width: 1050px !important; }

.headerFixed { display: block !important; }

html, body { background-image: transparent !important; }

html, body,
.main-div .container .landing .landingLeft .whatisrr span,
.main-div .register-container .register .registerRight .form .checkboxs span {
  color: #f4f4f4;
}

html, body {
  background: #698fa2;
  background: -moz-linear-gradient(top,  #698fa2 0%, #3c5b6d 100%);
  background: -webkit-gradient(linear, left top, left bottom, color-stop(0%,#698fa2), color-stop(100%,#3c5b6d));
  background: -webkit-linear-gradient(top,  #698fa2 0%,#3c5b6d 100%);
  background: -o-linear-gradient(top,  #698fa2 0%,#3c5b6d 100%);
  background: -ms-linear-gradient(top,  #698fa2 0%,#3c5b6d 100%);
  background: linear-gradient(to bottom,  #698fa2 0%,#3c5b6d 100%);
}

#headerRiemurasia {
  display: none;
  margin: 30px 25px 0 0  !important;
  float: right;
  z-index: -1 !important;
}

.comment,
#shout_all .message { color: #1e3e4f !important; }

.main-div .mediaBrowse .mediaNav #mediaList .mediaRow, 
.main-div .mediaBrowse .mediaNav #newestList .mediaRow {
  background: #618699;
  background: -moz-linear-gradient(top,  #618699 0%, #446476 100%);
  background: -webkit-gradient(linear, left top, left bottom, color-stop(0%,#618699), color-stop(100%,#446476));
  background: -webkit-linear-gradient(top,  #618699 0%,#446476 100%);
  background: -o-linear-gradient(top,  #618699 0%,#446476 100%);
  background: -ms-linear-gradient(top,  #618699 0%,#446476 100%);
  background: linear-gradient(to bottom,  #618699 0%,#446476 100%);
  
  border-top: 1px solid #779cad;
  border-bottom: 1px solid #254254;
}


.main-div .mediaBrowse .mediaNav #mediaList .mediaRow.current, 
.main-div .mediaBrowse .mediaNav #newestList .mediaRow.current {
  background: #a90329;
  background: -moz-linear-gradient(top,  #a90329 0%, #8f0222 44%, #6d0019 100%);
  background: -webkit-gradient(linear, left top, left bottom, color-stop(0%,#a90329), color-stop(44%,#8f0222), color-stop(100%,#6d0019));
  background: -webkit-linear-gradient(top,  #a90329 0%,#8f0222 44%,#6d0019 100%);
  background: -o-linear-gradient(top,  #a90329 0%,#8f0222 44%,#6d0019 100%);
  background: -ms-linear-gradient(top,  #a90329 0%,#8f0222 44%,#6d0019 100%);
  background: linear-gradient(to bottom,  #a90329 0%,#8f0222 44%,#6d0019 100%);
  
  border-top: 1px solid #c40d35;
  border-bottom: 1px solid #560012;
}

.main-div .mediaBrowse .mediaNav #mediaList .mediaRow:hover, 
.main-div .mediaBrowse .mediaNav #newestList .mediaRow:hover { background: #1e3e4f;  }

.main-div .mediaBrowse .mediaNav #mediaList .mediaRow.current:hover, 
.main-div .mediaBrowse .mediaNav #newestList .mediaRow.current:hover { background: #8f0222;  }

#container .mediaRow a,
#container .mediaRow a span {
  color: #fff !important; 
}

#container .mediaRow p {
  color: #e1e1e1 !important; 
}
.main-div .jylppy-container .jylppy .jylppyLeft .videoBox .navigation {
  padding-bottom: 30px;
}

.main-div .fingerLeft a,
.main-div .fingerRight a,
.main-div .jylppy-container .jylppy .jylppyLeft .videoBox .nextButton a,
.main-div .jylppy-container .jylppy .jylppyLeft .videoBox .backButton a {
  float: left;
  background: #F3F3F3;
  -webkit-border-radius: 5px;
  border-radius: 5px;
  background-clip: padding-box;
  padding: 10px;
}
.main-div .jylppy-container .jylppy .jylppyLeft .videoBox .nextButton a {  padding-left: 17px; }
.main-div .jylppy-container .jylppy .jylppyLeft .videoBox .backButton a { padding-right: 17px; }

.main-div .videoStatistics p { color: #e1e1e1 !important; }


.main-div .fingerLeft a,
.main-div .fingerRight a  { float: none !important; }

.main-div .fingerDate { color: #fff !important; }
.main-div .fingerpori .selectedDay { padding-top: 20px; }

.main-div,
.main-div .videoBox,
#header .headerContent,
#header .headerBackground,
.main-div .register-container .register .registerRight { background: #4E6C7B !important; }

#header .headerContent .headerBlock a { color: #a9c1d6 !important; }
#header .headerContent .headerBlock span.active { color: #fff !important; }

#header .headerContent .searchBlock #searchHeaderButton {
  background: url('http://i41.tinypic.com/nmm1cp.png') no-repeat 12px 0;
}

.main-div {
	-webkit-box-shadow: 0px 1px 3px 0px #304049;
	box-shadow: 0px 1px 3px 0px #304049;
}

#header .headerBackground {
	-webkit-box-shadow: 0px 0px 8px 0px #123244;
	box-shadow: 0px 0px 8px 0px #123244;
}
    
#header .headerContent .loggedBlock .profilepic{
      background-position: center center !important;
      padding: 0px !important;
      width: 59px !important;
      height: 59px !important;
}
    
#loggedUserpic {
	background: transparent !important;    
    width: 100% !important;
    height: 100% !important;
    display: block;
}
   
#header .headerContent .loggedBlock .logOut p.logout,
#header .headerContent .loggedBlock .logOut p.nickname { background: transparent !important; }
    
.main-div .boxContentLeft {
  background: url('http://i44.tinypic.com/iqv636.png') no-repeat;
}
.main-div .boxContentCenter {
  background: url('http://i40.tinypic.com/2cp855g.jpg') no-repeat;
}
.main-div .boxContentRight {
  background: url('http://i42.tinypic.com/2q8aqfp.png') no-repeat;
}
.main-div .sender,
.main-div .statisticTitle { color: #fff !important; }

.main-div .statisticNumber { color: #3c5b6d; }

h1 { color: #e1e1e1 !important; }

h2,
h3 { color: #a9c1d6; }

h4,
h5 { color: #a9c1d6; margin: 10px 0px 20px 0px; }

.main-div .mediaDetails p {
  color: #c3c3c3 !important;
}
.main-div .relatedDetails p { color: #e1e1e1 !important; }

.main-div .jylppy-container .jylppy .jylppyLeft .comments p.nolog {
  color: #fff;
}

#header .headerContent #loginInfo .loginContent,
.main-div .jylppy-container .jylppy .jylppyLeft .videoBox .mediaBox .media,
.main-div .video {
  border: none !important;
  background: #1e3e4f !important;
}

#header .headerContent .headerBlock span.open { background: transparent; }

a { color: #6ba0ce !important; }

#header .headerContent .menuItem .menuContent,
#header .headerContent .loggedBlock #loggedUsermenu .UsermenuContent {
  background: #1e3e4f !important;
}

#header .headerContent .menuItem {
  z-index: 10008;
}

.rounded-img { 
  border: 1px solid #3c5b6d;
  padding: 0px;
  -webkit-border-radius: 2px;
  border-radius: 2px;
  background-clip: padding-box;
  -webkit-box-shadow: 0px 0px 9px 0px #3c5b6d;
  box-shadow: 0px 0px 9px 0px #3c5b6d;
}

.main-div .mediaBrowse .mediaNav .subCategories { 
  background: #3c5b6d !important; 
}

.main-div .mediaBrowse .mediaNav .subCategories a { 
  color: #a9c1d6 !important;
}

.main-div .container .jylppy .jylppyRight .bestDayComment .bestbox .bestcomment,
.main-div .container .jylppy .jylppyRight .bestDayComment .bestbox .bestdetails p.time { color: #adc9d6; }
.main-div .container .jylppy .jylppyRight .bestDayComment .bestbox .bestcomment img { display: none; }

.main-div .container .jylppy .jylppyLeft .newestMedia { border-top: 2px solid #3c5b6d; }
.main-div .container .jylppy .jylppyRight .socialmedia,
.main-div .container .jylppy .jylppyRight .bestDayComment { border-bottom: 2px solid #3c5b6d; }


#joinRiemurasia .logoLeft {
  overflow: hidden;
  width: 430px;
  height: 320px;
}

#joinRiemurasia .loginRegister .joinRegisterButtons .login a,
#joinRiemurasia .loginRegister .joinRegisterButtons .register a {
  display: block;
  width: 263px;
  height: 59px;
  background: url('http://i40.tinypic.com/24fxnxe.png') top left;
}

#joinRiemurasia .loginRegister .joinRegisterButtons .register a { background-position: top right; }

#joinRiemurasia .loginRegister .joinRegisterButtons .login img,
#joinRiemurasia .loginRegister .joinRegisterButtons .register img {
  display: none;
}

#joinRiemurasia .loginRegister h1 .color { 
  color: #fff; 
  font-weight: bold; 
  font-size: 40px; 
}




div.rateit div.rateit-range,
div.rateit div.rateit-hover,
div.rateit div.rateit-selected,
div.rateit div.rateit-preset,
div.rateit div.rateit-preset-rtl {
  background-image: url('http://i40.tinypic.com/209j9s1.png') !important;
}


div.rateit div.rateit-hover {
  background-position: left -32px;
}

div.rateit div.rateit-hover-rtl {
  background-position: right -32px;
}

div.rateit div.rateit-selected {
  background-position: left -16px;
}

div.rateit div.rateit-selected-rtl {
  background-position: right -16px;
}

div.rateit div.rateit-preset {
  background-position: left -48px;
}

div.rateit div.rateit-preset-rtl {
  background-position: left -48px;
}


div.smallstars div.rateit-range,
div.smallstars div.rateit-hover,
div.smallstars div.rateit-selected,
div.smallstars div.rateit-preset,
div.smallstars div.rateit-preset-rtl {
  background-image: url('http://i42.tinypic.com/10gchky.png') !important;
}

div.smallstars div.rateit-hover {
  background-position: left -24px;
}

div.smallstars div.rateit-hover-rtl {
  background-position: right -24px;
}

div.smallstars div.rateit-selected {
  background-position: left -12px;
}

div.smallstars div.rateit-selected-rtl
{
  background-position: right -12px;
}

div.smallstars div.rateit-preset {
  background-position: left -36px;
}

div.smallstars div.rateit-preset-rtl {
  background-position: left -36px;
}
}
