body {
    background: #1b1b1b !important;
    font-family: ProximaNovaAltRegular,Arial,Helvetica,sans-serif !important;
}

#footer-container {
    background: #4d4d4d !important;
    border: none !important;
}


#yt-masthead-container {
    background: rgba(0,0,0,0.3) !important;
    border: none !important;
    position: fixed !important;
    z-index: 999 !important;
    width: 100% !important;
    height: 40px !important;
    padding-top: 10px !important;
}

#page-container {
    padding-top: 96px !important;
}

#masthead-search-terms {
    background: rgba(255,255,255,0.2) !important;
    border: none !important;
    box-shadow: none !important;
    border-top-left-radius: 3px !important;
    border-bottom-left-radius: 3px !important;
    height: 30px !important;
}

.search-term {
    color: #fff !important;
    font-size: 12px !important;
    font-family: ProximaNovaAltRegular !important;
    padding-left: 12px !important;
}

.gssb_m {
    background: transparent !important;
}
.gssb_e {
    border: none !important;
    box-shadow: none !important;
}

.gstl_50.gssb_c {
    position: fixed !important;
    background: rgba(0,0,0,0.2) !important;
    width: 100% !important;
}

/* WATCH PAGE 
 */

#player {
    background: #232323 !important;
    border-bottom: 1px solid #000 !important;
    padding-bottom: 2% !important;
    padding-top: 5% !important;
    margin-top: -100px !important;
}


#watch7-content {
    width: 880px !important;
    background: transparent !important;
}


#watch7-content, #watch-response-content {
    background: #181818 !important;
}

#watch7-headline, #watch7-user-header {
    background: #181818 !important;
    border-left: 1px solid #000 !important;
    border-right: 1px solid #000 !important;
}
#watch-discussion, #watch7-action-panels {
    border-color: #000 !important;
}
.watch-title {
    font-family: ProximaNovaAltBold !important;
    color: #ccc !important;
}
#watch7-action-buttons {
    border-color: #000 !important;
    border-bottom: 1px solid #222 !important;
    box-shadow: inset 0 -1px 0 #000 !important;

}

#watch-description-content,
.comment-text {
    color: #666 !important;
}

#watch-uploader-info {
    color: #ccc !important;
}

#watch-description-content > strong { display: none !important; }

#player-api {
    box-shadow: 3px 3px 10px #000 !important;
}

#watch-description-expand {
    background: transparent !important;
}
.video-extras-sparkbars, .video-extras-sparkbar-likes {
    border-color: #333 !important;
}
#watch7-action-panel-footer {

    background: none !important;
}
#comments-view hr, .yt-horizontal-rule {
    border-color: #222 !important;
    box-shadow: 0 -1px #111 !important;
}
.metadata-inline.yt-uix-button.yt-uix-button-text {
    background: -moz-linear-gradient(#333,#2c2c2c) !important;
    text-shadow: 0 1px #000 !important;
    color: #ccc !important;
}

.yt-uix-button-content {
    color: #ccc !important;
    text-shadow: 0 1px #000 !important;
}

.overlay {
    background: #181818 !important;
}

/* COMMENTS */


.yt-uix-form-input-textarea {
    background: #222 !important;
    border-color: #222!important;
    color: #999 !important;
    border-radius: 10px !important;
}
.comments-textarea-tip {
    border-right-color: #222 !important;
}



}
