css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("jira.mcclatchyinteractive.com") {

.ghx-parent-stub,
.ghx-parent-stub .ghx-group,
.ghx-parent-stub .ghx-group .ghx-key,
.ghx-parent-stub .ghx-group .ghx-summary {
   background-color: #444 !important;
   color: white !important;
}

.ghx-columns .ghx-subtask-group .ghx-subtask-group .ghx-issue,
.js-issue,
.ghx-issue-subtask,
.ghx-not-rankable,
.ghx-columns .ghx-subtask-group .ghx-subtask-group .ui-draggable {
   padding: 2px 10px 2px 34px !important;
   height: 65px !important;
   max-height: 65px !important;
}

.ghx-grabber {
    height: 65px !important;
    max-height: 65px !important;
}

.ghx-issue-fields .ghx-summary .ghx-inner {
    height: auto !important;
    line-height: normal !important;
}
.ghx-move-count {
   display: none !important;
}

}
