css:
@-moz-document domain("strims.pl") {

body {
    font-family: "Open Sans", sans-serif !important;
background:#e9eded;
}
#left-menu-simivar {float:left!important;position:relative;background:#ffffff;max-width:170px!important;padding:5px;margin-right: -182px!important;border:1px solid #dddddd;-webkit-border-radius: 4px;
-moz-border-radius: 4px;
border-radius: 4px;margin-top:100px;}

#left-menu-simivar .top_bar_submenu ul li { 
float: left;
height: 14px;
padding-top: 5px;
width: 167px!important;border:none!important;
}
#left-menu-simivar .top_bar_submenu ul li:hover{background:none!important;color:red;}

#left-menu-simivar .top_bar_submenu ul li a { 
float: left;
margin-left: 8px;
width: 159px!important;
font-size:12px;font-weight:400;color:#999999;
}
#left-menu-simivar .top_bar_submenu ul li a:hover {color:red;text-decoration:none;}

#top_bar { 
background: #2a2d32;}
#top_bar_wrapper > ul > li > a {font-size:11px;color:#989da3;}
#top { 
background: #3d444e;
}

.border_blue{border-bottom-color:#313339;}
#template.fixed #top { 
top: 0px;
}

#top_inner { 
min-height: 32px;
position: relative;
}
#top_bookmarks li a {background:#3e444d;height:31px;font-size:11px;font-weight:400;line-height:31px;color:#9d9e9f;}
#top_bookmarks li.selected a {background:#313339;border:none;color:#fff;}

#top_section a { 
font-size: 13px;color:#fff;line-height:35px;}


}
