css:
@-moz-document domain("weeb.tv") {
#programmeListFrame {
    height: auto !important;
}

.programmeListTextLeftName{
    display: none !important;
}

#programmeList ul li {
    height: 54px !important;
    width:56px !important;
    clear:none !important;
    margin:0px !important;
    overflow: hidden !important;
    border: 1px solid #ccc;
    border-radius:6px;
    background:transparent !important;
	zoom:0.8;
}


#programmeList{background: none !important;height:430px !important;}

#programmeList ul li a {
    margin-left: -1px;
}

#programmeList ul li div span.programmeListTextRight p.programmeListTextRightQuality {
    display: none;
}

#programmeListFrame {
   margin-top:-30px !important;
}
#programmeArrowsFrame{display:none}
#programmeList ul li div.programmeListImage{background-size: 70px 70px !important;height:50px !important;width:50px !important;}


.selected .programmeListImage{
    border: 2px solid red !important;
    border-radius:6px;
  box-shadow: inset 0px 0px 10px #F00;
}

.block{
padding: 20px 20px 20px !important;
width:1250px !important;
}

#container{
width:1250px !important;
}


#programmeList{width:660px !important;}
#programmeList ul li div.programmeListText{display:none !important;}


#programmeList ul li div.programmeListImage{

    border-radius:6px;
}

.block.white{
background:#ccc !important;
}

div#AdTaily1,
div#cpmprofit750top,
.addthis_toolbox,
div#cpmprofit750bottom,
div.block_footer
{display:none !important;}

#programmeList {
width: 660px !important;
position: fixed;
right: -30px;
top: 30px;
}

#programmeList ul li{
  border:0 !important;
    
}

#player_video {

background-color: #000;
width: 550px !important;
height: 390px !important;
border: 0 !important;
width: 100% !important;
height: 100% !important;
position: fixed !important;
top: 0 !important;
left: 0 !important;
}

#player_object,#player_object embed{
  width:100% !important;
  height: 100% !important;
}

html,body{
  height:100% !important;
  
}
}
