css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("akiba-online.com") 
{
html, .above_body, .morbid-box {
background:#000 !important;
}
dd, a, p, h1, h2, h3, h4, h5, .blockrow, .time, .restore,
 .attachments legend {
color:#FFF !important;
}
.body_wrapper, .attachments {
background:#222 !important;
border:1px red solid !important;
}
.floatcontainer {
color:#FFF !important;
background:#000 !important;
}
.navbar, .blockbody, .navlinks, .thread_controls,
  .toolsmenu div {
background:#000 !important;
border:1px red solid !important;
}
 .blockhead, span.forumtitle, span.forumthreadpost,
 span.forumlastpost, .collapse, .blockfoot, 
 .posthead, .forumhead, .searchlisthead, .restore {
color:#FFF !important;
background:red !important;
}
.blockbody tr {
border:1px red solid !important;
}
.blocksubhead, p.subforumdescription {
color:#FFF !important;
background:#222 !important;
border:1px red solid !important;
}
.postbody, .textcontrols, .userinfo, .postdetails  {
color:#FFF !important;
background:#000 !important;
border:1px red solid !important;
}
.forumrow, .table {
color:#FFF !important;
background:#000 !important;
border-left:1px red solid !important;
border-right:1px red solid !important;
border-bottom:1px red solid !important;
}
ul a, .sticky, .threadinfo, .threadstats,
 .threadlastpost  {
color:#FFF !important;
background:#000 !important;
}
.blockrow, .activitybit, .profile_widgets, .threadpostedin  {
color:#FFF !important;
background:#000 !important;
}
.bbcode_code, .quote_container {
color:#FFF !important;
background:#222 !important;
}
.notices li {
border:none !important;
}
}
