css:
@-moz-document domain(s.maxthon.com) {
body,select,a {font-family:Arial, Open Sans,sans-serif !important;font-size:13px !important;}
*:focus {outline:none !important;}
#searchImgTop {
    visibility:hidden;
    opacity:0;
}
#addPreferDom,.wrapper .preferList .delImg, .wrapper .navPanel span.adddPrefered img {
-webkit-filter: grayscale(1) !important;
filter: grayscale(1) !important;
}
.header .innerHeader h3 a:hover {
background-color:transparent !important;
color:#000 !important;
text-decoration:underline !important;
}
.header .innerHeader .logo {
    background-image:url(http://www.google.com/images/icons/product/search-64.png) !important;
    background-position:center !important;
    background-size:48px !important;
}
.wrapper .innerNavPanel a, .header, #headerNav {
background:none !important;
}
.note {
    font-size:13px !important;
    line-height:25px !important;
    position:absolute !important;
    bottom:0 !important;
    margin:0 !important;
    width:100% !important;
    -moz-box-sizing:border-box;
    box-sizing:border-box;
    border:none !important;
    border-top:1px solid #EBEBEB !important;
    background-color:#F1F1F1 !important;
    padding:5px 10px !important;
    
}
#iframeDom {width:100% !important;}

#headerNav {border-color: #EBEBEB !important;}
.wrapper .innerNavPanel a, #searchConfig h6 {
    border-left:none !important;
    border-right:none !important;
    font-size:10pt !important;
    color:#777 !important;
}
.wrapper .innerNavPanel a:hover, .wrapper .navPanel span#searchConfig.adddPrefered:hover h6 {
    color:#000 !important;
}
.wrapper .innerNavPanel a:active, .wrapper .navPanel span#searchConfig.adddPrefered:active h6 {
    color:#DD4B39 !important;
}
.wrapper .innerNavPanel a.currPr, .wrapper .innerNavPanel a.curr {
color:#DD4B39 !important;
font-weight:bold !important;
    border-bottom:3px solid #DD4B39 !important;
}
.header {background-color:#F1F1F1 !important;}
.wrapper .navPanel .innerNavPanel {border:none !important;}
::-moz-focus-inner, ::-moz-focus-outer {padding:0 !important;border:none !important;}
.butt, #custom_def, .spanImg {
    border:1px solid #D9D9D9 !important;
    background-color:#F1F1F1 !important;
    background-image:none !important;
    height:8px !important;
    -moz-appearance:none !important;
    font-family:Arial,sans-serif !important;
	font-size:13px !important;
	display:inline-block !important;
	line-height:8px !important;
	text-align:center !important;
	border-radius:2px !important;
	color:#333 !important;
	padding:10px !important;
	text-decoration:none !important;
	-moz-box-sizing:content-box !important;
    box-sizing:content-box !important;
}
.wrapper .perConfigCon .tableConfig span .spanImg {
    min-width:0 !important;
    height:auto !important;
    padding:3px !important;
    vertical-align:middle !important;
    margin-top:2px !important;
    -moz-box-sizing:content-box !important;
    box-sizing:content-box !important;
    
}
.butt:hover, #custom_def:hover, .spanImg:hover {
background-image:none !important;
background-color:#EAEAEA !important;
box-shadow:0 1px 0 rgba(0,0,0,.15) !important;
}
.butt:active, #custom_def:active, .spanImg:active {
background-image:none !important;
background-color:#E1E1E1 !important;
box-shadow:inset 0 2px 0 rgba(0,0,0,.1) !important;
}
.header .innerHeader .searchCon .searchText .spBut, #custom_butSave, #opt_butSave {
    min-height:30px !important;
	outline:none;
	padding:0 0 !important;
	min-width:60px !important;
	-moz-box-shadow:none;
	box-shadow:none;
	background-color:#4285f4 !important;
	background-image: -webkit-linear-gradient(top,#4387fd,#4683ea) !important;
	background-image: linear-gradient(to bottom,#4387fd,#4683ea) !important;
	margin-left:-1px !important;
	border-top-right-radius:2px !important;
	border-bottom-right-radius:2px !important;
	color:#FFF !important;
	border:none !important;
	-moz-appearance:none !important;
	font-weight:bold;
	text-decoration:none !important;
	font-family:Arial,sans-serif !important;
	font-size:13px !important;

}
#custom_butSave, .butt {border-radius:2px !important;}
.wrapper .hisDom {
top:68px !important;
border-color: #D9D9D9 #CCC #CCC !important;
box-shadow: 0px 2px 4px rgba(0, 0, 0, 0.2);
font-size:10pt !important;
height:auto !important;
width: 395px !important;
}
.wrapper .hisDom a.hisItem, .wrapper .hisDom a.hisItemHover, .wrapper .hisDom a.hisItemDel {
    padding:5px 10px !important;
    text-decoration:none !important;
    -moz-box-sizing:border-box !important;
    box-sizing:border-box !important;
    height:30px !important;
}
.wrapper .hisDom a.hisItemDel {
    border-top-color:#D9D9D9 !important
}
.header .innerHeader .searchCon .searchText .spBut::before {
content:" ";
width:80% !important;
display:block !important;
height:100% !important;
background-image:url("http://ssl.gstatic.com/gb/images/v1_ed0f1f70.png") !important;
background-size: 134px 1148px !important;
background-position: 15px -561px !important;
}
.header .innerHeader .searchCon .searchText .spBut:hover, #custom_butSave:hover, #opt_butSave:hover{
	-moz-box-shadow:0 1px 0 rgba(0,0,0,.15);
	box-shadow:0 1px 0 rgba(0,0,0,.15) !important;
}

.header .innerHeader .searchCon .searchText .spBut:active,#custom_butSave:active, #opt_butSave:active{
	-moz-box-shadow:inset 0 2px 0 rgba(0,0,0,.15);
	box-shadow:inset 0 2px 0 rgba(0,0,0,.15) !important;
	background-image:-moz-linear-gradient(top,#3c7ae4,#3f76d3) !important;
	background-image:linear-gradient(to bottom,#3c7ae4,#3f76d3) !important;
}
.header .innerHeader .searchCon .searchText .searchBox {
background-image:none !important;
background-color:#FFF !important;
font-family:Arial, sans-serif !important;
font-size:13px !important;
padding:5px 10px !important;
border-color: rgba(0, 0, 0, 0.15) !important;
}
.header .innerHeader .searchCon .searchText .searchBox:hover {
    border-color: rgba(0, 0, 0, 0.3) !important;
}
.header .innerHeader .searchCon .searchText .searchBox:focus {
border-color: #4285F4 !important;
}

.innerHeader .searchCon .title-menu a:not(.curType) {
    background-color:transparent !important;
    color:#36C !important;
    text-transform:capitalize !important;
}
.innerHeader .searchCon .title-menu a {
text-transform:capitalize !important;
}
.header .innerHeader .searchCon .title-menu  {
font-size:13px !important;
}
.header .innerHeader .searchCon .historyBut {
background:none !important;
height:0 !important;
width:0 !important;
border:5px solid transparent !important;
border-top-color:#444 !important;
margin-top:13px !important;
}
.wrapper .perConfigCon h3, .wrapper .options h3 {
    color:#DD4B39 !important;
    font-weight:normal !important;
    font-size: 20px !important;
    border-bottom:1px solid #EBEBEB !important;
    margin:0 !important;
    padding:15px 0 !important;
    padding-left:80px !important;
}
.wrapper .perConfigCon, .wrapper .options {
padding:0px 0 !important;
}
.wrapper .options h3 {
margin-bottom:10px !important;
}
.wrapper .options > *:not(#opt_butCancel):not(h3), #custom_butSave {
margin-left:80px !important;
}
.tableConfig {padding:0 73px}
.wrapper .perConfigCon .tableConfig strong, .wrapper .perConfigCon .tableConfig b{
    font: 300 15px/38px Roboto,arial,sans-serif;
}
.wrapper .preferList .preferItems,.ListItems {
    background:none !important;
    border-bottom:1px solid #d9d9d9 !important;
    color:#000 !important;
    font-size:10pt !important;
    color:#333 !important;
    height:33px !important;
    -moz-box-sizing:border-box;
    box-sizing:border-box;
}
.wrapper .preferListC div.searchMenuTit,.wrapper .preferList .act {
    background-color:#F1F1F1 !important;
    background-image:none !important;
    border-bottom:1px solid #D9D9D9 !important;
    color:#000 !important;
    font-size:10pt !important;
    color:#333 !important;
    height:33px !important;
    -moz-box-sizing:border-box;
    box-sizing:border-box;
}
.wrapper .preferList .preferItems:hover,.ListItems:hover {
    background-color:#F7F7F7 !important;
    text-decoration:none !important;
}
.wrapper .preferList, .wrapper .preferListC {
    box-shadow: 0px 1px 4px rgba(0, 0, 0, 0.2);
    border: 1px solid #D5D5D5 !important;
    background-color:#FFF !important;
}
}
