css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("s.maxthon.com") {
#searchImgTop {
    visibility:hidden;
    opacity:0;
}
.header {margin-top:32px !important;}
.header .innerHeader .searchCon .title-menu {
    background-color:#333 !important;
    float:none !important;
    color:#FFF !important;
    display:block !important;
    width:100% !important;
    position:fixed !important;
    left:0 !important;
    top:0 !important;
    height:32px !important;
    line-height:32px !important;
    padding-left:78px !important;
    padding-top:0 !important;
    
}
.header .innerHeader .searchCon .title-menu a[href] {
font-family: Arial, sans-serif !important;
color:#FFF !important;
font-weight:bold !important;
margin:0 !important;
margin-right:10px !important;
margin-top:0px !important;
opacity:0.8;
text-transform:capitalize !important;
}
.header .innerHeader .searchCon .title-menu a[href].curType, .header .innerHeader .searchCon .title-menu a[href]:hover {
    opacity:1 !important;
    text-decoration:none !important;
}
.header .innerHeader .searchText {
margin-top:18px !important;
}
.header .innerHeader .searchCon .historyBut {
    top:23px !important;
}
.histDom {
margin-top: 16px !important;
}
#iframeDom {
    height:calc(100% - 146px) !important;
    bottom:0 !important;
    width:100% !important;
    position:absolute !important;
}
}
