.notification_reply .notification_sentence blockquote {
white-space: pre-line !important;
margin-left: -10px !important;
border-left: 0px !important;
margin-top: -10px !important;
}


.controls_section li .posts, .controls_section li .followers, 
.controls_section li .drafts, .controls_section li .queue, 
.controls_section li .customize, .controls_section li .activity{
height: 13px !important;
margin-top: -3px !Important;
}

 #right_column li .likes, #right_column li .following,
 #right_column li .spotlight, #right_column, .result_link,
 #xkit-outbox-button, #right_column .messages,
 #right_column .delete_all_messages{
height: 13px !important;
}

#right_column .posts:after {background-position:13px -144px !important;}

#right_column .followers:after {background-position: 13px -192px !important;}


#recommended_tumblelogs li{
height: 25px !Important;
padding-bottom: 3px !important;
overflow: hidden !important;
background-size: auto 25px !important;
}

#right_column li .follow {
display: none !important;
}






/*#open_blog_link {
color: #4c4155 !Important;
}*/

#open_blog_link {
color: #bbb !important;
}

/*.currently_selected_blog {
color: #5e6377 !important;
}*/

.control { 
color: #d9d9d9 !important;
}

/*.controls_section li {
	background: rgba(77, 82, 102, 0.51) !important;
	}*/

div#right_column,div#right_column:after{background: none !important;}

.post_avatar {
border: 0px !Important;
background: none !Important;
}


.post_avatar_link {
border-bottom-right-radius: 30px !Important;
border-top-left-radius: 30px !important;
border: 2px solid #2b3044 !important;
background-color: rgba(77, 82, 102, 0.41) !important;
}

.post_avatar_image { display: none !Important; }

#MissingE_tagslist li, #MissingE_marklist li {
background: none !important;
}

#search_query,#search_query:focus{color: #a9a9a9 !important;}

#unread_tag_notice>span{color:rgba(85, 108, 131, 0.89) !important;line-height:6px!important}#unread_tag_notice,.popover_button.search_field_active{background-color:rgba(255, 255, 255, 0.80)!important; color: rgba(255, 255, 255, 0.41) !important;}


body {
background-image:url('http://i1210.photobucket.com/albums/cc412/the-wonderworker-girl/gray_zpsc9c825a8.jpg') !important;
background-repeat: no-repeat !important;
background-attachment: fixed !important;
background-color:grey !important;
background-position:bottom left !important;
/*background-size: cover !important;*/
}


div.l-content, #dashboard_index div.l-content, #dashboard_submissions div.l-content, #dashboard_drafts div.l-content, #dashboard_post_queue div.l-content, #dashboard_members div.l-content, #main_column, #page_nav {
	background: rgba(105, 117, 105, 0.25) !important;
}

#dashboard_useraction_following div.l-content, #post_content, #post_header, #dashboard_editing div.l-content, #dashboard_tumblelog_settings #main_column, #dashboard_tumblelog_settings #page_nav, #dashboard_settings #main_column, #dashboard_settings #page_nav, #dashboard_following #page_nav, #dashboard_following #main_column, #dashboard_following div.l-content, #dashboard_lookup_contacts #main_column, #dashboard_lookup_contacts #page_nav, #dashboard_lookup_contacts div.l-content, #dashboard_directory div.l-content, #dashboard_directory #page_nav, #dashboard_directory #main_column, #dashboard_edit_post #page_nav, #dashboard_edit_post div.l-content, #dashboard_edit_post #main_column {
background: #ffffff !important;
}

#post_content {
border-bottom-right-radius: 5px;
border-bottom-left-radius: 5px;
}

#post_header {
border-top-left-radius: 5px;
border-top-right-radius: 5px;
}

/*#left_column #posts .notification .avatar {
                      border-color:transparent !important;
                        background-color:rgba(77, 82, 102, 0.66) !important;
opacity: 0.6 !important;
                    -webkit-transition: all 0.5s ease-in-out;
   -moz-transition: all 0.5s ease-in-out;
   -o-transition: all 0.5s ease-in-out; 
   }*/


#left_column #posts .notification .avatar:hover {opacity:1 !important;
-webkit-transition: all 0.5s ease-in-out;
   -moz-transition: all 0.5s ease-in-out;
   -o-transition: all 0.5s ease-in-out; }

.post{opacity:1 !important; border-bottom:0px !important; color:#555555 !important;
	/*border-radius: 6px !Important;*/}
.post:hover{opacity:1 !important}

.post_content{
	background: transparent !important;
}

.post_permalink{background:none!important;
width: 16px!important;height:16px!important;
background:#6f7488!important;
border-bottom-left-radius:10px!important;
border-top-right-radius:24px!important;
margin-top:6px!important;margin-right:6px!important;
-moz-box-shadow:-1px 1px 2px rgba(16,16,16,1)!important;
-webkit-box-shadow:-1px 1px 2px rgba(16,16,16,1)!important;
box-shadow:-1px 1px 2px rgba(16,16,16,1)!important;}

.post_permalink:hover{background:rgba(85, 108, 131, 0.54)!important;}


.active{background: transparent !important; color:#afaadc !important;}

#footer{background: transparent !important;
background-image:none !important;}


#footer a, #footer a:link, #footer a:visited {
color: #707b8e !important;
}
#footer a:hover {
color: #99abcc !important;
}
#footer {
color: #707b8e !important;
}

#left_column #posts .notification.alt {
background-color: rgba(33, 35, 65, 0.34) !important;
border-bottom-color: rgba(21, 21, 37, 0.66) !important;}


#left_column #posts .notification.last_notification,

#left_column #posts .notification.single_notification {      
border-bottom-color:rgba(21, 21, 37, 0.66) !important; }

#left_column #posts .notification .avatar {
border-color:transparent !important;
background-color:rgba(21, 21, 37, 0.66) !important;
border-radius: 50px !important;
opacity: 0.6 !important;
-webkit-transition: all 0.5s ease-in-out;
   -moz-transition: all 0.5s ease-in-out;
   -o-transition: all 0.5s ease-in-out; }

.dashboard_options_form {
background-color: rgba(36, 37, 68, 0.54) !important;
border: 1px solid rgba(119, 122, 150, 0.34) !important;
color: #777a96 !important;
}

.publish_on_day {
color: #a2afd4 !important;
}

#next_page_link, #previous_page_link {
background: rgba(11, 13, 34, 0.51)!important;
border: 1px solid rgba(128, 152, 176, 0.21) !important;
color: rgba(128, 152, 176, 0.51) !important;
-webkit-transition: all 0.5s ease-in-out;
   -moz-transition: all 0.5s ease-in-out;
   -o-transition: all 0.5s ease-in-out; 
}

#next_page_link:hover, #previous_page_link:hover {
background: rgba(128, 152, 176, 0.21)!important;
border: 1px solid rgba(11, 13, 34, 0.71) !important;
color: rgba(11, 13, 34, 0.71) !important;
-webkit-transition: all 0.5s ease-in-out;
   -moz-transition: all 0.5s ease-in-out;
   -o-transition: all 0.5s ease-in-out; 
}

.avatar_frame, .preview_frame {
border: rgba(11, 13, 34, 0.71) !important;
}


}

@-moz-document url-prefix('http://www.tumblr.com/dashboard/iframe'), url-prefix('https://www.tumblr.com/dashboard/iframe')  {
body {background: transparent !important;} }
